mapper.select:
SELECT e.insure_reg_code, t.aac001 as psnNo, (select aac003 from insure_individual_basic c where c.id = t.mib_id) as psnName, e.crte_time, e.omsgid, e.oinfno, l.name oinfname FROM insure_individual_settle e, insure_individual_visit t, sys_code_detail l WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND e.hosp_code = l.hosp_code AND e.oinfno = l.value AND l.c_code = 'YBCZLX' AND e.hosp_code = #{hospCode}
AND t.aac001 like concat('%',#{psnNo},'%')
AND e.oinfno = #{oinfno}
and e.crte_time > STR_TO_DATE(#{startDate},'%Y-%m-%d')
and e.crte_time <= DATE_ADD(STR_TO_DATE(#{endDate},'%Y-%m-%d'),INTERVAL 1 day)
union all
select t.insure_reg_code, t.aac001 as psnNo, (select aac003 from insure_individual_basic c where c.id = t.mib_id) as psnName, t.crte_time, t.omsgid, t.oinfno, l.name oinfname from insure_individual_visit t, sys_code_detail l where t.hosp_code = l.hosp_code AND t.oinfno = l.value AND l.c_code = 'YBCZLX' AND t.hosp_code = #{hospCode}
AND t.aac001 like concat('%',#{psnNo},'%')
AND t.oinfno = #{oinfno}
and t.crte_time > STR_TO_DATE(#{startDate},'%Y-%m-%d')
and t.crte_time <= DATE_ADD(STR_TO_DATE(#{endDate},'%Y-%m-%d'),INTERVAL 1 day)

SELECT e.insure_reg_code AS insureRegCode, e.visit_id, t.medical_reg_no AS mdtrtId, e.insureSettleId AS setlId, e.settle_id, t.aka130 as med_type, t.aac001 AS psnNo, c.aac003 AS psnName, c.aac004 AS sex, c.aac002 as idCard, c.aac006 AS birthday, e.crte_time AS crteTime, t.visit_drpt_name, e.total_price FROM insure_individual_settle e, insure_individual_visit t, insure_individual_basic c WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND c.hosp_code = t.hosp_code AND c.id = t.mib_id AND t.hosp_code = #{hospCode}
and ( c.aac003 like concat('%',#{keyword},'%') or t.medical_reg_no like concat('%',#{keyword},'%') or e.insureSettleId like concat('%',#{keyword},'%') )
and t.crte_time > STR_TO_DATE(#{startDate},'%Y-%m-%d')
and t.crte_time <= DATE_ADD(STR_TO_DATE(#{endDate},'%Y-%m-%d'),INTERVAL 1 day)
and t.is_hospital =#{isHospital}

SELECT max(e.insure_reg_code) as insureRegCode, t.medicine_org_code fixmedins_code, e.clr_type, e.clr_way, e.clr_optins, sum(e.person_price) as person_price, sum(e.total_price) as medfee_sumamt, (sum(e.total_price) - sum(e.personal_price) - sum(e.person_price) - sum(e.rests_price)) as fund_pay_sumamt, sum(e.personal_price) as acct_pay, count(1) as fixmedins_setl_cnt FROM insure_individual_settle e, insure_individual_visit t WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND e.settle_state = '1' and e.insureSettleId is not null and date(e.crte_time) >= date(#{startDate}) and date(e.crte_time) <= date(#{endDate}) and e.clr_type = #{clrType} and e.clr_way = #{clrWay} and e.insure_reg_code = #{insureRegCode}
and e.state = '0'
group by e.clr_type,e.clr_way,t.medicine_org_code

SELECT c.aac002 as name, e.insure_reg_code as insureRegCode, t.aac001 as psn_no, e.settle_id, e.visit_id, t.medical_reg_no AS mdtrt_id, e.insureSettleId AS setl_id, e.state as refd_setl_flag, (select aac003 from insure_individual_basic c where c.id = t.mib_id) as psnName, c.aae140 as insutype, t.is_hospital, e.clr_type , if (e.state = '0','0','1') as refd_setl_flag, e.state, e.clr_optins, t.insure_reg_code as setl_optins, e.total_price as medfee_sumamt, (e.total_price - e.personal_price - e.person_price - e.rests_price) as fund_pay_sumamt, e.personal_price as acct_pay FROM insure_individual_settle e, insure_individual_visit t, insure_individual_basic c WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND c.hosp_code = t.hosp_code AND c.id = t.mib_id AND t.hosp_code = #{hospCode} AND c.aae140 IS NOT NULL AND e.settle_state = '1'
AND t.aac001 like concat('%',#{psnNo},'%')
AND e.insure_reg_code = #{insureRegCode}
/*开始日期*/
and date(e.crte_time) >= date(#{startDate})
/*结束日期*/
and date(e.crte_time) <= date(#{endDate})
and e.clr_type = #{clrType}
and e.state = '0'

SELECT max( e.insure_reg_code ) AS insureRegCode, t.medicine_org_code medicineOrgCode, c.aae140, CASE WHEN LEFT ( e.insure_reg_code, 4 ) = LEFT ( t.insuplc_admdvs, 4 ) THEN '0' ELSE '1' END isRemote, t.aka130, t.aka130_name aka130Name, count( 1 ) AS personNum, ROUND( sum( e.total_price ), 2 ) medfeeSumamt, ROUND( sum( e.plan_price ) + sum( e.serious_price ) + sum( e.civil_price ) + sum( e.personal_price ), 2 ) planSumPrice, ROUND( sum( e.person_price ), 2 ) personPrice, ROUND( sum( e.plan_price ), 2 ) planPrice, ROUND( sum( e.serious_price ), 2 ) seriousPrice, ROUND( sum( e.civil_price ), 2 ) civilPrice, ROUND( sum( e.personal_price ), 2 ) personalPrice, ROUND( sum( e.rests_price ), 2 ) restsPrice, ROUND( sum( e.maf_pay ), 2 ) mafPay, ROUND( sum( e.retire_price ), 2 ) retirePrice FROM insure_individual_settle e, insure_individual_visit t, insure_individual_basic c WHERE e.hosp_code = t.hosp_code AND e.visit_id = t.visit_id AND c.id = t.mib_id AND e.settle_state = '1' AND e.state = '0' AND e.insureSettleId IS NOT NULL AND e.insure_reg_code = #{insureRegCode} AND e.hosp_code = #{hospCode}
and c.aae140 = #{aae140}
/*开始日期*/
and date(e.crte_time) >= date(#{startDate})
/*结束日期*/
and date(e.crte_time) <= date(#{endDate})
AND t.aka130 in
#{item}
GROUP BY t.aka130
