select id, hosp_code, insure_reg_code, code, name, value, remark, crte_id, crte_name, crte_time, ext01, ext02, ext03 from insure_dict where hosp_code=#{hospCode}
and ( code like concat('%',#{keyword},'%') or name like concat('%',#{keyword},'%') or insure_reg_code like concat('%',#{keyword},'%') )
order by crte_time desc

