mapper.select:
SELECT  hosp_code,
item_id, item_code,
item_name, code, type_code, spec, unit_code,
price, sum(split_num) as split_num , split_unit_code, biz_id, biz_name,
sum(consum_num) as consum_num, avg_sell_price, sum(sell_price_all) as sell_price_all,
avg_buy_price, sum(buy_price_all) as buy_price_all, sum(profit_price) as profit_price
from extract_consumption_detail
where
hosp_code = #{hospCode}
and summary_type = #{summaryType}

    and extract_time >= #{startTime}


    and extract_time <= #{endTime}


    and item_code = #{itemCode}


    and biz_id in

        #{item}



    and (
    item_name like concat('%',#{itemName},'%') or
    pym like concat('%',#{itemName},'%') or
    wbm like concat('%',#{itemName},'%')
    )

group by item_id,biz_id,unit_code

SELECT  hosp_code,
item_id, item_code,
item_name, code, type_code, spec, unit_code,
price, sum(split_num) as split_num , split_unit_code, biz_id, biz_name,
sum(consum_num) as consum_num, avg_sell_price, sum(sell_price_all) as sell_price_all,
avg_buy_price, sum(buy_price_all) as buy_price_all, sum(profit_price) as profit_price
from extract_consumption_detail
where
hosp_code = #{hospCode}
and summary_type = #{summaryType}

    and extract_time >= #{startTime}


    and extract_time <= #{endTime}


    and item_code = #{itemCode}


    and biz_id in

        #{item}



    and (
    item_name like concat('%',#{itemName},'%') or
    pym like concat('%',#{itemName},'%') or
    wbm like concat('%',#{itemName},'%')
    )

group by item_id

select b.*, (select bd.name from base_dept bd where bd.hosp_code = b.hosp_code and b.biz_id = bd.id) as deptName from ( select t.item_id, t.biz_id, t.curr_unit_code, max(extract_time) extract_time from extract_stro_invoicing_detail t group by t.item_id,t.curr_unit_code,t.biz_id) as c inner join extract_stro_invoicing_detail b on c.item_id = b.item_id and c.biz_id = b.biz_id and c.extract_time = b.extract_time
and b.biz_id = #{bizId}
and b.item_id in
#{id}
