SELECT ywlx, visitNo, name, bedName, patientCode, settleTime, inTime, outTime, inDays, totalPrice, planPrice, civilPrice, seriousPrice, retirePrice, hospPrice, personPrice, apTotalPrice, comPay, /*企业补充医疗*/ ynyh, remark, mafPay, personalPrice, restsPrice FROM ( SELECT 'MZ' ywlx, b.visit_no as visitNo, ( SELECT NAME FROM outpt_visit ov WHERE ov.hosp_code = a.hosp_code AND ov.id = a.visit_id ) AS name , '' AS bedName, a.patient_code AS patientCode, a.settle_time AS settleTime, '' AS inTime, '' AS outTime, '' AS inDays, b.total_price AS totalPrice, b.plan_price AS planPrice, b.civil_price AS civilPrice, b.serious_price AS seriousPrice, b.retire_price AS retirePrice, b.hosp_price AS hospPrice, b.person_price AS personPrice, b.rests_price AS restsPrice, 0.00 AS apTotalPrice, ( a.total_price - a.reality_price ) AS ynyh, b.remark, ifnull(b.maf_pay, 0.00) as mafPay, ifnull(b.com_pay, 0.00) as comPay, /*企业补充医疗*/ ifnull(b.personal_price, 0.00) as personalPrice FROM outpt_settle a, insure_individual_settle b WHERE a.id = b.settle_id and a.is_settle = '1' AND a.hosp_code = #{hospCode}
AND b.aka130 = #{medType}
UNION ALL SELECT 'ZY' ywlx, c.in_no AS visitNo, c.NAME, c.bed_name AS bedName, a.patient_code AS patientCode, a.settle_time AS settleTime, c.in_time AS inTime, c.out_time AS outTime, DATEDIFF( c.out_time, c.in_time ) AS inDays, b.total_price AS totalPrice, b.plan_price AS planPrice, b.civil_price AS civilPrice, b.serious_price AS seriousPrice, b.retire_price AS retirePrice, b.rests_price AS restsPrice, b.hosp_price AS hospPrice, b.person_price AS personPrice, a.ap_total_price AS apTotalPrice, a.hosp_jm_price AS ynyh, b.remark, ifnull(b.maf_pay, 0.00) as mafPay, ifnull(b.com_pay, 0.00) as comPay, /*企业补充医疗*/ ifnull(b.personal_price, 0.00) as personalPrice FROM insure_individual_settle b LEFT JOIN inpt_settle a ON a.id = b.settle_id AND a.hosp_code = b.hosp_code JOIN inpt_visit c ON b.visit_id = c.id AND b.hosp_code = c.hosp_code WHERE b.hosp_code = #{hospCode} AND a.is_settle = '1' AND a.status_code = '0'
AND b.aka130 = #{medType}
) ss WHERE 1=1
and ywlx IN
#{item}
and DATE_FORMAT(settleTime,'%Y-%m-%d') = #{searchDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d %H:%i:%s') >= #{startDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d %H:%i:%s') <= #{endDate}
and patientCode =#{patientCode}
ORDER BY settleTime DESC

select SUM(totalPrice) as totalPriceSum, SUM(planPrice)as planPriceSum, SUM(civilPrice)as civilPrice, SUM(seriousPrice)as seriousPriceSum, SUM(retirePrice)as retirePriceSum, SUM(hospPrice)as hospPriceSum, SUM(personPrice)as personPriceSum, SUM(apTotalPrice)as apTotalPriceSum, SUM(comPay)as comPaySum, /*企业补充医疗汇总*/ SUM(ynyh)as ynyhSum from( SELECT ywlx, visitNo, name, bedName, patientCode, settleTime, inTime, outTime, inDays, totalPrice, planPrice, civilPrice, seriousPrice, retirePrice, hospPrice, personPrice, apTotalPrice, comPay, /*企业补充医疗*/ ynyh, remark FROM ( SELECT 'MZ' ywlx, b.visit_no as visitNo, ( SELECT NAME FROM outpt_visit ov WHERE ov.hosp_code = a.hosp_code AND ov.id = a.visit_id ) AS name , '' AS bedName, a.patient_code AS patientCode, a.settle_time AS settleTime, '' AS inTime, '' AS outTime, '' AS inDays, b.total_price AS totalPrice, b.plan_price AS planPrice, b.civil_price AS civilPrice, b.serious_price AS seriousPrice, b.retire_price AS retirePrice, b.hosp_price AS hospPrice, b.person_price AS personPrice, b.com_pay AS comPay, /*企业补充医疗*/ 0.00 AS apTotalPrice, ( a.total_price - a.reality_price ) AS ynyh, b.remark FROM outpt_settle a, insure_individual_settle b WHERE a.id = b.settle_id and a.is_settle = '1' AND a.hosp_code = #{hospCode} UNION ALL SELECT 'ZY' ywlx, c.in_no AS visitNo, c.NAME, c.bed_name AS bedName, a.patient_code AS patientCode, a.settle_time AS settleTime, c.in_time AS inTime, c.out_time AS outTime, DATEDIFF( c.out_time, c.in_time ) AS inDays, b.total_price AS totalPrice, b.plan_price AS planPrice, b.civil_price AS civilPrice, b.serious_price AS seriousPrice, b.retire_price AS retirePrice, b.hosp_price AS hospPrice, b.person_price AS personPrice, b.com_pay AS comPay, /*企业补充医疗*/ a.ap_total_price AS apTotalPrice, a.hosp_jm_price AS ynyh, b.remark FROM insure_individual_settle b LEFT JOIN inpt_settle a ON a.id = b.settle_id AND a.hosp_code = b.hosp_code JOIN inpt_visit c ON b.visit_id = c.id AND b.hosp_code = c.hosp_code WHERE b.hosp_code = #{hospCode} AND a.is_settle = '1' AND a.status_code = '0' ) ss WHERE 1=1
and ywlx IN
#{item}
and DATE_FORMAT(settleTime,'%Y-%m-%d') = #{searchDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d') >= #{startDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d') <= #{endDate}
ORDER BY settleTime DESC ) tableSum

select SUM(totalPrice) as totalPriceSum, SUM(planPrice)as planPriceSum, SUM(civilPrice)as civilPrice, SUM(seriousPrice)as seriousPriceSum, SUM(retirePrice)as retirePriceSum, SUM(hospPrice)as hospPriceSum, SUM(personPrice)as personPriceSum, SUM(apTotalPrice)as apTotalPriceSum, SUM(comPay) AS comPaySUM, SUM(ynyh)as ynyhSum from( SELECT ywlx, patientCode, SUM( ss.totalPrice ) AS totalPrice, SUM( ss.planPrice ) AS planPrice, SUM( ss.civilPrice ) AS civilPrice, SUM( ss.seriousPrice ) AS seriousPrice, SUM( ss.retirePrice ) AS retirePrice, SUM( ss.hospPrice ) AS hospPrice, SUM( ss.personPrice ) AS personPrice, SUM( ss.apTotalPrice ) AS apTotalPrice, SUM( ss.comPay) AS comPay, /*企业补充医疗*/ SUM( ss.ynyh ) AS ynyh FROM ( SELECT 'MZ' AS ywlx, DATE_FORMAT( a.settle_time, '%Y-%m-%d' ) AS settleTime, a.patient_code AS patientCode, IFNULL( b.total_price, 0 ) AS totalPrice, IFNULL( b.plan_price, 0 ) AS planPrice, IFNULL( b.civil_price, 0 ) AS civilPrice, IFNULL( b.serious_price, 0 ) AS seriousPrice, IFNULL( b.retire_price, 0 ) AS retirePrice, IFNULL( b.hosp_price, 0 ) AS hospPrice, IFNULL( b.person_price, 0 ) AS personPrice, ifnull(b.com_pay, 0) as comPay, /*企业补充医疗*/ 0 AS apTotalPrice, IFNULL(( a.total_price - a.reality_price ), 0 ) AS ynyh FROM outpt_settle a, insure_individual_settle b WHERE a.hosp_code = b.hosp_code AND a.id = b.settle_id AND a.is_settle = '1' AND a.hosp_code = #{hospCode} UNION ALL SELECT 'ZY' AS ywlx, DATE_FORMAT( a.settle_time, '%Y-%m-%d' ) AS settleTime, a.patient_code AS patientCode, IFNULL( b.total_price, 0 ) AS totalPrice, IFNULL( b.plan_price, 0 ) AS planPrice, IFNULL( b.civil_price, 0 ) AS civilPrice, IFNULL( b.serious_price, 0 ) AS seriousPrice, IFNULL( b.retire_price, 0 ) AS retirePrice, IFNULL( b.hosp_price, 0 ) AS hospPrice, IFNULL( b.person_price, 0 ) AS personPrice, ifnull( b.com_pay, 0) as comPay, /*企业补充医疗*/ IFNULL( a.ap_total_price, 0 ) AS apTotalPrice, IFNULL(( a.total_price - a.reality_price ), 0 ) AS ynyh FROM inpt_settle a, insure_individual_settle b WHERE a.id = b.settle_id AND a.hosp_code = b.hosp_code AND a.is_settle = '1' AND a.hosp_code = #{hospCode} ) ss WHERE 1 = 1
and ywlx IN
#{item}
and DATE_FORMAT(settleTime,'%Y-%m-%d') = #{searchDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d') >= #{startDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d') <= #{endDate}
GROUP BY ywlx, patientCode )tableSum

SELECT ywlx, patientCode, SUM( ss.totalPrice ) AS totalPrice, SUM( ss.planPrice ) AS planPrice, SUM( ss.civilPrice ) AS civilPrice, SUM( ss.seriousPrice ) AS seriousPrice, SUM( ss.retirePrice ) AS retirePrice, SUM( ss.hospPrice ) AS hospPrice, SUM( ss.personPrice ) AS personPrice, SUM( ss.apTotalPrice ) AS apTotalPrice, SUM( ss.ynyh ) AS ynyh, SUM( ss.mafPay) AS mafPay, SUM( ss.comPay ) AS comPay, /*企业补充医疗*/ SUM( ss.personalPrice) as personalPrice FROM ( SELECT 'MZ' AS ywlx, DATE_FORMAT( a.settle_time, '%Y-%m-%d %H:%i:%s' ) AS settleTime, a.patient_code AS patientCode, IFNULL( b.total_price, 0 ) AS totalPrice, IFNULL( b.plan_price, 0 ) AS planPrice, IFNULL( b.civil_price, 0 ) AS civilPrice, IFNULL( b.serious_price, 0 ) AS seriousPrice, IFNULL( b.retire_price, 0 ) AS retirePrice, IFNULL( b.hosp_price, 0 ) AS hospPrice, IFNULL( b.person_price, 0 ) AS personPrice, 0 AS apTotalPrice, IFNULL(( a.total_price - a.reality_price ), 0 ) AS ynyh, ifnull(b.maf_pay, 0.00) as mafPay, ifnull(b.com_pay, 0.00) as comPay, /*企业补充医疗*/ ifnull(b.personal_price, 0.00) as personalPrice FROM outpt_settle a, insure_individual_settle b WHERE a.hosp_code = b.hosp_code AND a.id = b.settle_id AND a.is_settle = '1' AND a.hosp_code = #{hospCode}
AND b.aka130 = #{medType}
UNION ALL SELECT 'ZY' AS ywlx, DATE_FORMAT( a.settle_time, '%Y-%m-%d %H:%i:%s' ) AS settleTime, a.patient_code AS patientCode, IFNULL( b.total_price, 0 ) AS totalPrice, IFNULL( b.plan_price, 0 ) AS planPrice, IFNULL( b.civil_price, 0 ) AS civilPrice, IFNULL( b.serious_price, 0 ) AS seriousPrice, IFNULL( b.retire_price, 0 ) AS retirePrice, IFNULL( b.hosp_price, 0 ) AS hospPrice, IFNULL( b.person_price, 0 ) AS personPrice, IFNULL( a.ap_total_price, 0 ) AS apTotalPrice, IFNULL(( a.total_price - a.reality_price ), 0 ) AS ynyh, ifnull(b.maf_pay, 0.00) as mafPay, ifnull(b.com_pay, 0.00) as comPay, /*企业补充医疗*/ ifnull(b.personal_price, 0.00) as personalPrice FROM inpt_settle a, insure_individual_settle b WHERE a.id = b.settle_id AND a.hosp_code = b.hosp_code AND a.is_settle = '1' AND a.status_code = '0' AND a.hosp_code = #{hospCode}
AND b.aka130 = #{medType}
) ss WHERE 1 = 1
and ywlx IN
#{item}
and DATE_FORMAT(settleTime,'%Y-%m-%d') = #{searchDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d %H:%i:%s') >= #{startDate}
and DATE_FORMAT(settleTime,'%Y-%m-%d %H:%i:%s') <= #{endDate}
and patientCode =#{patientCode}
GROUP BY ywlx, patientCode

SELECT a.id, a.hosp_code, a.visit_time as visitTime, (CONCAT( a.name,'/', case a.gender_code when '1' then '男' when '2' then '女' else '' end, '/', a.age, case a.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '天' else '' end )) AS patientInfo, a.cert_no as certNo, a.phone, ifnull( a.is_first_visit, '0' ) AS isFirstVisit, a.visit_no as visitNo, a.doctor_name as doctorName, a.dept_name AS visitDeptName, a.patient_code as patientCode, (SELECT name from base_disease bd where od.hosp_code = bd.hosp_code and od.disease_id = bd.id) as diagnoseName, od.crte_time as diagnoseTime, omr.start_date as startDate, ifnull( a.is_food_borne, 0 ) AS isFoodBorne, b.reality_price as totalPrice, a.now_address as nowAddress, a.occupation_code as occupationCode, a.contact_name as contactName FROM outpt_visit a JOIN ( SELECT t.hosp_code, t.visit_id, SUM(t.reality_price) as reality_price FROM ( SELECT ors.hosp_code, org.visit_id, ors.reality_price FROM outpt_register_settle ors JOIN outpt_register org ON ors.hosp_code = org.hosp_code AND ors.register_id = org.id where ors.hosp_code = #{hospCode} and ors.status_code = '0' UNION ALL SELECT hosp_code, visit_id, reality_price FROM outpt_settle WHERE hosp_code = #{hospCode} and is_settle = '1' and status_code = '0' ) t GROUP BY t.hosp_code, t.visit_id ) b ON a.hosp_code = b.hosp_code AND a.id = b.visit_id LEFT JOIN outpt_diagnose od ON a.hosp_code = od.hosp_code AND a.id = od.visit_id AND od.is_main = '1' LEFT JOIN ( SELECT visit_id, hosp_code, max( start_date ) AS start_date FROM outpt_medical_record WHERE hosp_code = #{hospCode} GROUP BY visit_id, hosp_code ) omr ON a.hosp_code = omr.hosp_code and a.id = omr.visit_id WHERE a.hosp_code = #{hospCode}
and a.patient_code = #{typeCode}
AND DATE_FORMAT(a.visit_time, '%Y-%m-%d %H:%i%s') >= date_format(#{startDate}, '%Y-%m-%d %H:%i%s')
AND DATE_FORMAT(a.visit_time, '%Y-%m-%d %H:%i%s') <= date_format(#{endDate}, '%Y-%m-%d %H:%i%s')
AND ( a.name LIKE CONCAT('%', #{keyword}, '%') OR a.visit_no LIKE CONCAT('%', #{keyword}, '%') OR a.pym LIKE CONCAT('%', #{keyword}, '%') OR a.wbm LIKE CONCAT('%', #{keyword}, '%') OR a.cert_no LIKE CONCAT('%', #{keyword}, '%'))
and a.doctor_id = #{doctorId}
and a.dept_id = #{deptId}
ORDER BY a.visit_time DESC

