SELECT type_code, code, good_name, spec, unit_code, num, concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name, prod_code, prod_name, ddd FROM ( SELECT max('门诊') type_code, g.code, g.good_name, g.spec, g.unit_code, sum(a.num) num, (case max(g.antibacterial_code) when 1 then '抗菌类' when 2 then '抗菌类' when 3 then '特殊使用抗菌药' else '' end) antibacterial_name, if(max(g.proton_pump) = '1','质子泵','') proton_pump_name, g.prod_code, (select name from base_product t where t.hosp_code = g.hosp_code and t.code = g.prod_code limit 1) prod_name, max(g.ddd) ddd FROM base_drug g join phar_out_distribute_all_detail a on g.hosp_code =a.hosp_code and g.id = a.item_id join phar_out_distribute b on a.distribute_id=b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time > #{startDate}
and b.crte_time <= #{endDate}
group by code,good_name,g.prod_code,spec,unit_code )tab where tab.num !=0

SELECT crte_time, sum( visit_number ) visit_number, concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name FROM ( SELECT DATE_FORMAT( ( b.crte_time ), '%Y-%m' ) crte_time, g.antibacterial_code, ( CASE g.antibacterial_code WHEN 1 THEN '抗菌类' WHEN 2 THEN '抗菌类' WHEN 3 THEN '特殊使用抗菌药' ELSE '' END ) antibacterial_name, IF ( g.proton_pump = '1', '质子泵', '' ) proton_pump_name, b.visit_id, 1 visit_number FROM base_drug g JOIN phar_out_distribute_all_detail a ON g.hosp_code = a.hosp_code AND g.id = a.item_id JOIN phar_out_distribute b ON a.distribute_id = b.id WHERE g.hosp_code = #{hospCode} and a.num >0 and a.num > a.total_back_num AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time > #{startDate}
and b.crte_time <= #{endDate}
and g.prep_code in ('241','16','138')
GROUP BY DATE_FORMAT( ( b.crte_time ), '%Y-%m' ), g.antibacterial_code, g.proton_pump, b.visit_id ) tab GROUP BY crte_time, antibacterial_name, proton_pump_name ORDER BY crte_time ASC

SELECT type_code, code, good_name, spec, unit_code, num, concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name, prod_code, prod_name, ddd FROM ( SELECT max('住院') type_code, g.code, g.good_name, g.spec, g.unit_code, sum(a.num) num, (case max(g.antibacterial_code) when 1 then '抗菌类' when 2 then '抗菌类' when 3 then '特殊使用抗菌药' else '' end) antibacterial_name, if(max(g.proton_pump) = '1','质子泵','') proton_pump_name, g.prod_code, (select name from base_product t where t.hosp_code = g.hosp_code and t.code = g.prod_code limit 1) prod_name, max(g.ddd) ddd FROM base_drug g join phar_in_distribute_all_detail a on g.hosp_code =a.hosp_code and g.id = a.item_id join phar_in_distribute b on a.distribute_id=b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time > #{startDate}
and b.crte_time <= #{endDate}
