SELECT concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name, sum( visit_number ) visit_number FROM ( SELECT g.antibacterial_code, ( CASE g.antibacterial_code WHEN 1 THEN '抗菌类' WHEN 2 THEN '抗菌类' WHEN 3 THEN '特殊使用抗菌药' ELSE '' END ) antibacterial_name, IF ( g.proton_pump = '1', '质子泵', '' ) proton_pump_name, a.visit_id, 1 visit_number FROM base_drug g JOIN phar_in_distribute_all_detail a ON g.hosp_code = a.hosp_code AND g.id = a.item_id JOIN phar_in_distribute b ON a.distribute_id = b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time > #{startDate}
and b.crte_time <= #{endDate}
and g.prep_code in ('241','16','138')
GROUP BY g.antibacterial_code, g.proton_pump, a.visit_id having sum(a.num) > 0 ) tab GROUP BY antibacterial_name, proton_pump_name

select t2.zyNum, t1.* from ( SELECT IFNULL(SUM( CASE a.status_code WHEN '1' THEN 1 ELSE 0 END ), 0 ) AS ryNum,
IFNULL(SUM( CASE a.status_code WHEN '5' THEN 1 ELSE 0 END ), 0 ) AS ycyNum, IFNULL(SUM( CASE a.status_code WHEN '7' THEN 1 ELSE 0 END ), 0 ) AS cyNum, IFNULL(SUM( b.num ), 0 ) as babyNum, a.in_dept_id as inDeptId, a.hosp_code, (SELECT code from base_dept bd where bd.id = a.in_dept_id and bd.hosp_code = a.hosp_code) as deptCode, a.in_dept_name as inDeptName FROM inpt_visit a left join (SELECT ib.visit_id, ib.hosp_code, COUNT(ib.id) as num FROM inpt_baby ib GROUP BY ib.visit_id, ib.hosp_code) b on a.hosp_code = b.hosp_code and a.id = b.visit_id where a.hosp_code = #{hospCode}
and a.in_time >= #{startDate}
and a.in_time <= #{endDate}
and a.in_dept_id = #{deptId}
GROUP BY a.in_dept_id, a.in_dept_name, a.hosp_code ) t1 join ( SELECT a.in_dept_id as inDeptId, a.in_dept_name as inDeptName, a.hosp_code, IFNULL(SUM( CASE a.status_code WHEN '2' THEN 1 ELSE 0 END ), 0 ) AS zyNum FROM inpt_visit a where a.hosp_code = #{hospCode}
and a.in_time <= #{endDate}
GROUP BY a.in_dept_id,a.in_dept_name, a.hosp_code ) t2 on t1.hosp_code = t2.hosp_code and t1.inDeptId = t2.inDeptId

SELECT t1.dept_id as deptId, (SELECT code from base_dept bd where bd.hosp_code = t1.hosp_code and t1.dept_id = bd.id) as deptCode, t1.dept_name as deptName, t1.hosp_code as hospCode, IFNULL(t3.zyNum, 0) as zyNum, IFNULL(t2.cyNum, 0) as cyNum, IFNULL(t1.szDays, 0) as szDays, IFNULL(t2.czDays, 0) as czDays, IFNULL(CONVERT(t2.czDays/t2.cyNum, DECIMAL(14,2)),0) as pjDays FROM ( SELECT hosp_code, in_dept_id as dept_id , in_dept_name as dept_name,
IFNULL(sum((case when DATE_FORMAT( in_time, '%Y-%m-%d %H:%i:%s' ) < DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s') and out_time is not null and DATE_FORMAT( out_time, '%Y-%m-%d %H:%i:%s' ) BETWEEN DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) AND DATE_FORMAT( #{endDate}, '%Y-%m-%d %H:%i:%s' ) then if( DATEDIFF(out_time, #{startDate}) = 0, 1, DATEDIFF(out_time, #{startDate})) when DATE_FORMAT( in_time, '%Y-%m-%d %H:%i:%s') < DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) and DATE_FORMAT( out_time, '%Y-%m-%d %H:%i:%s') < DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) then 0 when DATE_FORMAT( in_time, '%Y-%m-%d %H:%i:%s' ) < DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) and (out_time is null or out_time = '' or DATE_FORMAT( out_time, '%Y-%m-%d %H:%i:%s') > DATE_FORMAT(#{endDate}, '%Y-%m-%d %H:%i:%s') ) then DATEDIFF(#{endDate}, #{startDate}) when DATE_FORMAT( in_time, '%Y-%m-%d %H:%i:%s' ) BETWEEN DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) AND DATE_FORMAT( #{endDate}, '%Y-%m-%d %H:%i:%s' ) and out_time is not null and DATE_FORMAT( out_time, '%Y-%m-%d %H:%i:%s' ) BETWEEN DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) AND DATE_FORMAT( #{endDate}, '%Y-%m-%d %H:%i:%s' ) then if(DATEDIFF( out_time, in_time) = 0, 1,DATEDIFF( out_time, in_time)) WHEN DATE_FORMAT( in_time, '%Y-%m-%d %H:%i:%s' ) BETWEEN DATE_FORMAT( #{startDate}, '%Y-%m-%d %H:%i:%s' ) AND DATE_FORMAT( #{endDate}, '%Y-%m-%d %H:%i:%s' ) and (out_time is null or out_time = '' or DATE_FORMAT( out_time, '%Y-%m-%d %H:%i:%s') > DATE_FORMAT(#{endDate}, '%Y-%m-%d %H:%i:%s')) then if(DATEDIFF(#{endDate}, in_time) = 0, 1, DATEDIFF(#{endDate}, in_time)) when DATE_FORMAT( in_time, '%Y-%m-%d %H:%i:%s') > DATE_FORMAT( #{endDate}, '%Y-%m-%d %H:%i:%s') then 0 end)), 0) as szDays
FROM inpt_visit where hosp_code = #{hospCode} and bed_id is not null and status_code in ('2','5','7')
and in_dept_id = #{deptId}
GROUP BY in_dept_id, in_dept_name, hosp_code ) t1 LEFT JOIN ( SELECT out_dept_id as dept_id, out_dept_name as dept_name, hosp_code, count(1) AS cyNum, IFNULL(SUM( CASE WHEN DATEDIFF(IFNULL(DATE_FORMAT(out_time,'%Y-%m-%d %H:%i:%s'), DATE_FORMAT(#{endDate} ,'%Y-%m-%d %H:%i:%s')), DATE_FORMAT(in_time,'%Y-%m-%d %H:%i:%s')) = 0 THEN 1 ELSE DATEDIFF(IFNULL(DATE_FORMAT(out_time,'%Y-%m-%d %H:%i:%s'), DATE_FORMAT(#{endDate} ,'%Y-%m-%d %H:%i:%s')), DATE_FORMAT(in_time,'%Y-%m-%d %H:%i:%s')) END ), 0) as czDays FROM inpt_visit where hosp_code = #{hospCode} and status_code in('2','5','7')
and out_dept_id = #{deptId}
and DATE_FORMAT(out_time,'%Y-%m-%d %H:%i:%s') BETWEEN DATE_FORMAT(#{startDate} ,'%Y-%m-%d %H:%i:%s') and DATE_FORMAT(#{endDate} ,'%Y-%m-%d %H:%i:%s')
GROUP BY out_dept_id, out_dept_name, hosp_code ) t2 on t1.hosp_code = t2.hosp_code and t1.dept_id = t2.dept_id left join ( SELECT a.in_dept_id as dept_id, a.in_dept_name as dept_name, a.hosp_code, IFNULL(SUM( CASE a.status_code WHEN '2' THEN 1 ELSE 0 END ), 0 ) AS zyNum FROM inpt_visit a where a.hosp_code = #{hospCode}
and DATE_FORMAT(a.in_time, '%Y-%m-%d %H:%i:%s') <= #{endDate}
GROUP BY a.in_dept_id,a.in_dept_name, a.hosp_code ) t3 on t1.hosp_code = t3.hosp_code and t1.dept_id = t3.dept_id

SELECT crte_time, type_code, code, usual_name, good_name, spec, unit_code, split_ratio, num, price, total_price, concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name, prod_code, prod_name FROM ( SELECT date_format(b.crte_time,'%Y%m%d') crte_time, max('门诊') type_code, g.code, max(g.usual_name) usual_name, g.good_name, g.spec, g.unit_code, max(g.split_ratio) split_ratio, sum(a.num) num, max(a.price) price, sum(a.total_price) total_price, (case max(g.antibacterial_code) when 1 then '抗菌类' when 2 then '抗菌类' when 3 then '特殊使用抗菌药' else '' end) antibacterial_name, if(max(g.proton_pump) = '1','质子泵','') proton_pump_name, g.prod_code, (select name from base_product t where t.hosp_code = g.hosp_code and t.code = g.prod_code limit 1) prod_name, max(g.ddd) ddd FROM base_drug g join phar_out_distribute_all_detail a on g.hosp_code =a.hosp_code and g.id = a.item_id join phar_out_distribute b on a.distribute_id=b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time >= #{startDate}
and b.crte_time <= #{endDate}
group by date_format(b.crte_time,'%Y%m%d'),code,good_name,g.prod_code,spec,unit_code )tab where tab.num != 0 order by tab.code asc, tab.crte_time desc

SELECT crte_time, type_code, code, usual_name, good_name, spec, unit_code, split_ratio, num, price, total_price, concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name, prod_code, prod_name FROM ( SELECT date_format(b.crte_time,'%Y%m%d') crte_time, max('住院') type_code, g.code, max(g.usual_name) usual_name, g.good_name, g.spec, g.unit_code, max(g.split_ratio) split_ratio, sum(a.num) num, max(a.price) price, sum(a.total_price) total_price, (case max(g.antibacterial_code) when 1 then '抗菌类' when 2 then '抗菌类' when 3 then '特殊使用抗菌药' else '' end) antibacterial_name, if(max(g.proton_pump) = '1','质子泵','') proton_pump_name, g.prod_code, (select name from base_product t where t.hosp_code = g.hosp_code and t.code = g.prod_code limit 1) prod_name, max(g.ddd) ddd FROM base_drug g join phar_in_distribute_all_detail a on g.hosp_code =a.hosp_code and g.id = a.item_id join phar_in_distribute b on a.distribute_id=b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time >=#{startDate}
and b.crte_time <= #{endDate}
group by date_format(b.crte_time,'%Y%m%d'),code,good_name,g.prod_code,spec,unit_code
)tab where num > 0 order by tab.code asc, tab.crte_time desc

SELECT crte_time, type_code, code, usual_name, good_name, spec, unit_code, split_ratio, num, price, total_price, concat( antibacterial_name, IF ( antibacterial_name != '' AND proton_pump_name != '', '/', '' ), proton_pump_name ) antibacterial_name, prod_code, prod_name FROM ( SELECT date_format(b.crte_time,'%Y%m') crte_time, max('住院') type_code, g.code, max(g.usual_name) usual_name, g.good_name, g.spec, g.unit_code, max(g.split_ratio) split_ratio, sum(a.num) num, max(a.price) price, sum(a.total_price) total_price, (case max(g.antibacterial_code) when 1 then '抗菌类' when 2 then '抗菌类' when 3 then '特殊使用抗菌药' else '' end) antibacterial_name, if(max(g.proton_pump) = '1','质子泵','') proton_pump_name, g.prod_code, (select name from base_product t where t.hosp_code = g.hosp_code and t.code = g.prod_code limit 1) prod_name, max(g.ddd) ddd FROM base_drug g join phar_in_distribute_all_detail a on g.hosp_code =a.hosp_code and g.id = a.item_id join phar_in_distribute b on a.distribute_id=b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time >=#{startDate}
and b.crte_time <= #{endDate}
group by date_format(b.crte_time,'%Y%m'),code,good_name,g.prod_code,spec,unit_code union all SELECT date_format(b.crte_time,'%Y%m') crte_time, max('门诊') type_code, g.code, max(g.usual_name) usual_name, g.good_name, g.spec, g.unit_code, max(g.split_ratio) split_ratio, sum(a.num) num, max(a.price) price, sum(a.total_price) total_price, (case max(g.antibacterial_code) when 1 then '抗菌类' when 2 then '抗菌类' when 3 then '特殊使用抗菌药' else '' end) antibacterial_name, if(max(g.proton_pump) = '1','质子泵','') proton_pump_name, g.prod_code, (select name from base_product t where t.hosp_code = g.hosp_code and t.code = g.prod_code limit 1) prod_name, max(g.ddd) ddd FROM base_drug g join phar_out_distribute_all_detail a on g.hosp_code =a.hosp_code and g.id = a.item_id join phar_out_distribute b on a.distribute_id=b.id WHERE g.hosp_code = #{hospCode} AND(( g.antibacterial_code IS NOT NULL AND g.antibacterial_code != '' ) or g.proton_pump = '1')
and b.crte_time >= #{startDate}
and b.crte_time <= #{endDate}
group by date_format(b.crte_time,'%Y%m'),code,good_name,g.prod_code,spec,unit_code )tab where tab.num != 0 order by tab.crte_time desc,tab.code asc

select sum(s.price) as 'priceSum', sum(s.total_price) as 'totalPriceSum', sum(s.total_num) as 'numSum' from ( SELECT ia.price, ia.total_price, ia.total_num FROM medic_apply ma LEFT JOIN inpt_advice ia ON ia.hosp_code = ma.hosp_code AND ma.order_no = ia.order_no AND ma.visit_id = ia.visit_id LEFT JOIN inpt_visit iv ON ma.visit_id = iv.id AND iv.hosp_code = ma.hosp_code LEFT JOIN inpt_baby ib ON ia.baby_id = ib.id WHERE ma.is_inpt = '1' and ma.hosp_code = #{hospCode}
and ma.crte_time > #{startTime}
and ma.crte_time < #{endTime}
and ma.type_code = #{typeCode}
and ma.imp_dept_id = #{impDeptId}
and (iv.name like concat('%',#{keyword},'%') or iv.cert_no like concat('%',#{keyword},'%') or ma.in_no like concat('%',#{keyword},'%'))
and ma.content like concat('%',#{itemName},'%')
and ma.medic_type = #{technologyCode}
and ma.document_sta = #{documentSta}
and 1=2
union all SELECT pd.price as price, pd.total_price as total_price, pd.total_num as total_num FROM medic_apply ma, outpt_prescribe_detail pd, outpt_visit ov, outpt_prescribe op, outpt_settle os WHERE ma.visit_id = ov.id and ma.hosp_code = #{hospCode} and ma.hosp_code = ov.hosp_code and ma.opd_id = pd.id and ma.hosp_code = pd.hosp_code and ma.is_inpt = '0' and ma.document_sta <> '01' and op.hosp_code = pd.hosp_code and op.id = pd.op_id and os.hosp_code = op.hosp_code and os.id = op.settle_id
and ma.type_code = #{typeCode}
and os.settle_time >= #{startTime}
and os.settle_time <= #{endTime}
and ma.imp_dept_id = #{impDeptId}
and (ov.name like concat('%',#{keyword},'%') or ov.cert_no like concat('%',#{keyword},'%') or ma.in_no like concat('%',#{keyword},'%'))
and ma.content like concat('%',#{itemName},'%')
and ma.document_sta = #{documentSta}
and 1=2
and ma.medic_type = #{technologyCode}
) s
