mapper.select:
select
a.bfc_id,c.name as bfcName
from inpt_cost a
left join inpt_settle b on a.settle_id = b.id and a.hosp_code = b.hosp_code and b.status_code = '0'
join base_finance_classify c on a.bfc_id = c.id and a.hosp_code = c.hosp_code
join inpt_visit d on a.visit_id = d.id and a.hosp_code = d.hosp_code
where a.hosp_code = #{hospCode}

    and d.status_code = #{statusCode}

        and b.id is not null

            and b.settle_time >= #{startDate}


            and b.settle_time <= #{endDate}




            and d.out_time >= #{startDate}


            and d.out_time <= #{endDate}




            and d.in_time >= #{startDate}


            and d.in_time <= #{endDate}





    and (
    d.outpt_doctor_name like concat('%', #{doctorName}, '%')
    or d.zz_doctor_name like concat('%', #{doctorName}, '%')
    or d.jz_doctor_name like concat('%', #{doctorName}, '%')
    or d.zg_doctor_name like concat('%', #{doctorName}, '%')
    )

group by a.bfc_id,c.name

select a.id, a.hosp_code, a.profile_id, a.in_profile, a.in_no, case when ib.id is null then a.name else ib.name end as name, case when ib.id is null then a.gender_code else ib.gender_code end as gender_code, case when ib.id is null then a.age else 0 end as age, a.age_unit_code, a.birthday, a.nation_code, a.cert_code, a.cert_no, a.marry_code, a.phone, a.address, a.preferential_type_id, case when ib.id is null then a.patient_code else '' end as patient_code, a.receive_hosp_name, a.bed_id, a.bed_name, a.nursing_code, a.diet_type, a.Illness_code, a.status_code, a.in_ward_id, a.in_dept_id, a.in_dept_name, case when ib.id is null then a.in_time else ib.birth_time end as in_time, a.zz_doctor_id, a.zz_doctor_name, a.jz_doctor_id, a.jz_doctor_name, a.zg_doctor_id, a.zg_doctor_name, a.in_remark, a.in_mode_code, a.in_disease_id, case when ib.id is null then a.in_disease_name else '' end as in_disease_name, a.in_disease_icd10, a.in_situation_code, a.outpt_visit_no, a.outpt_doctor_id, a.outpt_doctor_name, a.outpt_disease_id, a.outpt_disease_name, a.outpt_disease_icd10, a.out_ward_id, a.out_dept_id, a.out_dept_name, a.out_time, a.out_disease_id, a.out_disease_name, a.out_disease_icd10, a.out_oper_id, a.out_oper_name, a.out_oper_time, a.out_situation_code, a.out_remark, a.out_mode_code, a.is_archive, a.archive_time, a.archive_id, a.archive_name, a.insure_code, a.insure_org_code, a.insure_no, a.insure_biz_code, a.insure_treat_code, a.insure_patient_id, a.insure_remark, case when ib.id is null then a.total_advance else 0 end as total_advance, a.total_cost, a.total_balance, a.crte_id, a.crte_name, a.crte_time, c.settle_time, ifnull(c.reality_price, a.total_cost) as totalCost, c.settle_take_price as settleTakePrice, c.settle_back_price as settleBackPrice, (c.mi_price-ifnull(c.acct_pay, 0)) as miPrice, c.acct_pay as acctPay, case when ib.id is null then (case when DATEDIFF(ifnull(a.out_time, now()),a.in_time)=0 then 1 else DATEDIFF(ifnull(a.out_time, now()),a.in_time) end) else (case when DATEDIFF(ifnull(a.out_time, now()),ib.birth_time)=0 then 1 else DATEDIFF(ifnull(a.out_time, now()),ib.birth_time) end) end as inDay, b.* from inpt_visit a left join ( select ${sqlStr} d.id visistID, c.baby_id from inpt_visit d join inpt_cost a on a.visit_id = d.id and a.hosp_code = d.hosp_code left join inpt_settle c on a.hosp_code = c.hosp_code and a.visit_id = c.visit_id and c.id = a.settle_id where a.hosp_code = #{hospCode}
and d.status_code = #{statusCode}
and c.settle_time >= #{startDate}
and c.settle_time <= #{endDate}
and d.out_time >= #{startDate}
and d.out_time <= #{endDate}
and d.in_time >= #{startDate}
and d.in_time <= #{endDate}
and ( d.outpt_doctor_name like concat('%', #{doctorName}, '%') or d.zz_doctor_name like concat('%', #{doctorName}, '%') or d.jz_doctor_name like concat('%', #{doctorName}, '%') or d.zg_doctor_name like concat('%', #{doctorName}, '%') )
and d.in_dept_id = #{inDeptId}
and d.patient_code = #{patientCode}
and d.name like concat('%', #{keyword}, '%')
group by c.baby_id, d.id ) b on a.id = b.visistID left join inpt_settle c on a.id = c.visit_id and a.hosp_code = c.hosp_code and c.status_code = '0' and (c.baby_id = b.baby_id || (b.baby_id is null and c.baby_id is null) ) left join inpt_baby ib on ib.hosp_code = a.hosp_code and ib.visit_id = a.id and ib.id = c.baby_id where a.hosp_code = #{hospCode}
and a.status_code = #{statusCode}
and c.settle_time >= #{startDate}
and c.settle_time <= #{endDate}
and a.out_time >= #{startDate}
and a.out_time <= #{endDate}
and a.in_time >= #{startDate}
and a.in_time <= #{endDate}
and ( a.outpt_doctor_name like concat('%', #{doctorName}, '%') or a.zz_doctor_name like concat('%', #{doctorName}, '%') or a.jz_doctor_name like concat('%', #{doctorName}, '%') or a.zg_doctor_name like concat('%', #{doctorName}, '%') )
and a.in_dept_id = #{inDeptId}
and a.patient_code = #{patientCode}
and a.name like concat('%', #{keyword}, '%')
order by a.out_time desc

select max(a.out_dept_name) outDeptName,
a.out_dept_id outDeptId,
count(a.id) peopleNum,
sum(c.reality_price) totalCost,
sum(c.settle_take_price) as settleTakePriceAll,
sum(c.settle_back_price) as settleBackPriceAll,
b.*
from inpt_visit a
join (
select ${sqlStr} d.out_dept_id
from inpt_visit d
join inpt_cost a on a.visit_id = d.id and a.hosp_code = d.hosp_code


        left join inpt_settle c on a.visit_id = c.visit_id and a.settle_id =c.id and a.hosp_code = c.hosp_code
        where a.hosp_code = #{hospCode}
        and d.status_code = #{statusCode}
        and c.id is not null and c.is_settle = '1'

            and c.settle_time >= #{startDate}


            and c.settle_time <= #{endDate}



        left join inpt_settle c on a.visit_id = c.visit_id and a.hosp_code = c.hosp_code
        where a.hosp_code = #{hospCode}
        and d.status_code = #{statusCode}

            and d.out_time >= #{startDate}


            and d.out_time <= #{endDate}



        left join inpt_settle c on a.visit_id = c.visit_id and a.hosp_code = c.hosp_code
        where a.hosp_code = #{hospCode}
        and d.status_code = #{statusCode}

            and d.in_time >= #{startDate}


            and d.in_time <= #{endDate}





    and (
    d.outpt_doctor_name like concat('%', #{doctorName}, '%')
    or d.zz_doctor_name like concat('%', #{doctorName}, '%')
    or d.jz_doctor_name like concat('%', #{doctorName}, '%')
    or d.zg_doctor_name like concat('%', #{doctorName}, '%')
    )


    and d.name like concat('%', #{keyword}, '%')

group by d.out_dept_id
) b on a.out_dept_id = b.out_dept_id
left join inpt_settle c on a.id = c.visit_id and a.hosp_code = c.hosp_code and c.status_code = '0'
where a.hosp_code = #{hospCode}

    and a.status_code = #{statusCode}



            and c.settle_time >= #{startDate}


            and c.settle_time <= #{endDate}




            and a.out_time >= #{startDate}


            and a.out_time <= #{endDate}




            and a.in_time >= #{startDate}


            and a.in_time <= #{endDate}





    and (
    a.outpt_doctor_name like concat('%', #{doctorName}, '%')
    or a.zz_doctor_name like concat('%', #{doctorName}, '%')
    or a.jz_doctor_name like concat('%', #{doctorName}, '%')
    or a.zg_doctor_name like concat('%', #{doctorName}, '%')
    )


    and a.name like concat('%', #{keyword}, '%')

group by a.out_dept_id

select id, hosp_code, visit_id, baby_id, iat_id, iatd_group_no, iatd_seq_no, advice_exec_id, source_code, source_id, old_cost_id, source_dept_id, in_dept_id, bfc_id, item_code, item_id, item_name, price, num, spec, prep_code, dosage, dosage_unit_code, usage_code, rate_id, speed_code, use_days, num_unit_code, total_num, total_num_unit_code, herb_note_code, use_code, herb_num, total_price, preferential_price, reality_price, back_num, doctor_id, doctor_name, dept_id, phar_id, is_dist, is_give, back_code, is_ok, ok_id, ok_name, ok_time, settle_code, settle_id, is_check, check_id, check_name, check_time, zz_doctor_id, zz_doctor_name, jz_doctor_id, jz_doctor_name, zg_doctor_id, zg_doctor_name, exec_id, exec_name, exec_time, exec_dept_id, plan_exec_time, status_code, is_cost, cost_time, crte_id, crte_name, crte_time, (select b.name from base_finance_classify b where bfc_id = b.id and b.hosp_code = #{hospCode}) as bfcName from inpt_cost a where a.hosp_code = #{hospCode}
and a.visit_id = #{visitId}
and source_dept_id = #{sourceDeptId}
and date(crte_time) >= date(#{startTime})
and date(crte_time) <= date(#{stopTime})
and a.visit_id in
#{id}

select a.id, a.hosp_code, a.biz_id, a.location_no, a.item_code, a.item_id, a.item_name, a.spec, a.prep_code, a.num, a.unit_code, a.buy_price_all, a.sell_price_all, a.split_num, a.split_price, a.split_unit_code, a.stock_max, a.stock_min, a.stock_occupy, a.stock_time,a.crte_time,a.split_ratio, case when a.item_code='1' then bd.code when a.item_code='2' then bm.code end code, (case a.item_code when '1' then (SELECT NAME FROM base_product t WHERE t.hosp_code = bd.hosp_code AND t.CODE = bd.prod_code limit 1) when '0' then (SELECT NAME FROM base_product t WHERE t.hosp_code = bm.hosp_code AND t.CODE = bm.prod_code limit 1) end ) as prodName from stro_stock_time a left join base_drug bd on bd.id = a.item_id and bd.hosp_code = #{hospCode} left join base_material bm on bm.id = a.item_id and bm.hosp_code = #{hospCode} where a.hosp_code = #{hospCode} and a.stock_time = #{stockTime} and a.biz_id = #{bizId}
and ( a.item_name like concat('%',#{itemName},'%') or bd.usual_pym like concat('%',#{itemName},'%') or bd.good_pym like concat('%',#{itemName},'%') or bd.good_name like concat('%',#{itemName},'%') or bd.usual_name like concat('%',#{itemName},'%') or bm.name like concat('%',#{itemName},'%') or bm.pym like concat('%',#{itemName},'%') )
and ifnull(a.split_num,0)>0

SELECT a.item_id itemId, max(a.item_name) itemName, max(a.unit_code) unitCode, (select b.name from base_dept b where b.id = max(a.biz_id) and b.hosp_code = #{hospCode}) deptName, case max(a.item_code) when '1' then max(b.spec) when '2' then max(c.spec) end spec, case max(a.item_code) when '1' then max(b.code) when '2' then max(c.code) end drugCode, ifnull((select d.up_sell_price_all/d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.id = min(a.id)),0)upSellPrice, (select d.sell_price from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPrice, (select d.surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) num, (select d.sell_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPriceAll, (select d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)) upNum, ifnull((select d.up_sell_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)),0) upSellPriceAll, sum(if( a.outin_code='2',a.sell_price * a.num,0)) stroInNumPriceAll, sum(if( a.outin_code='2',a.num,0)) stroInNum, sum(if( a.outin_code='3',a.sell_price * a.num,0)) returnSuplPriceAll, sum(if( a.outin_code='3',a.num,0)) returnSuplPriceNum, sum(if( a.outin_code='4',a.sell_price * a.num,0)) stroToDeptPriAll, sum(if( a.outin_code='4',a.num,0)) stroToDeptPriNum, sum(if( a.outin_code='5',a.sell_price * a.num,0)) stroToPharPriAll, sum(if( a.outin_code='5',a.num,0)) stroToPharPriNum, sum(if( a.outin_code='8',a.sell_price * a.num,0)) reportLossesPriAll, sum(if( a.outin_code='8',a.num,0)) reportLossesNum, sum(if( a.outin_code='21',a.sell_price * a.num,0)) pharReturnStroPriAll, sum(if( a.outin_code='21',a.num,0)) pharReturnStroNum, sum(if( a.outin_code='24' and a.sell_price_all > a.up_sell_price_all,a.sell_price_all - a.up_sell_price_all,0)) adjustProfitPriAll, sum(if( a.outin_code='24' and a.sell_price_all < a.up_sell_price_all, a.sell_price_all - a.up_sell_price_all,0)) adjustLossPriAll, sum(if( a.outin_code='7' and a.num < 0,a.sell_price * a.num,0)) takeStockSubtractPriAll, sum(if( a.outin_code='7' and a.num < 0,a.num,0)) takeStockSubtractNum, sum(if( a.outin_code='7' and a.num > 0,a.sell_price * a.num,0)) takeStockAddPriAll, sum(if( a.outin_code='7' and a.num > 0, a.num,0)) takeStockAddNum FROM stro_invoicing a left join base_drug b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = "1" left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = "2" where a.hosp_code = #{hospCode} and a.outin_code in ('2','3','4','5','7','8','21','24') and a.biz_id in (select p.id from base_dept p where p.type_code in ("13","3") and p.hosp_code = #{hospCode})
and b.type_code = #{drugType}
and c.type_code = #{materialType}
and a.biz_id = #{bizId}
/*开始日期*/
and a.crte_time >= #{startDate}
/*结束日期*/
and a.crte_time <= #{endDate}
and ( a.item_name like concat('%', #{keyword}, '%') or b.usual_pym like concat('%', #{keyword}, '%') or b.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%'))
group by a.item_id

SELECT a.item_id itemId, max(a.item_name) itemName, max(a.unit_code) unitCode, (select b.name from base_dept b where b.id = max(a.biz_id) and b.hosp_code = #{hospCode}) deptName, case max(a.item_code) when '1' then max(b.spec) when '2' then max(c.spec) end spec, case max(a.item_code) when '1' then max(b.code) when '2' then max(c.code) end drugCode, ifnull((select d.up_buy_price_all/d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.id = min(a.id)),0)upSellPrice, (select d.buy_price from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPrice, (select d.surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) num, (select d.buy_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPriceAll, (select d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)) upNum, ifnull((select d.up_buy_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)),0) upSellPriceAll, sum(if( a.outin_code='2',a.buy_price * a.num,0)) stroInNumPriceAll, sum(if( a.outin_code='2',a.num,0)) stroInNum, sum(if( a.outin_code='3',a.buy_price * a.num,0)) returnSuplPriceAll, sum(if( a.outin_code='3',a.num,0)) returnSuplPriceNum, sum(if( a.outin_code='4',a.buy_price * a.num,0)) stroToDeptPriAll, sum(if( a.outin_code='4',a.num,0)) stroToDeptPriNum, sum(if( a.outin_code='5',a.buy_price * a.num,0)) stroToPharPriAll, sum(if( a.outin_code='5',a.num,0)) stroToPharPriNum, sum(if( a.outin_code='8',a.buy_price * a.num,0)) reportLossesPriAll, sum(if( a.outin_code='8',a.num,0)) reportLossesNum, sum(if( a.outin_code='21',a.buy_price * a.num,0)) pharReturnStroPriAll, sum(if( a.outin_code='21',a.num,0)) pharReturnStroNum, sum(if( a.outin_code='24' and a.buy_price_all > a.up_buy_price_all,a.buy_price_all - a.up_buy_price_all,0)) adjustProfitPriAll, sum(if( a.outin_code='24' and a.buy_price_all < a.up_buy_price_all, a.buy_price_all - a.up_buy_price_all,0)) adjustLossPriAll, sum(if( a.outin_code='7' and a.num < 0,a.buy_price * a.num,0)) takeStockSubtractPriAll, sum(if( a.outin_code='7' and a.num < 0,a.num,0)) takeStockSubtractNum, sum(if( a.outin_code='7' and a.num > 0,a.buy_price * a.num,0)) takeStockAddPriAll, sum(if( a.outin_code='7' and a.num > 0, a.num,0)) takeStockAddNum FROM stro_invoicing a left join base_drug b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = "1" left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = "2" where a.hosp_code = #{hospCode} and a.outin_code in ('2','3','4','5','7','8','21','24') and a.biz_id in (select p.id from base_dept p where p.type_code in ("13","3") and p.hosp_code = #{hospCode})
and b.type_code = #{drugType}
and c.type_code = #{materialType}
and a.biz_id = #{bizId}
/*开始日期*/
and a.crte_time >=#{startDate}
/*结束日期*/
and a.crte_time <= #{endDate}
and ( a.item_name like concat('%', #{keyword}, '%') or b.usual_pym like concat('%', #{keyword}, '%') or b.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%'))
group by a.item_id

SELECT a.item_id itemId, max(a.item_name) itemName, max(a.unit_code) unitCode, (select b.name from base_dept b where b.id = max(a.biz_id) and b.hosp_code = #{hospCode}) deptName, case max(a.item_code) when '1' then max(b.spec) when '2' then max(c.spec) end spec, case max(a.item_code) when '1' then max(b.code) when '2' then max(c.code) end drugCode, ifnull((select d.up_sell_price_all/d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and id = min(a.id)),0)upSellPrice, (select d.new_price from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPrice, (select d.surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) num, (select d.sell_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPriceAll, (select d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)) upNum, ifnull((select d.up_sell_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)),0) upSellPriceAll, sum(if( a.outin_code='20',a.sell_price * a.num,0)) pharInNumPriceAll, /**药房入库确认总金额**/ sum(if( a.outin_code='20',a.num,0)) pharInNum, /**药房入库确认总数量**/ sum(if( a.outin_code='8',a.sell_price * a.num,0)) reportLossesPriAll, /**药房报损总金额**/ sum(if( a.outin_code='8',a.num,0)) reportLossesNum, /**药房报损总数量**/ sum(if( a.outin_code='6',a.sell_price * a.num,0)) pharReturnStroPriAll, /**药房退药库库总金额**/ sum(if( a.outin_code='6',a.num,0)) pharReturnStroNum, /**药房退药库总数量**/ sum(if( a.outin_code='7' and a.num < 0,a.sell_price * a.num,0)) takeStockSubtractPriAll, /**药房盘亏总金额**/ sum(if( a.outin_code='7' and a.num < 0,a.num,0)) takeStockSubtractNum, /**药房盘亏总数量**/ sum(if( a.outin_code='7' and a.num > 0,a.sell_price * a.num,0)) takeStockAddPriAll, /**药房盘盈总金额**/ sum(if( a.outin_code='7' and a.num > 0, a.num,0)) takeStockAddNum, /**药房盘盈总数量**/ sum(if((a.outin_code='23' or a.outin_code='25'),a.sell_price * a.num,0)) outSalesPriAll, /**门诊销售总金额**/ sum(if((a.outin_code='23' or a.outin_code='25'),a.num,0)) outSalesNum, /**门诊销售总数量**/ sum(if((a.outin_code='27' or a.outin_code='28'),a.sell_price * a.num,0)) inSalesPriAll, /**住院销售总金额**/ sum(if((a.outin_code='27' or a.outin_code='28'),a.num,0)) inSalesNum, /**住院销售总数量**/ sum(if( a.outin_code='4',a.sell_price * a.num,0)) pharOutStroPriAll, /**药房出库科室总金额**/ sum(if( a.outin_code='4',a.num,0)) pharOutStroNum, /**药房出库科室总数量**/ sum(if( a.outin_code='24' and a.sell_price_all > a.up_sell_price_all,a.sell_price_all - a.up_sell_price_all,0)) adjustProfitPriAll, /**调盈**/ sum(if( a.outin_code='24' and a.sell_price_all < a.up_sell_price_all, a.sell_price_all - a.up_sell_price_all,0)) adjustLossPriAll /**调亏**/ FROM stro_invoicing a left join base_drug b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = "1" left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = "2" where a.hosp_code = #{hospCode} and a.outin_code in ( '7', '8', '20', '23', '25','6', '27', '28','24','4' ) and a.biz_id in (select p.id from base_dept p where p.type_code in ("4","5") and p.hosp_code = #{hospCode})
and b.type_code = #{drugType}
and c.type_code = #{materialType}
and a.biz_id = #{bizId}
/*开始日期*/
and a.crte_time >= #{startDate}
/*结束日期*/
and a.crte_time <= #{endDate}
and ( a.item_name like concat('%', #{keyword}, '%') or b.usual_pym like concat('%', #{keyword}, '%') or b.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%'))
group by a.item_id

SELECT a.item_id itemId, max(a.item_name) itemName, max(a.unit_code) unitCode, (select b.name from base_dept b where b.id = max(a.biz_id) and b.hosp_code = #{hospCode}) deptName, case max(a.item_code) when '1' then max(b.spec) when '2' then max(c.spec) end spec, case max(a.item_code) when '1' then max(b.code) when '2' then max(c.code) end drugCode, ifnull((select d.up_buy_price_all/d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)),0)upSellPrice, (select d.buy_price from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPrice, (select d.surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) num, (select d.buy_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = max(a.id)) sellPriceAll, (select d.up_surplus_num from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)) upNum, ifnull((select d.up_buy_price_all from stro_invoicing d where d.item_id = a.item_id and d.hosp_code = #{hospCode} and d.id = min(a.id)),0) upSellPriceAll, sum(if( a.outin_code='20',a.buy_price * a.num,0)) pharInNumPriceAll, /**药房入库确认总金额**/ sum(if( a.outin_code='20',a.num,0)) pharInNum, /**药房入库确认总数量**/ sum(if( a.outin_code='8',a.buy_price * a.num,0)) reportLossesPriAll, /**药房报损总金额**/ sum(if( a.outin_code='8',a.num,0)) reportLossesNum, /**药房报损总数量**/ sum(if( a.outin_code='6',a.buy_price * a.num,0)) pharReturnStroPriAll, /**药房退药库库总金额**/ sum(if( a.outin_code='6',a.num,0)) pharReturnStroNum, /**药房退药库总数量**/ sum(if( a.outin_code='7' and a.num < 0,a.buy_price * a.num,0)) takeStockSubtractPriAll, /**药房盘亏总金额**/ sum(if( a.outin_code='7' and a.num < 0,a.num,0)) takeStockSubtractNum, /**药房盘亏总数量**/ sum(if( a.outin_code='7' and a.num > 0,a.buy_price * a.num,0)) takeStockAddPriAll, /**药房盘盈总金额**/ sum(if( a.outin_code='7' and a.num > 0, a.num,0)) takeStockAddNum, /**药房盘盈总数量**/ sum(if((a.outin_code='23' or a.outin_code='25'),a.buy_price * a.num,0)) outSalesPriAll, /**门诊销售总金额**/ sum(if((a.outin_code='23' or a.outin_code='25'),a.num,0)) outSalesNum, /**门诊销售总数量**/ sum(if((a.outin_code='27' or a.outin_code='28'),a.buy_price * a.num,0)) inSalesPriAll, /**住院销售总金额**/ sum(if((a.outin_code='27' or a.outin_code='28'),a.num,0)) inSalesNum, /**住院销售总数量**/ sum(if( a.outin_code='4',a.buy_price * a.num,0)) pharOutStroPriAll, /**药房出库科室总金额**/ sum(if( a.outin_code='4',a.num,0)) pharOutStroNum, /**药房出库科室总数量**/ sum(if( a.outin_code='24' and a.sell_price_all > a.up_sell_price_all,a.sell_price_all - a.up_sell_price_all,0)) adjustProfitPriAll, /**调盈**/ sum(if( a.outin_code='24' and a.sell_price_all < a.up_sell_price_all, a.sell_price_all - a.up_sell_price_all,0)) adjustLossPriAll /**调亏**/ FROM stro_invoicing a left join base_drug b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = "1" left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = "2" where a.hosp_code = #{hospCode} and a.outin_code in ( '7', '8', '20', '23', '25','6','27','28','4','24' ) and a.biz_id in (select p.id from base_dept p where p.type_code in ("4","5") and p.hosp_code = #{hospCode})
and b.type_code = #{drugType}
and c.type_code = #{materialType}
and a.biz_id = #{bizId}
/*开始日期*/
and a.crte_time >=#{startDate}
/*结束日期*/
and a.crte_time <= #{endDate}
and ( a.item_name like concat('%', #{keyword}, '%') or b.usual_pym like concat('%', #{keyword}, '%') or b.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%'))
group by a.item_id

select a.id as id,a.item_name as itemName,a.spec,a.price,a.split_price as splitPrice, a.curr_unit_code as currUnitCode,a.unit_code as unitCode,a.split_unit_code as splitUnitCode,a.split_num as splitNum, case when a.curr_unit_code = a.unit_code then a.num else a.split_num end num,a.crte_time as crteTime,a.crte_name as crteName, a.item_code as itemCode, a.item_id as itemId,"门诊退药" as retrunType, (select name from base_dept where hosp_code = b.hosp_code and id = b.phar_id) as pharName, (select name from base_dept where hosp_code = b.hosp_code and id = op.dept_id) as deptName, '门诊病人' as statusCode, (case a.item_code when '1' then bd.type_code when '2' then c.type_code end ) typeCode, (concat(v.name, '/',(ifnull((select e.name from sys_code_detail e where e.value=v.gender_code and e.c_code='XB'),'') ), '/',ifnull(v.age,''), (ifnull((select name from sys_code_detail where c_code='NLDW' and value=v.age_unit_code and hosp_code=v.hosp_code),'')))) patientInfo from phar_out_distribute_all_detail a left join phar_out_distribute b on b.id = a.distribute_id and b.hosp_code = a.hosp_code left join outpt_visit v on v.id = b.visit_id and v.hosp_code = b.hosp_code left join outpt_prescribe op on op.id = a.op_id left join base_drug bd on a.item_id = bd.id and a.hosp_code = bd.hosp_code and a.item_code = "1" left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = "2" where ifnull(a.num,0) < 0 and a.hosp_code = #{hospCode}
and a.crte_time >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and a.crte_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
and op.dept_id = #{deptToldId}
and b.phar_id = #{pharId}
and ( a.item_name like concat('%', #{keyword}, '%') or bd.usual_pym like concat('%', #{keyword}, '%') or bd.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%') or b.crte_name like concat('%', #{keyword}, '%') or v.name like concat('%', #{keyword}, '%'))
order by a.crte_time desc

select v.status_code as statusCode, e.id as id,e.item_name as itemName,e.spec,e.price,e.split_price as splitPrice, e.curr_unit_code as currUnitCode,e.unit_code as unitCode,e.split_unit_code as splitUnitCode,e.split_num as splitNum, case when e.curr_unit_code = e.unit_code then e.num else e.split_num end num,e.crte_time as crteTime, e.crte_name as crteName,e.item_code as itemCode, e.item_id as itemId,"住院退药" as retrunType, (select name from base_dept where hosp_code = d.hosp_code and id = d.phar_id) as pharName, (select name from base_dept where hosp_code = d.hosp_code and id = ia.dept_id) as deptName, (concat(v.name, '/',(ifnull((select name from sys_code_detail where value = v.gender_code and c_code='XB' and hosp_code = e.hosp_code),'') ), '/',v.age, (ifnull((select name from sys_code_detail where c_code='NLDW' and value=v.age_unit_code and hosp_code=v.hosp_code),'岁')))) patientInfo from phar_in_distribute_all_detail e join phar_in_distribute d on e.hosp_code = d.hosp_code and d.id = e.distribute_id left join inpt_visit v on v.id = e.visit_id and v.hosp_code = e.hosp_code left join base_drug b on e.item_id = b.id and e.hosp_code = b.hosp_code and e.item_code = "1" left join base_material c on e.item_id = c.id and e.hosp_code = c.hosp_code and e.item_code = "2" left join inpt_advice ia on ia.id = e.advice_id WHERE e.hosp_code = #{hospCode} and ifnull(e.num,0) < 0
and e.crte_time >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and ia.dept_id = #{deptToldId}
and e.crte_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
and d.phar_id = #{pharId}
and v.status_code = #{statusCode}
and ( e.item_name like concat('%', #{keyword}, '%') or b.usual_pym like concat('%', #{keyword}, '%') or b.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%') or d.crte_name like concat('%', #{keyword}, '%') or v.name like concat('%', #{keyword}, '%'))
order by e.crte_time desc

select * from ( select e.id as id,e.item_name as itemName,e.spec,e.price,e.split_price as splitPrice, e.curr_unit_code as currUnitCode,e.unit_code as unitCode,e.split_unit_code as splitUnitCode,e.split_num as splitNum, case when e.curr_unit_code = e.unit_code then e.num else e.split_num end num,e.crte_time as crteTime, e.crte_name as crteName,e.item_code as itemCode, e.item_id as itemId,"住院退药" as retrunType, (select name from base_dept where hosp_code = d.hosp_code and id = d.phar_id) as pharName, (select name from base_dept where hosp_code = d.hosp_code and id = ia.dept_id) as deptName, v.status_code as statusCode, (concat(v.name, '/',(ifnull((select name from sys_code_detail sc where hosp_code = v.hosp_code and sc.value=v.gender_code and sc.c_code='XB'),'') ), '/',v.age, (ifnull((select name from sys_code_detail where c_code='NLDW' and value=v.age_unit_code and hosp_code=v.hosp_code),'')))) patientInfo FROM phar_in_distribute_all_detail e left join phar_in_distribute d on e.hosp_code = d.hosp_code and d.id = e.distribute_id left join inpt_visit v on v.id = e.visit_id and v.hosp_code = e.hosp_code left join base_drug b on e.item_id = b.id and e.hosp_code = b.hosp_code and e.item_code = "1" left join base_material c on e.item_id = c.id and e.hosp_code = c.hosp_code and e.item_code = "2" left join inpt_advice ia on ia.id = e.advice_id WHERE e.hosp_code = #{hospCode} and ifnull(e.num,0) < 0
and e.crte_time >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and e.crte_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
and d.phar_id = #{pharId}
and ia.dept_id = #{deptToldId}
and v.status_code = #{statusCode}
and ( e.item_name like concat('%', #{keyword}, '%') or b.usual_pym like concat('%', #{keyword}, '%') or b.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%') or d.crte_name like concat('%', #{keyword}, '%') or v.name like concat('%', #{keyword}, '%'))
union all select a.id as id,a.item_name as itemName,a.spec,a.price,a.split_price as splitPrice, a.curr_unit_code as currUnitCode,a.unit_code as unitCode,a.split_unit_code as splitUnitCode,a.split_num as splitNum, case when a.curr_unit_code = a.unit_code then a.num else a.split_num end num,a.crte_time as crteTime,a.crte_name as crteName, a.item_code as itemCode, a.item_id as itemId,"门诊退药" as retrunType, (select name from base_dept where hosp_code = b.hosp_code and id = b.phar_id) as pharName, (select name from base_dept where hosp_code = b.hosp_code and id = op.dept_id) as deptName, '门诊病人' as statusCode, (concat(v.name, '/',(ifnull((select e.name from sys_code_detail e where e.value=v.gender_code and e.c_code='XB'),'') ), '/',v.age, (ifnull((select name from sys_code_detail where c_code='NLDW' and value=v.age_unit_code and hosp_code=v.hosp_code),'')))) patientInfo from phar_out_distribute_all_detail a left join phar_out_distribute b on b.id = a.distribute_id and b.hosp_code = a.hosp_code left join outpt_visit v on v.id = b.visit_id and v.hosp_code = b.hosp_code left join outpt_prescribe op on op.id = a.op_id left join base_drug bd on a.item_id = bd.id and a.hosp_code = bd.hosp_code and a.item_code = "1" left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = "2" where ifnull(a.num,0) < 0 and a.hosp_code = #{hospCode}
and a.crte_time >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
and a.crte_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
and b.phar_id = #{pharId}
and 1=-1
and op.dept_id = #{deptToldId}
and ( a.item_name like concat('%', #{keyword}, '%') or bd.usual_pym like concat('%', #{keyword}, '%') or bd.usual_wbm like concat('%', #{keyword}, '%') or c.pym like concat('%', #{keyword}, '%') or c.wbm like concat('%', #{keyword}, '%') or b.crte_name like concat('%', #{keyword}, '%') or v.name like concat('%', #{keyword}, '%'))
) tab order by tab.crteName desc

select
sum(a.reality_price) as realityPrice,            /**queryInPatient应结费用**/
sum(a.actual_price) as actualPrice,             /**实收金额**/
sum(a.self_price) as selfPrice,                 /**个人支付**/
sum(a.ap_offset_price) as apOffsetPrice,          /**预交金抵充**/
sum(a.settle_take_price) as settleTakePrice,     /**结算补收**/
sum(a.settle_back_price) as settleBackPrice,     /**结算退款**/
max(a.crte_name) as crteName,
sum(a.mi_price) as miPrice,
(select sum(c.price) from inpt_advance_pay c where c.hosp_code = a.hosp_code and c.status_code = '0' and c.is_settle = '0'
and c.crte_id = a.crte_id

    and DATE_FORMAT(c.crte_time,'%Y-%m-%d %H:%i:%s') >= #{startDate}


    and DATE_FORMAT(c.crte_time,'%Y-%m-%d %H:%i:%s') <= #{endDate}

group by c.crte_id
) settledAdvancePay,
sum(a.ap_total_price) as unSettledAdvancePay

from inpt_settle a
where a.hosp_code = #{hospCode} and a.is_settle = '1'
/*开始日期*/

    and DATE_FORMAT(a.settle_time,'%Y-%m-%d %H:%i:%s') >= #{startDate}

/*结束日期*/

    and DATE_FORMAT(a.settle_time,'%Y-%m-%d %H:%i:%s') <= #{endDate}

group by a.crte_id

select sum(a.total_price) as realityPrice, /**queryInPatient应结费用**/ sum(a.reality_total_price) as actualPrice, /**实收金额**/ 0 as selfPrice, /**个人支付**/ sum(a.yjcd_total_price) as apTotalPrice, /**预交金抵充**/ 0 as settleTakePrice, /**结算补收**/ sum(a.back_total_price) as settleBackPrice, /**结算退款**/ max(a.crte_name) as crteName, sum(a.insure_total_price) as miPrice, sum(a.yjss_total_price) as settledAdvancePay, sum(a.yjjs_total_price) as unSettledAdvancePay
from outin_daily a where a.hosp_code = #{hospCode} and a.type_code = '2' /*开始日期*/
and DATE_FORMAT(a.end_time,'%Y-%m-%d %H:%i:%s') >=#{startDate}
/*结束日期*/
and DATE_FORMAT(a.end_time,'%Y-%m-%d %H:%i:%s') <= #{endDate}
group by a.crte_id

select * from
(
select a.crte_name as crteName,'住院结账补交款' as inCometype,a.visit_id as visitId,p.price as money,a.settle_time as time,
a.red_id as redId,a.status_code as statusCode, p.pay_code as payCode,a.settle_no as settleNo,
(select code from sys_user b where a.crte_id = b.id and b.hosp_code = #{hospCode}) as account,
(select name from inpt_visit o where o.id = a.visit_id and o.hosp_code = #{hospCode}) as patientName,
(select daily_no from outin_daily d where a.daily_settle_id = d.id and d.hosp_code = #{hospCode}) as dailyNo
from inpt_settle a
join inpt_pay p on p.settle_id = a.id and p.hosp_code = a.hosp_code
where a.hosp_code = #{hospCode}
/*开始日期*/

    and a.settle_time >= #{startDate}

/*结束日期*/

    and a.settle_time <= #{endDate}

/*开始日期*/

    and (a.crte_name like concat('%',#{keyword},'%'))

union all
select a.crte_name as crteName,'住院结账补交款' as inCometype,a.visit_id as visitId,a.trunc_price as money,a.settle_time as time,
a.red_id as redId,a.status_code as statusCode, '尾款' as payCode,a.settle_no as settleNo,
(select code from sys_user b where a.crte_id = b.id and b.hosp_code = #{hospCode}) as account,
(select name from inpt_visit o where o.id = a.visit_id and o.hosp_code = #{hospCode}) as patientName,
(select daily_no from outin_daily d where a.daily_settle_id = d.id and d.hosp_code = #{hospCode}) as dailyNo
from inpt_settle a
