select t1.bfcId, t1.bfcName from ( SELECT a.bfc_id as bfcId, b.name as bfcName FROM outpt_cost a , base_finance_classify b, outpt_settle os where a.bfc_id = b.id and a.settle_code = '2' /*已结算*/ and os.is_settle = '1' and a.bfc_id is not null and a.hosp_code = #{hospCode} and a.settle_id = os.id and a.hosp_code = os.hosp_code /*开始日期*/
and date(os.settle_time) >= date(#{startDate})
/*结束日期*/
and date(os.settle_time) <= date(#{endDate})
UNION ALL SELECT b.bfc_id as bfcId, c.name as bfcName FROM outpt_register_settle a LEFT JOIN outpt_register_detail b on a.register_id = b.register_id and a.hosp_code = b.hosp_code LEFT JOIN base_finance_classify c on b.bfc_id = c.id and a.hosp_code = b.hosp_code where a.status_code = '0' and b.status_code = '0' and b.bfc_id is not null and a.hosp_code = #{hospCode} /*开始日期*/
and date(a.settle_time) >= date(#{startDate})
/*结束日期*/
and date(a.settle_time) <= date(#{endDate})
) t1 group by t1.bfcId, t1.bfcName

