select A.* from ( select max(a.item_name) as itemName, case max(a.item_code) when '1' then max(bd.last_buy_price) when '2' then max(bm.last_buy_price) end lastBuyPrice, sum(a.num) as num, sum(a.split_num) as splitNum, sum(a.total_price) as totalPrice, max(a.price) as price, max(a.split_price) as splitPrice, case max(a.item_code) when '1' then max(bd.spec) when '2' then max(bm.spec) end spec, case max(a.item_code) when '1' then max(bd.code) when '2' then max(bm.code) end code, case max(a.item_code) when '1' then max(bd.prep_code) when '2' then '' end prepCode, max(a.unit_code) as unitCode, max(a.split_unit_code) as splitUnitCode, (select name from base_dept where id = b.dept_id) as deptName, (select name from base_dept where id = b.phar_id) as pharName from phar_in_distribute_all_detail a left join phar_in_distribute b on a.distribute_id = b.id left join base_drug bd on a.item_id = bd.id and a.hosp_code = bd.hosp_code and a.item_code = '1' left join base_material bm on a.item_id = bm.id and a.hosp_code = bm.hosp_code and a.item_code = '2' where a.hosp_code = #{hospCode} /*开始日期*/
and a.crte_time >= STR_TO_DATE(#{startTime},'%Y-%m-%d %H:%i:%s')
/*结束日期*/
and a.crte_time <= STR_TO_DATE(#{endTime},'%Y-%m-%d %H:%i:%s')
/*项目名称*/
and (a.item_name like concat('%',#{keyword},'%') or bd.usual_name like concat('%',#{keyword},'%') or bd.good_pym like concat('%',#{keyword},'%') or bd.good_wbm like concat('%',#{keyword},'%') or bd.usual_pym like concat('%',#{keyword},'%') or bd.usual_wbm like concat('%',#{keyword},'%') )
/*开嘱科室*/
and b.dept_id = #{deptId}
/*剂型*/
and bd.prep_code = #{prepCode}
/*发药药房*/
and b.phar_id = #{pharId}
group by b.dept_id, a.item_id ) A where A.splitNum != 0

select
bs.name as supplierName, /*供应商名*/
count(distinct sid.order_no) as mainNum, /*单据总数*/
count(0) as detailNum, /*明细总数*/
count(distinct sid.item_id) as itemNum, /*品种总数*/
count(distinct sid.batch_no) as batchNoNum, /*批次总数*/
max(sid.crte_time) as lastTime, /*最近入库时间*/
(sum( sid.num * sid.buy_price) ) / count( DISTINCT sid.order_no ) AS avgMainBuyPrice,/*单据平均购进价*/
(sum( sid.buy_price * sid.num) ) / count( DISTINCT sid.item_id ) AS avgItemBuyPrice,
(sum( sid.buy_price * sid.num ) ) AS buyPriceAll,/*购进总金额*/
(sum( sid.sell_price * sid.num ) ) AS sellPriceAll,/*零售总金额*/
( sum( sid.sell_price * sid.num )- sum( sid.buy_price * sid.num )) AS priceSpread /*购零差*/
from stro_invoicing sid
${sql}

    and sid.crte_time >=STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')

/*出院日期*/

    and sid.crte_time <=STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')

/*药房*/

    and bs.id in

        #{id}


/*科室*/

    and bs.id in

        #{id}


/*供应商*/

    and bs.id in

        #{id}


and
sid.biz_id in

    #{item}

group by supplierName

