select SUM(buyPrice)as buyPriceSum,SUM(sellPrice)as sellPriceSum,SUM(num)as numSum ,SUM(buyPriceAll)as buyPriceAllSum,SUM(sellPriceAll)as sellPriceAllSum,SUM(priceSpread)as priceSpreadSum from( select bs.name as supplierName, max(sid.item_name) as itemName, max(sid.unit_code) as unitCode, sid.batch_No as batchNo, min(expiry_date) as expiryDate, max(sid.buy_price) as buyPrice, max(sid.sell_price) as sellPrice, sum(sid.num) as num, (sum(sid.num) * avg(sid.buy_price)) as buyPriceAll, (sum(sid.num) * avg(sid.sell_price)) as sellPriceAll, ((avg(sid.sell_price) - avg(sid.buy_price))* sum(sid.num)) as priceSpread, case max(sid.item_code) when '1' then max(b.spec) when '2' then max(c.spec) end spec, case max(sid.item_code) when '1' then max(b.code) when '2' then max(c.code) end code, case max(sid.item_code) when '1' then max(b.big_type_code) when '2' then '3' end typeCode, case max(sid.item_code) when '1' then (select p.name from base_product p where p.hosp_code = #{hospCode} and p.code = max(b.prod_code)) when '2' then (select p.name from base_product p where p.hosp_code = #{hospCode} and p.code = max(c.prod_code)) end prodName from stro_invoicing sid left join base_drug b on sid.item_id = b.id and sid.hosp_code = b.hosp_code and sid.item_code = '1' left join base_material c on sid.item_id = c.id and sid.hosp_code = c.hosp_code and sid.item_code = '2' ${sql}
and date(sid.crte_time) >=date(#{startDate})
/*出院日期*/
and date(sid.crte_time) <= date(#{endDate})
/*药房*/
and bs.id in
#{id}
/*科室*/
and bs.id in
#{id}
/*供应商*/
and bs.id in
#{id}
and sid.biz_id in
#{item}
group by supplierName,
(case sid.item_code when '1' then b.big_type_code when '2' then '3' end),
sid.item_id,sid.batch_No order by supplierName )tableSum

