and b.name like concat('%',#{name},'%')
and a.status_code = #{statusCode}
union all
SELECT b.register_no, settle.id AS settle_no, b.name, b.gender_code , a.bfc_id , a.item_code , a.item_name, a.spec, a.unit_code AS unit_name, a.price, a.num as total_num, a.total_price, a.preferential_price, a.reality_price, '是' as is_dist, settle.crte_name AS get_cost_name, DATE_FORMAT( settle.crte_time, '%Y-%m-%d %H:%i:%S' ) AS get_cost_time, b.doctor_name AS doctor_name, b.dept_id AS dept_id, b.dept_id AS exec_dept_id, NULL as disease_name, NULL as prodName FROM outpt_register_settle settle join outpt_register_detail a on settle.register_id = a.register_id and a.hosp_code = settle.hosp_code left join outpt_visit b on a.visit_id = b.id and a.hosp_code = b.hosp_code where a.hosp_code = #{hospCode}
and DATE_FORMAT(settle.settle_time,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(settle.settle_time,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d %H:%i:%S') >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%S')
and DATE_FORMAT(a.crte_time,'%Y-%m-%d %H:%i:%S') <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%S')
and b.name like concat('%',#{name},'%')
and a.status_code = #{statusCode}
) aa order by get_cost_time,bfc_id desc

select * from ( select b.in_no, settle.settle_no, b.name, b.gender_code , a.bfc_id, a.item_code, a.item_name, a.spec, a.num_unit_code as unit_name, a.price, if (a.reality_price < 0, -1*a.total_num, a.total_num) as num, a.total_price, a.preferential_price, a.reality_price, settle.crte_name as get_cost_name, DATE_FORMAT(settle.crte_time,'%Y-%m-%d %H:%i:%S') as get_cost_time, a.doctor_name, a.dept_id, a.exec_dept_id, (select name from base_disease where id = (select SUBSTRING_INDEX(diagnose_ids, ',', 1) from outpt_prescribe where id = b.in_disease_id)) as disease_name, ifnull((select name from base_product e join base_drug f on f.prod_code=e.code where e.hosp_code=a.hosp_code and f.hosp_code=a.hosp_code and f.id=a.item_id),'') prodName from inpt_settle settle join inpt_cost a on settle.id = a.settle_id and a.hosp_code = settle.hosp_code join inpt_visit b on a.hosp_code = b.hosp_code and settle.visit_id = b.id where settle.is_settle = '1' and a.hosp_code = #{hospCode}
and settle.settle_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%S')
and settle.settle_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%S')
and a.cost_time >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%S')
and a.cost_time <= DATE_FORMAT(#{endTime},'%Y-%m-%d %H:%i:%S')
and b.name like concat('%',#{name},'%')
and a.status_code = #{statusCode}
) aa order by get_cost_time,bfc_id desc

