mapper.select:
select A.* from ( select max(a.item_name) as itemName, sum(a.num) as num, sum(a.split_num) as splitNum, sum(a.total_price) as totalPrice, max(a.price) as price, max(a.split_price) as splitPrice, case max(a.item_code) when '1' then max(bd.spec) when '2' then max(bm.spec) end spec, case max(a.item_code) when '1' then max(bd.code) when '2' then max(bm.code) end code, max(a.unit_code) as unitCode, max(a.split_unit_code) as splitUnitCode, max(date(b.crte_time)) as creTime, (select name from base_dept where id = b.dept_id) as deptName from phar_out_distribute_all_detail a left join phar_out_distribute b on a.distribute_id = b.id left join base_drug bd on a.item_id = bd.id and a.hosp_code = bd.hosp_code and a.item_code = '1' left join base_material bm on a.item_id = bm.id and a.hosp_code = bm.hosp_code and a.item_code = '2' where a.hosp_code = #{hospCode}
and b.dept_id = #{deptId}
/*开始日期*/
and a.crte_time >= #{startTime}
/*结束日期*/
and a.crte_time <= #{endTime}
and ( a.item_name like concat('%',#{keyword},'%') or bd.usual_name like concat('%',#{keyword},'%') or bd.good_pym like concat('%',#{keyword},'%') or bd.good_wbm like concat('%',#{keyword},'%') or bd.usual_pym like concat('%',#{keyword},'%') or bd.usual_wbm like concat('%',#{keyword},'%') )
group by b.dept_id, a.item_id ) A where A.splitNum != 0

SELECT a.id, a.hosp_code, a.profile_id, a.in_profile, a.in_no, a.name, a.gender_code, a.age, a.age_unit_code, a.birthday, a.nation_code, a.cert_code, a.cert_no, a.marry_code, a.phone, a.address, a.preferential_type_id, a.patient_code, a.receive_hosp_name, a.bed_id, a.bed_name, a.nursing_code, a.diet_type, a.Illness_code, a.status_code, a.in_ward_id, a.in_dept_id, a.in_dept_name, a.in_time, a.zz_doctor_id, a.zz_doctor_name, a.jz_doctor_id, a.jz_doctor_name, a.zg_doctor_id, a.zg_doctor_name, a.in_remark, a.in_mode_code, a.in_disease_id, a.in_disease_name, a.in_disease_icd10, a.in_situation_code, a.outpt_visit_no, a.outpt_doctor_id, a.outpt_doctor_name, a.outpt_disease_id, a.outpt_disease_name, a.outpt_disease_icd10, a.out_ward_id, a.out_dept_id, a.out_dept_name, a.out_time, a.out_disease_id, a.out_disease_name, a.out_disease_icd10, a.out_oper_id, a.out_oper_name, a.out_oper_time, a.out_situation_code, a.out_remark, a.out_mode_code, a.is_archive, a.archive_time, a.archive_id, a.archive_name, a.insure_code, a.insure_org_code, a.insure_no, a.insure_biz_code, a.insure_treat_code, a.insure_patient_id, a.insure_remark, a.total_advance, (case s.id when null then a.total_cost else s.reality_price end) total_cost, (case when s.settle_take_price != '0' then s.settle_take_price when s.settle_back_price != '0' then s.settle_back_price else '0' end) as bu_tui_fei, a.total_balance, a.crte_id, a.crte_name, a.crte_time, s.settle_time as settleTime, s.self_price, s.mi_price, (select sum(total_price) from inpt_cost c where c.hosp_code = #{hospCode} and c.item_code = '1' and a.id = c.visit_id) as drugCost, (select sum(total_price) from inpt_cost c where c.hosp_code = #{hospCode} and c.item_code = '1' and a.id = c.visit_id)/a.total_cost as drugCostRate, (select name from base_dept b where b.id = a.in_ward_id and b.hosp_code = a.hosp_code) as wardName, bpf.source_tj_code, bpf.total_in from inpt_visit a left join inpt_settle s on a.id=s.visit_id and a.hosp_code=s.hosp_code and s.is_settle='1' and s.status_code = '0' left join base_profile_file bpf on a.profile_id = bpf.id where a.hosp_code = #{hospCode}
and a.status_code = #{statusCode}
and (a.name like concat('%',#{keyword},'%') or a.in_no like concat('%',#{keyword},'%') or a.in_profile like concat('%',#{keyword},'%') or a.in_disease_name like concat('%',#{keyword},'%') or a.zz_doctor_name like concat('%',#{keyword},'%') or a.jz_doctor_name like concat('%',#{keyword},'%') or a.zg_doctor_name like concat('%',#{keyword},'%') or a.outpt_doctor_name like concat('%',#{keyword},'%') )
and a.in_dept_id = #{inDeptId}
and a.in_ward_id = #{inWardId}
and a.in_disease_name like concat('%',#{inDiseaseName},'%')
/*当天出院*/
and DATE_FORMAT(a.out_time,'%Y-%m-%d') = DATE_FORMAT(#{startDate},'%Y-%m-%d') and (a.status_code ='5' or a.status_code ='7')
/*当天在院*/
and DATE_FORMAT(a.in_time,'%Y-%m-%d') <= DATE_FORMAT(#{startDate},'%Y-%m-%d') and (a.out_time is null or DATE_FORMAT(a.out_time,'%Y-%m-%d') >= DATE_FORMAT(#{startDate},'%Y-%m-%d')) and a.status_code ='2'

select DATE_FORMAT(c.settle_time,'%Y-%m-%d') settle_time, a.id, a.hosp_code, a.profile_id, a.out_profile, a.name, a.gender_code, a.age, a.age_unit_code, a.birthday, a.nation_code, a.cert_code, a.cert_no, a.marry_code, a.phone, a.patient_code, c.reality_price totalCost, b.* from outpt_visit a left join outpt_settle c on a.id = c.visit_id and a.hosp_code = c.hosp_code and c.status_code = '0' left join ( select ${sql} d.id visistID from outpt_visit d join outpt_cost a on a.visit_id = d.id and a.hosp_code = d.hosp_code left join outpt_settle c on a.hosp_code = c.hosp_code and a.visit_id = c.visit_id and c.id = a.settle_id where a.hosp_code = #{hospCode}
and d.patient_code =#{patientCode}
and c.settle_time >= #{settleStartTime}
and c.settle_time <= #{settleEndTime}
and d.name like concat('%', #{keyword}, '%')
and c.is_settle =#{isSettle}
group by d.id ) b on c.visit_id = b.visistID where a.hosp_code = #{hospCode}
and a.patient_code =#{patientCode}
and c.settle_time >= #{settleStartTime}
and c.settle_time <= #{settleEndTime}
and a.name like concat('%', #{keyword}, '%')
and c.is_settle =#{isSettle}
order by c.settle_time desc

