 left join inpt_settle s on a.id=s.visit_id and a.hosp_code=s.hosp_code and s.is_settle='1' and s.status_code = '0' and (s.baby_id is null or s.baby_id ='') left join base_profile_file bpf on a.profile_id = bpf.id where a.hosp_code = #{hospCode}
and a.status_code = #{statusCode}
and (a.name like concat('%',#{keyword},'%') or a.in_no like concat('%',#{keyword},'%') or a.in_profile like concat('%',#{keyword},'%') or a.in_disease_name like concat('%',#{keyword},'%') or a.zz_doctor_name like concat('%',#{keyword},'%') or a.jz_doctor_name like concat('%',#{keyword},'%') or a.zg_doctor_name like concat('%',#{keyword},'%') or a.outpt_doctor_name like concat('%',#{keyword},'%') )
and a.in_dept_id = #{inDeptId}
and a.in_ward_id = #{inWardId}
and a.in_disease_name like concat('%',#{inDiseaseName},'%')
/*开始日期*/
and DATE_FORMAT(a.in_time,'%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i:%s')
/*结束日期*/
and DATE_FORMAT(a.in_time,'%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{endDate},'%Y-%m-%d %H:%i:%s')
/*开始日期*/
and DATE_FORMAT(a.out_time,'%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i:%s')
/*结束日期*/
