and a.zg_doctor_id = #{zgDoctorId}
and a.patient_code = #{patientCode} ) ORDER BY in_time desc ,px asc

select a.bfc_id,c.name as bfcName from outpt_cost a left join outpt_settle b on a.settle_id = b.id and a.hosp_code = b.hosp_code and b.status_code = '0' join base_finance_classify c on a.bfc_id = c.id and a.hosp_code = c.hosp_code join outpt_visit d on a.visit_id = d.id and a.hosp_code = d.hosp_code where a.hosp_code = #{hospCode} and b.is_settle = '1'
