and d.doctor_name like concat('%', #{outptDoctorName}, '%')
group by a.bfc_id,c.name

select ifnull(B.reality_price,0) as realityPrice, ifnull(B.total_price,0) as totalPrice, ifnull(B.bfc_id,'0') as bfcId, ifnull((select name from base_finance_classify bfc where bfc.id = B.bfc_id and bfc.hosp_code = #{hospCode} ),'其他') as bfcName, ifnull(B.doctor_id,'0') as doctorId, ifnull(B.doctor_name,t.doctor_name) as doctorName, ifnull(B.visit_id,'0') as visitId, t.name as visitName, ifnull(B.id,'0') as settleId, t.register_no registerNo, t.gender_code genderCode, t.age, t.age_unit_code, t.cert_no certNo, t.phone, t.patient_code patientCode, t.dept_name deptName, A.settle_time settleTime from outpt_settle A inner join outpt_cost B on A.id = B.settle_id and A.hosp_code = B.hosp_code inner join outpt_visit t on t.hosp_code = b.hosp_code and t.id = B.visit_id where A.hosp_code = #{hospCode} and A.is_settle = '1' and b.settle_code = '2'
and date(A.settle_time) >=date(#{settleStartTime})
/*结束日期*/
and date(A.settle_time) <= date(#{settleEndTime})
and t.patient_code =#{patientCode}
and (t.name like concat('%', #{keyword}, '%') or t.phone like concat('%', #{keyword}, '%') or t.cert_no like concat('%', #{keyword}, '%'))
and t.dept_id =#{deptId}
and (B.doctor_name like concat('%', #{outptDoctorName}, '%') or t.doctor_name like concat('%', #{outptDoctorName}, '%'))
order by A.settle_time desc

SELECT b.register_no AS registerNo, b. NAME AS name, b.gender_code AS genderCode, b.age AS age, b.cert_no AS certNo, b.phone AS phone, b.register_time AS registerTime, b.dept_name AS deptName, a.reality_price AS registerPrice, settle.settle_time AS settleTime FROM outpt_register_settle settle JOIN outpt_register b ON settle.register_id = b.id AND settle.hosp_code = b.hosp_code JOIN outpt_register_detail a ON settle.register_id = a.register_id AND settle.hosp_code = a.hosp_code AND settle.status_code = a.status_code WHERE a.hosp_code = #{hospCode} AND b.is_cancel = '0'
and settle.crte_time >= #{settleStartTime}
and settle.crte_time <= #{settleEndTime}
and b.dept_id =#{deptId}
and (b.name like concat('%', #{keyword}, '%') or b.phone like concat('%', #{keyword}, '%') or b.cert_no like concat('%', #{keyword}, '%'))

