a.bfc_id,c.name as bfcName
from inpt_cost a
left join inpt_settle b on a.settle_id = b.id and a.hosp_code = b.hosp_code and b.status_code = '0'
join base_finance_classify c on a.bfc_id = c.id and a.hosp_code = c.hosp_code
join inpt_visit d on a.visit_id = d.id and a.hosp_code = d.hosp_code
where a.hosp_code = #{hospCode} and d.status_code = '7' and b.id is not null and b.is_settle = '1' and b.status_code = '0'

    and b.settle_time >= #{settleStartTime}


    and b.settle_time <= #{settleEndTime}


    and d.in_dept_id = #{inDeptId}


    and d.patient_code = #{patientCode}


    and d.in_time >= #{startTime}


    and d.in_time <= #{endTime}



    and (
    d.outpt_doctor_name like concat('%', #{doctorName}, '%')
    or d.zz_doctor_name like concat('%', #{doctorName}, '%')
    or d.jz_doctor_name like concat('%', #{doctorName}, '%')
    or d.zg_doctor_name like concat('%', #{doctorName}, '%')
    )


    and (d.name like concat('%', #{keyword}, '%')
    or d.phone like concat('%', #{keyword}, '%')
    or d.cert_no like concat('%', #{keyword}, '%')
    )

group by a.bfc_id,c.name

select a.id, a.hosp_code, a.profile_id, a.in_profile, a.in_no as inNo, case when ib.id is null then a.name else ib.name end as name, case when ib.id is null then a.gender_code else ib.gender_code end as genderCode, case when ib.id is null then a.age else 0 end as age, a.age_unit_code, a.birthday, a.nation_code, a.cert_code, a.cert_no as certNo, a.marry_code, a.phone, a.address, a.preferential_type_id, case when ib.id is null then a.patient_code else '' end as patientCode, a.receive_hosp_name, a.bed_id, a.bed_name, a.nursing_code, a.diet_type, a.Illness_code, a.status_code, a.in_ward_id, a.in_dept_id, a.in_dept_name as inDeptName, case when ib.id is null then a.in_time else ib.birth_time end as inTime, a.zz_doctor_id, a.zz_doctor_name, a.jz_doctor_id, a.jz_doctor_name, a.zg_doctor_id, a.zg_doctor_name as zgDoctorName, a.in_remark, a.in_mode_code, a.in_disease_id, case when ib.id is null then a.in_disease_name else '' end as inDiseaseName, a.in_disease_icd10, a.in_situation_code, a.outpt_visit_no, a.outpt_doctor_id, a.outpt_doctor_name, a.outpt_disease_id, a.outpt_disease_name, a.outpt_disease_icd10, a.out_ward_id, a.out_dept_id, a.out_dept_name as outDeptName, a.out_time as outTime, a.out_disease_id, a.out_disease_name, a.out_disease_icd10, a.out_oper_id, a.out_oper_name, a.out_oper_time, a.out_situation_code, a.out_remark, a.out_mode_code, a.is_archive, a.archive_time, a.archive_id, a.archive_name, a.insure_code, a.insure_org_code, a.insure_no, a.insure_biz_code, a.insure_treat_code, a.insure_patient_id, a.insure_remark, case when ib.id is null then a.total_advance else 0 end as totalAdvance, a.total_cost, a.total_balance, a.crte_id, a.crte_name, a.crte_time, c.settle_time as settleTime, c.self_price as selfPirce, c.reality_price totalCost, c.settle_take_price as settleTakePrice, c.settle_back_price as settleBackPrice, (c.mi_price-ifnull(c.acct_pay,0.00)) as miPrice, c.acct_pay as acctPay, case when ib.id is null then (case when DATEDIFF(ifnull(a.out_time, now()),a.in_time)=0 then 1 else DATEDIFF(ifnull(a.out_time, now()),a.in_time) end) else (case when DATEDIFF(ifnull(a.out_time, now()),ib.birth_time)=0 then 1 else DATEDIFF(ifnull(a.out_time, now()),ib.birth_time) end) end as inDay, b.* from inpt_visit a left join ( select ${sqlStr} d.id visistID, c.baby_id from inpt_visit d join inpt_cost a on a.visit_id = d.id and a.hosp_code = d.hosp_code left join inpt_settle c on a.hosp_code = c.hosp_code and a.visit_id = c.visit_id and c.id = a.settle_id where a.hosp_code = #{hospCode} and d.status_code = '7' and c.id is not null and c.is_settle = '1' and c.status_code = '0'
and c.settle_time >= #{settleStartTime}
and c.settle_time <= #{settleEndTime}
and d.in_time >= #{startTime}
and d.in_time <= #{endTime}
and ( d.outpt_doctor_name like concat('%', #{doctorName}, '%') or d.zz_doctor_name like concat('%', #{doctorName}, '%') or d.jz_doctor_name like concat('%', #{doctorName}, '%') or d.zg_doctor_name like concat('%', #{doctorName}, '%') )
and d.in_dept_id = #{inDeptId}
and d.patient_code = #{patientCode}
and (a.name like concat('%', #{keyword}, '%') or a.phone like concat('%', #{keyword}, '%') or a.cert_no like concat('%', #{keyword}, '%') )
group by c.baby_id, d.id ) b on a.id = b.visistID left join inpt_settle c on a.id = c.visit_id and a.hosp_code = c.hosp_code and c.status_code = '0' and (c.baby_id = b.baby_id || (b.baby_id is null and c.baby_id is null) ) left join inpt_baby ib on ib.hosp_code = a.hosp_code and ib.visit_id = a.id and ib.id = c.baby_id where a.hosp_code = #{hospCode} and c.id is not null and c.is_settle = '1' and c.status_code = '0'
and c.settle_time >= #{settleStartTime}
and c.settle_time <= #{settleEndTime}
and ( a.outpt_doctor_name like concat('%', #{doctorName}, '%') or a.zz_doctor_name like concat('%', #{doctorName}, '%') or a.jz_doctor_name like concat('%', #{doctorName}, '%') or a.zg_doctor_name like concat('%', #{doctorName}, '%') )
and a.in_dept_id = #{inDeptId}
and a.patient_code = #{patientCode}
and a.in_time >= #{startTime}
and a.in_time <= #{endTime}
and (a.name like concat('%', #{keyword}, '%') or a.phone like concat('%', #{keyword}, '%') or a.cert_no like concat('%', #{keyword}, '%') )
order by a.out_time desc

select b.in_no as inNo, b.name,b.gender_code as genderCode, b.age,a.doctor_id as doctorId, a.doctor_name as doctorName, a.dept_id as outptDeptId, a.dept_name as outptDeptName, b.in_dept_id as inDeptId, b.in_dept_name as inDeptName, b.in_time as inTime, b.bed_name as bedName, b.in_disease_name as inDiseaseName from outpt_visit a JOIN inpt_visit b on b.outpt_visit_no = a.visit_no and b.hosp_code =#{hospCode} where a.tran_in_code ='2' and a.hosp_code =#{hospCode}
and a.dept_id = #{deptId}
and b.in_time >= #{startTime}
and b.in_time <= #{endTime}
and b.name like concat('%', #{keyword}, '%')
and a.doctor_name like concat('%', #{outptDoctorName}, '%')
order by b.in_time desc

select COUNT(a.id) as totalNum,
       a.doctor_name as doctorName,
       a.dept_id,
       a.dept_name as outptDeptName
from outpt_visit a
JOIN inpt_visit b on b.outpt_visit_no = a.visit_no and b.hosp_code =#{hospCode}
where a.tran_in_code ='2' and a.hosp_code =#{hospCode}

    and a.dept_id = #{deptId}


    and b.in_time >= #{startTime}


    and b.in_time <= #{endTime}


    and a.doctor_name like concat('%', #{outptDoctorName}, '%')

GROUP BY a.dept_id,a.doctor_id

select x.supplierName as supplierName, max(bfc.name) as bfcName, count(distinct x.itemId) as itemNum, count(distinct x.batchNo) as batchNoNum, sum(x.buyPriceAll)/count(distinct x.itemId) as avgItemBuyPrice, sum(x.buyPriceAll) as buyPriceAll, sum(x.sellPriceAll) as sellPriceAll, x.typeCode, (sum(x.sellPriceAll) - sum(x.buyPriceAll)) as priceSpread from ( select bs.name as supplierName, sid.item_id as itemId, sid.batch_no as batchNo, sid.buy_price as buyPrice, (sid.buy_price * sid.num) as buyPriceAll, (sid.sell_price * sid.num) as sellPriceAll, c.bfc_code as bfcCode, c.type_code typeCode from stro_invoicing sid join base_material c on sid.item_id = c.id and sid.hosp_code = c.hosp_code and sid.item_code = '2' ${sql}
and sid.crte_time >=STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')
/*出院日期*/
and sid.crte_time <= STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')
/*药房*/
and bs.id in
#{id}
/*科室*/
and bs.id in
#{id}
/*供应商*/
and bs.id in
#{id}
and sid.biz_id in
#{item}
)x left join base_finance_classify bfc on x.bfcCode = bfc.code and bfc.hosp_code = #{hospCode} group by supplierName,x.typeCode order by supplierName

select
allResult.up_code as upCode,
outCurrentResult.reality_price as outCurrentRealityPrice,/*本月费用*/
outYearResult.reality_price as outYearRealityPrice,/*上年同期*/
concat(format((ifnull(outCurrentResult.reality_price,0) - outYearResult.reality_price)/outYearResult.reality_price*100,2),'%') as outSameCompare,/*同比增长率*/
outMonthResult.reality_price as outMonthRealityPrice, /*上月费用*/
concat(format((ifnull(outCurrentResult.reality_price,0) - outMonthResult.reality_price)/outMonthResult.reality_price*100,2),'%') as outLinkCompare /*环比*/
from
/*====门诊中 本月、上月、同期 所有的upCode=====*/
(
    select b.up_code from(
        /*======挂号的大类======begin*/
        SELECT
        a.bfc_id
        FROM
        outpt_register_settle settle
        JOIN outpt_register_detail a ON settle.hosp_code = a.hosp_code and settle.register_id = a.register_id
        AND settle.status_code = a.status_code
        WHERE
        settle.hosp_code = #{hospCode}
        AND (
        ( settle.settle_time >= #{startDate} AND settle.settle_time <= ifnull( #{endDate}, now()) )
        OR date_format( settle.settle_time, '%Y-%m' ) = date_format( DATE_SUB( #{startDate}, INTERVAL 1 MONTH ), '%Y-%m' )
        OR (
        settle.settle_time >= date_sub( #{startDate}, INTERVAL 1 YEAR )
        AND settle.settle_time <= date_sub( ifnull( #{endDate}, now()), INTERVAL 1 YEAR )
        )
        )
        /*======挂号的大类======end*/
        union all
        /*=====门诊大类===begin*/
        SELECT
        a.bfc_id
        FROM
        outpt_settle settle
        JOIN outpt_cost a ON settle.id = ifnull( a.settle_id, '' )
        AND settle.hosp_code = a.hosp_code
        AND a.settle_code = '2'
        WHERE
        settle.is_settle = '1'
        AND settle.hosp_code = #{hospCode}
        AND (
        ( settle.settle_time >= #{startDate} AND settle.settle_time <= ifnull( #{endDate}, now()) )
        OR date_format( settle.settle_time, '%Y-%m' ) = date_format( DATE_SUB( #{startDate}, INTERVAL 1 MONTH ), '%Y-%m' )
        OR (
        settle.settle_time >= date_sub( #{startDate}, INTERVAL 1 YEAR )
        AND settle.settle_time <= date_sub( ifnull( #{endDate}, now()), INTERVAL 1 YEAR )
        )
        )
        /*=====门诊大类===end*/
)a JOIN base_finance_classify b ON a.bfc_id = b.id AND  b.hosp_code = #{hospCode}
join base_finance_classify c on b.up_code = c.code AND  c.hosp_code = #{hospCode}
group by b.up_code, c.name
) allResult
/*====门诊中 本月、上月、同期 所有的upCode=====*/
left join
/*=======门诊本月费用begin========*/
(
    select a.up_code, sum(a.reality_price) reality_price
    from (

    where settle.hosp_code = #{hospCode} and settle.settle_time >= #{startDate}
    and settle.settle_time <= ifnull(#{endDate}, now())
    group by b.up_code
    union all

    where settle.is_settle = '1' and settle.hosp_code = #{hospCode} and settle.settle_time >=  #{startDate}
    and settle.settle_time <= ifnull(#{endDate}, now())
    group by b.up_code
    )a group by a.up_code
) outCurrentResult on allResult.up_code = outCurrentResult.up_code
left join
/*=======门诊本月费用end=========*/
/*=======门诊上月费用begin========*/
(
    select a.up_code, sum(a.reality_price) reality_price
    from (

    where settle.hosp_code = #{hospCode} and date_format(settle.settle_time,'%Y-%m') = date_format(DATE_SUB(#{startDate}, INTERVAL 1 MONTH),'%Y-%m')
    group by b.up_code
    union all

    where settle.is_settle = '1' and settle.hosp_code = #{hospCode} and date_format(settle.settle_time,'%Y-%m') = date_format(DATE_SUB(#{startDate}, INTERVAL 1 MONTH),'%Y-%m')
    group by b.up_code
    )a group by a.up_code
) outMonthResult on allResult.up_code = outMonthResult.up_code
/*=======门诊上月费用end========*/
left join
/*=======门诊上年同期费用begin========*/
(
    select a.up_code, sum(a.reality_price) reality_price
    from (

    where settle.hosp_code = #{hospCode} and settle.settle_time >= date_sub(#{startDate}, interval 1 year)
    and settle.settle_time <= date_sub(ifnull(#{endDate}, now()), interval 1 year)
    group by b.up_code
    union all

    where settle.is_settle = '1' and settle.hosp_code = #{hospCode} and settle.settle_time >= date_sub(#{startDate},
    interval 1 year)
    and settle.settle_time <= date_sub(ifnull(#{endDate}, now()), interval 1 year)
    group by b.up_code
    )a group by a.up_code
) outYearResult on allResult.up_code = outYearResult.up_code
/*=======门诊上年同期费用end========*/
where
allResult.up_code in

    #{item}

select
allInResult.up_code as upCode,
inCurrentResult.reality_price as inCurrentRealityPrice, /*当月住院收入*/
inYearResult.reality_price as inYearRealityPrice, /*上年同期收入*/
concat(format((ifnull(inCurrentResult.reality_price,0) - inYearResult.reality_price)/inYearResult.reality_price*100,2),'%') as inSameCompare,/*上年同比*/
inMonthResult.reality_price as inMonthRealityPrice, /*上月收入*/
concat(format((ifnull(inCurrentResult.reality_price,0) - inMonthResult.reality_price)/inMonthResult.reality_price*100,2),'%') as inLinkCompare
from
/*====住院中 本月、上月、同期 所有的upCode=====*/
(
