select a.bfc_id,c.name as bfcName from inpt_cost a left join inpt_settle b on a.settle_id = b.id and a.hosp_code = b.hosp_code and b.status_code = '0' join base_finance_classify c on a.bfc_id = c.id and a.hosp_code = c.hosp_code join inpt_visit d on a.visit_id = d.id and a.hosp_code = d.hosp_code where a.hosp_code =#{hospCode} and b.id is not null and b.is_settle = '1' and a.settle_code ='2'
and a.dept_id = #{deptId}
and d.patient_code = #{patientCode}
and b.settle_time >= #{settleStartTime}
and b.settle_time <= #{settleEndTime}
and ( d.outpt_doctor_name like concat('%', #{doctorName}, '%') or d.zz_doctor_name like concat('%', #{doctorName}, '%') or d.jz_doctor_name like concat('%', #{doctorName}, '%') or d.zg_doctor_name like concat('%', #{doctorName}, '%') )
and (d.name like concat('%', #{keyword}, '%') or d.phone like concat('%', #{keyword}, '%') or d.cert_no like concat('%', #{keyword}, '%') )
group by a.bfc_id,c.name

SELECT DISTINCT a.in_no AS inNo, a. NAME AS name, a.gender_code AS genderCode, a.age AS age, a.patient_code AS patientCode, ifnull((select name from base_dept where id = o.oper_dept_id and hosp_code =#{hospCode}),'') AS operDeptName, o.bed_name as bedName, o.ana_code as anaCode, o.oper_disease_name AS operDiseaseName, o.in_disease_name AS inDiseaseName, o.doctor_name AS doctorName, o.assistant_name1 AS assistantName1, o.assistant_name2 AS assistantName2, o.assistant_name3 AS assistantName3, o.oper_plan_time AS operPlanTime, o.oper_room_name AS operRoomName, o.xh_nurse_name AS xhNurseName, o.qx_nurse_name AS qxNurseName, o.ana_name1 AS anaName1, o.ana_name2 AS anaName2, o.ana_name3 AS anaName3, o.oper_end_time AS operEndTime, o.status_code as operStatusCode, a.id, a.hosp_code, a.profile_id, a.in_profile, a.age_unit_code as ageUnitCode, a.birthday, a.nation_code, a.cert_code, a.cert_no AS certNo, a.marry_code, a.phone, a.address, a.preferential_type_id, a.receive_hosp_name, a.bed_id, a.bed_name, a.nursing_code, a.diet_type, a.Illness_code, a.status_code, a.in_ward_id, a.in_dept_id, a.in_dept_name AS inDeptName, a.zz_doctor_id, a.zz_doctor_name, a.jz_doctor_id, a.jz_doctor_name, a.zg_doctor_id, a.zg_doctor_name AS zgDoctorName, a.in_remark, a.in_mode_code, a.in_disease_id, a.in_disease_icd10, a.in_situation_code, a.outpt_visit_no, a.outpt_doctor_id, a.outpt_doctor_name, a.outpt_disease_id, a.outpt_disease_name, a.outpt_disease_icd10, a.out_ward_id, a.out_dept_id, a.out_dept_name AS outDeptName, a.out_time AS outTime, a.out_disease_id, a.out_disease_name, a.out_disease_icd10, a.out_oper_id, a.out_oper_name, a.out_oper_time, a.out_situation_code, a.out_remark, a.out_mode_code, a.is_archive, a.archive_time, a.archive_id, a.archive_name, a.insure_code, a.insure_org_code, a.insure_no, a.insure_biz_code, a.insure_treat_code, a.insure_patient_id, a.insure_remark, a.crte_id, a.crte_name, a.crte_time, a.total_cost AS totalCost, o.total_price AS operTotalPrice, o.cost_time AS costTime, o.cost_name AS costName, b.* FROM inpt_visit a left join ( select ${sqlStr} d.id visistID from inpt_visit d join inpt_cost a on a.visit_id = d.id and a.hosp_code = d.hosp_code join inpt_settle c on a.hosp_code = c.hosp_code and a.visit_id = c.visit_id and c.id = a.settle_id join oper_info_record o ON d.hosp_code = o.hosp_code and o.visit_id = d.id where a.hosp_code = #{hospCode} and c.id is not null and c.is_settle = '1' and a.settle_code ='2' and o.is_cost ='1'
and c.settle_time >= #{settleStartTime}
and c.settle_time <= #{settleEndTime}
and ( d.outpt_doctor_name like concat('%', #{doctorName}, '%') or d.zz_doctor_name like concat('%', #{doctorName}, '%') or d.jz_doctor_name like concat('%', #{doctorName}, '%') or d.zg_doctor_name like concat('%', #{doctorName}, '%') )
and a.dept_id = #{deptId}
and d.patient_code = #{patientCode}
and (d.name like concat('%', #{keyword}, '%') or d.phone like concat('%', #{keyword}, '%') or d.cert_no like concat('%', #{keyword}, '%') )
group by o.id ) b on a.id = b.visistID join inpt_settle c on a.id = c.visit_id and a.hosp_code = c.hosp_code and c.status_code = '0' join oper_info_record o ON a.hosp_code = o.hosp_code and o.visit_id = a.id where a.hosp_code = #{hospCode} and c.id is not null and c.is_settle = '1' and o.is_cost ='1'
and c.settle_time >= #{settleStartTime}
and c.settle_time <= #{settleEndTime}
and ( a.outpt_doctor_name like concat('%', #{doctorName}, '%') or a.zz_doctor_name like concat('%', #{doctorName}, '%') or a.jz_doctor_name like concat('%', #{doctorName}, '%') or a.zg_doctor_name like concat('%', #{doctorName}, '%') )
and a.in_dept_id = #{inDeptId}
and a.patient_code = #{patientCode}
and o.cost_time >= #{startTime}
and o.cost_time <= #{endTime}
and (a.name like concat('%', #{keyword}, '%') or a.phone like concat('%', #{keyword}, '%') or a.cert_no like concat('%', #{keyword}, '%') )
order by a.out_time desc

select settleTime, sum(zfbr) as zfbr, sum(lxbr) as lxbr, sum(gsbr) as gsbr, sum(ysbr) as ysbr, sum(tcbr) as tcbr, sum(zfbr) + sum(lxbr) + sum(gsbr) + sum(ysbr) + sum(tcbr) as hjje from ( select DATE_FORMAT(s.settle_time, '%Y-%m-%d') settleTime, sum(case when s.patient_code in ('-1', '0') then c.reality_price end) zfbr, ifnull(sum(case when s.patient_code = '-2' then c.reality_price end),0) lxbr, ifnull(sum(case when s.patient_code = '-3' then c.reality_price end),0) gsbr, ifnull(sum(case when s.patient_code = '-4' then c.reality_price end), 0) ysbr, ifnull(sum(case when s.patient_code = '-1' then c.preferential_price end),0) as tcbr from outpt_cost c left join outpt_settle s on c.hosp_code= s.hosp_code and c.visit_id = s.visit_id and c.settle_id = s.id where c.item_code = #{itemType} and s.hosp_code = #{hospCode} and s.settle_time between #{startDate} and #{endDate} and s.patient_code <= '0' and s.is_settle = '1'
and c.dept_id in
#{id}
and c.phar_id in
#{id}
GROUP BY settleTime
union all
select DATE_FORMAT(ss.settle_time, '%Y-%m-%d') settleTime, ifnull(sum(ss.self_price),0) as zfbr, 0 as lxbr, 0 as gsbr, 0 as ysbr, ifnull(sum(ss.mi_price),0) as tcbr from outpt_settle ss where id in ( select distinct s.id from outpt_cost c left join outpt_settle s on c.hosp_code= s.hosp_code and c.visit_id = s.visit_id and c.settle_id = s.id where c.item_code = #{itemType} and s.hosp_code = #{hospCode} and s.settle_time between #{startDate} and #{endDate} and s.patient_code > '0' and s.is_settle = '1'
and c.dept_id in
#{id}
and c.phar_id in
#{id}
) GROUP BY settleTime ) a GROUP BY settleTime

SELECT bfc.CODE AS bfcCode, bfc.id AS bfcId, bfc.NAME as bfcName, ifnull(sum( ord.total_price ),0) AS totalPrice, ifnull(sum( ord.preferential_price ),0) AS preferentialPrice, ifnull(sum( ord.reality_price ),0) AS realityPrice FROM outpt_register_detail ord LEFT JOIN outpt_register_settle ors ON ors.register_id = ord.register_id and ors.hosp_code = ord.hosp_code left join base_finance_classify bfc on bfc.id = ord.bfc_id and bfc.hosp_code = ord.hosp_code where ord.hosp_code = #{hospCode}
and ors.settle_time >= #{startDate}
and ors.settle_time <= #{endDate}
GROUP BY ord.bfc_id

SELECT bfc.code AS bfcCode, bfc.id AS bfcId, bfc.NAME as bfcName, ifnull(sum( oc.total_price ),0) AS totalPrice, ifnull(sum( oc.preferential_price ),0) AS preferentialPrice, ifnull(sum( oc.reality_price ),0) AS realityPrice FROM outpt_cost oc LEFT JOIN base_finance_classify bfc ON oc.bfc_id = bfc.id and oc.hosp_code = bfc.hosp_code left join outpt_settle os on os.id = oc.settle_id and oc.hosp_code = os.hosp_code where os.is_settle= '1' and oc.settle_code = '2'
and os.settle_time >= #{startDate}
and os.settle_time <= #{endDate}
and os.patient_code != '0'
and os.patient_code = '0'
and oc.hosp_code = #{hospCode} GROUP BY oc.bfc_id

SELECT 'ZFY' AS bfcCode, '999' AS bfcId, '总费用' as bfcName, ''as preferentialPrice, ifnull(sum( A.totalPrice ),0) AS totalPrice, ifnull(sum( A.realityPrice),0) AS realityPrice, ifnull(sum( A.miPrice),0) AS miPrice, ifnull(sum( A.selfPrice),0) AS selfPrice FROM ( SELECT os.total_price AS totalPrice, os.reality_price AS realityPrice, os.mi_price AS miPrice, self_price as selfPrice FROM outpt_settle os WHERE os.is_settle = '1' and os.hosp_code = #{hospCode}
and os.settle_time >= #{startDate}
and os.settle_time <= #{endDate}
and os.patient_code != '0'
and os.patient_code = '0'
union all SELECT ors.total_price AS totalPrice, ors.reality_price AS realityPrice, 0 as miPrice, ors.reality_price as selfPrice FROM outpt_register_settle ors WHERE ors.hosp_code = #{hospCode}
and ors.settle_time >= #{startDate}
and ors.settle_time <= #{endDate}
and ors.id = ''
) A

select
t.in_dept_id as deptId,t.in_dept_name as deptName,t.id as visitId,
ifnull(ics.reality_price, ifnull(B.reality_price, 0)) as realityPrice,
ifnull(B.bfc_id,'0') as bfcId,
ifnull((select name from base_finance_classify bfc where bfc.id = B.bfc_id and bfc.hosp_code = #{hospCode}
),'其他')
as bfcName
    from
    inpt_settle a
    inner join inpt_cost b on a.id = b.settle_id and a.hosp_code = b.hosp_code and b.dept_id =#{deptId}
    inner join inpt_cost_settle ics on b.id=ics.cost_id
    inner join inpt_visit t on t.hosp_code = b.hosp_code and t.id = b.visit_id
    where
    a.hosp_code = #{hospCode}
    and a.is_settle = '1'
    and b.settle_code = '2'
    and a.status_code = '0'

and a.settle_time >= #{settleStartTime}


and a.settle_time <= #{settleEndTime}


and t.in_dept_id = #{inDeptId}

select
ifnull(ics.reality_price, ifnull(B.reality_price, 0)) as realityPrice,
ifnull(B.bfc_id,'0') as bfcId,
ifnull((select name from base_finance_classify bfc where bfc.id = B.bfc_id and bfc.hosp_code = #{hospCode}
),'其他')
as bfcName,
ifnull(t.patient_code, '-1') as patientCode,
ifnull((select name from sys_code_detail sc where sc.`value` = t.patient_code and sc.c_code='BRLX' and sc.hosp_code = #{hospCode}
),'其他') as patientType
from
inpt_settle A
inner join inpt_cost B on A.id = B.settle_id and A.hosp_code = B.hosp_code
inner join inpt_cost_settle ics on B.id = ics.cost_id
inner join inpt_visit t on t.hosp_code = b.hosp_code and t.id = B.visit_id
where
A.hosp_code = #{hospCode}
and A.is_settle = '1'
and B.settle_code = '2'
and A.status_code = '0'
/*开始日期*/

    and A.settle_time >=STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')

/*结束日期*/

    and A.settle_time <=STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')

select
ifnull(B.reality_price, 0) as realityPrice,
ifnull(B.bfc_id,'0') as bfcId,
ifnull((select name from base_finance_classify bfc where bfc.id = B.bfc_id and bfc.hosp_code = #{hospCode}
),'其他')
as bfcName,
ifnull(t.patient_code, '-1') as patientCode,
ifnull((select name from sys_code_detail sc where sc.`value` = t.patient_code and sc.c_code='BRLX' and sc.hosp_code = #{hospCode}
),'其他') as patientType
from
outpt_settle A
inner join outpt_cost B on A.id = B.settle_id and A.hosp_code = B.hosp_code
inner join outpt_visit t on t.hosp_code = b.hosp_code and t.id = B.visit_id
where
A.hosp_code = #{hospCode}
and A.is_settle = '1'
and B.settle_code = '2'
and A.status_code = '0'
/*开始日期*/

    and A.settle_time >=STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')

/*结束日期*/

    and A.settle_time <=STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')

select
ifnull(t.reality_price, 0) as realityPrice,
ifnull(t.bfc_id,'0') as bfcId,
ifnull((select name from base_finance_classify bfc where bfc.id = t.bfc_id and bfc.hosp_code = #{hospCode}
),'其他')
as bfcName,
ifnull(v.patient_code, '-1') as patientCode,
ifnull((select name from sys_code_detail sc where sc.`value` = v.patient_code and sc.c_code='BRLX' and sc.hosp_code = #{hospCode}
),'其他') as patientType
from
outpt_register_settle A
inner join outpt_register B on A.register_id = B.id and A.hosp_code = B.hosp_code and B.is_cancel = '0'
inner join outpt_register_detail t on t.hosp_code = b.hosp_code and t.register_id = B.id
inner join outpt_visit v on v.hosp_code = B.hosp_code and v.id = B.visit_id
where A.hosp_code = #{hospCode} and A.status_code = '0'
/*开始日期*/

    and A.settle_time >=STR_TO_DATE(#{startDate},'%Y-%m-%d %H:%i:%s')

/*结束日期*/

    and A.settle_time <=STR_TO_DATE(#{endDate},'%Y-%m-%d %H:%i:%s')
