mapper.select:
select
id, hosp_code, name, dept_id, visit_code, register_code, remark, pym, wbm, is_expert, is_valid, crte_id, crte_name, crte_time
from outpt_classify
where hosp_code = #{hospCode}
and is_valid = '1'

    and id = #{data.id}


    and dept_id = #{data.deptId}


    and dept_id in

        #{deptId}


order by visit_code desc

SELECT a.id, b.cy_id, (select name FROM outpt_classify oc where oc.hosp_code = b.hosp_code and oc.id = b.cy_id) as cy_name, (select visit_code FROM outpt_classify oc where oc.hosp_code = b.hosp_code and oc.id = b.cy_id) as visit_code, (select register_code FROM outpt_classify oc where oc.hosp_code = b.hosp_code and oc.id = b.cy_id) as register_code, (select count(1) FROM outpt_register oc where oc.hosp_code = a.hosp_code and oc.doctor_id = a.doctor_id) as registerCount, a.doctor_id, a.doctor_name, b.dept_id, (SELECT name from base_dept bd where bd.hosp_code = b.hosp_code and bd.id = b.dept_id) as dept_name, b.queue_date, LEFT(t1.start_time, 5) as start_time, LEFT(t1.end_time, 5) as end_time, t1.register_num, t1.used_register_num, t1.surplus_register_num FROM outpt_doctor_queue a LEFT JOIN outpt_classes_queue b on a.cq_id = b.id and a.hosp_code = b.hosp_code LEFT JOIN ( SELECT hosp_code, dq_id, start_time, end_time, MAX(register_num) as register_num, IFNULL(SUM(case when is_use = '1' then 1 when is_lock = '1' then 1 when ifnull(profile_id,'') !='' then 1 else 0 end),0) as used_register_num, (MAX(register_num) - IFNULL(SUM(case when is_use = '1' then 1 when is_lock = '1' then 1 when ifnull(profile_id,'') !='' then 1 else 0 end),0)) as surplus_register_num FROM outpt_doctor_register GROUP BY hosp_code, dq_id, start_time, end_time ) t1 on a.hosp_code = t1.hosp_code and a.id = t1.dq_id where a.hosp_code = #{hospCode} and a.is_valid = '1' and b.is_valid = '1'
and DATE_FORMAT(NOW(), '%H:%i:%s') < t1.end_time
and b.dept_id = #{data.deptId}
and a.doctor_id = #{data.doctorId}
and DATE_FORMAT(b.queue_date, '%Y-%m-%d') = DATE_FORMAT(#{data.queueDate}, '%Y-%m-%d')
ORDER BY t1.start_time

SELECT a.id, a.cy_id, a.item_code, a.item_id, a.num, a.use_code, c.name, c.unit_code as item_unit_code, c.spec, ROUND(c.price, 2) as item_price, ROUND(CONVERT((a.num * c.price), DECIMAL(14,4)), 2) as item_price_all, ROUND(CONVERT((a.num * c.price), DECIMAL(14,4)), 2) as item_preferential_all_price, c.bfc_code, (SELECT id from base_finance_classify bfc where bfc.hosp_code = c.hosp_code and bfc.code = c.bfc_code) as bfc_id, (SELECT name from base_finance_classify bfc where bfc.hosp_code = c.hosp_code and bfc.code = c.bfc_code) as bfc_name FROM outpt_classify_cost a LEFT JOIN outpt_classify b on a.hosp_code = b.hosp_code and a.cy_id = b.id JOIN base_item c on a.hosp_code = c.hosp_code and a.item_id = c.id where a.hosp_code = #{hospCode} and b.is_valid = '1'
and a.cy_id = #{data.cyId}
order by a.cy_id

SELECT distinct a.id as visit_id, a.hosp_code, a.profile_id, a.register_id, a.register_no, a.name, a.age, a.visit_no, a.cert_no, a.doctor_id, a.doctor_name, a.dept_id, a.dept_name, a.visit_time, a.is_visit, a.crte_id, a.crte_name, a.crte_time FROM outpt_visit a JOIN outpt_prescribe b on a.hosp_code = b.hosp_code and a.id = b.visit_id where a.hosp_code = #{hospCode} and a.is_visit = '1' and b.is_settle = '0' and b.is_cancel = '0' and b.is_submit = '1'
and a.profile_id = #{data.profileId}
and DATE_FORMAT(a.visit_time, '%Y-%m-%d') >= DATE_FORMAT(#{data.startDate}, '%Y-%m-%d')
and DATE_FORMAT(a.visit_time, '%Y-%m-%d') <= DATE_FORMAT(#{data.endDate}, '%Y-%m-%d')
and a.dept_id = #{data.deptId}
and a.doctor_id = #{data.doctorId}
order by a.visit_time desc

SELECT ROUND(SUM(a.reality_price), 2) as order_price, 0 as zfje, b.id, b.visit_id, b.order_no, b.type_code, b.prescribe_type_code, b.is_settle, b.doctor_id, b.doctor_name, b.dept_id, b.dept_name, b.diagnose_ids, (SELECT GROUP_CONCAT(bd.name) FROM base_disease bd where bd.hosp_code = b.hosp_code and FIND_IN_SET(bd.id,b.diagnose_ids) GROUP BY b.id) as diagnose_names, b.crte_time as prescribe_time FROM outpt_cost a LEFT JOIN outpt_prescribe b on a.hosp_code = b.hosp_code and a.op_id = b.id WHERE a.hosp_code = #{hospCode} and b.is_settle = '0' and b.is_submit = '1'
and a.visit_id = #{data.visitId}
GROUP BY b.id ORDER BY b.order_no asc

SELECT b.name, a.item_code, a.item_name, c.content, a.total_num as num, a.num_unit_code, a.bfc_id, bfc.code as bfc_code, bfc.name as bfc_name, a.dosage, a.dosage_unit_code, a.use_days, a.usage_code, a.spec, ROUND(a.price, 2) as price, ROUND(a.total_price, 2) as total_price, ROUND(a.reality_price, 2) as reality_price, a.doctor_name, ( SELECT NAME FROM base_dept bd WHERE bd.hosp_code = a.hosp_code AND bd.id = a.exec_dept_id ) AS exec_dept_name, ( SELECT NAME FROM base_dept bd WHERE bd.hosp_code = a.hosp_code AND bd.id = a.dept_id ) AS create_dept_name, d.settle_time FROM outpt_cost a LEFT JOIN outpt_visit b on a.hosp_code = b.hosp_code and a.visit_id = b.id LEFT JOIN outpt_prescribe_detail c on a.hosp_code = c.hosp_code and a.opd_id = c.id JOIN base_finance_classify bfc on a.hosp_code = bfc.hosp_code and a.bfc_id = bfc.id LEFT JOIN outpt_settle d on a.hosp_code = d.hosp_code and a.settle_id = d.id WHERE a.hosp_code = #{hospCode}
and a.status_code = #{data.statusCode}
and a.settle_code = #{data.settleCode}
and a.visit_id = #{data.visitId}
and a.op_id = #{data.opId}
ORDER BY c.group_no, c.group_seq_no

SELECT
id, hosp_code, dq_id, register_time, start_time, end_time, register_num, profile_id, is_use, is_lock, is_add, crte_id, crte_name, crte_time
FROM
outpt_doctor_register
where hosp_code = #{hospCode}
and is_use = '0'
and is_lock = '0'

    and ifnull(profile_id,'') =''


    and dq_id = #{data.dqId}



    and left(start_time, 5) = #{data.startTime}



    and left(end_time, 5) = #{data.endTime}

order by register_time

select id, hosp_code, dq_id, register_time, start_time, end_time, register_num, profile_id, is_use, is_lock, is_add, crte_id, crte_name, crte_time
from outpt_doctor_register
where hosp_code = #{hospCode}

    and dq_id = #{dqId}


    and profile_id = #{profileId}


    and start_time = #{startTime}


    and end_time = #{endTime}

order by register_time

select *
from outpt_doctor_register
where hosp_code = #{hospCode}
  and id = #{sourceId}

mapper.update:
update outpt_doctor_register a, outpt_doctor_queue b, outpt_classes_queue c set a.is_lock = #{isLock}, a.profile_id = #{profileId}, a.is_use = #{isUse},
a.seq_no = #{seqNo}
a.seq_no =( select count(1) from outpt_register d where d.dept_id = c.dept_id and date(d.register_time) = date(#{registerTime}) and is_cancel = '0')
where a.dq_id = b.id and b.cq_id = c.id and a.hosp_code =#{hospCode} and a.id = #{sourceId}

update outpt_doctor_register
set is_use = #{isUse}
where hosp_code = #{hospCode}
  and id = #{id}

update outpt_register
set is_cancel = #{isCancel}
where hosp_code = #{hospCode}
  and id = #{id}

update
outpt_register_settle

    status_code = #{statusCode}

where 1=1
and register_id = #{registerId}
and hosp_code = #{hospCode}

mapper.select:
SELECT a.id as apply_id, a.type_code, b.name, b.visit_no, a.apply_no, a.bar_code, c.item_name, c.price, c.num, a.dept_id, a.dept_name, a.report_time, a.rporter FROM medic_apply a JOIN outpt_prescribe_detail c on a.hosp_code = c.hosp_code and a.opd_id = c.id JOIN outpt_visit b on a.hosp_code = b.hosp_code and a.visit_id = b.id where a.hosp_code = #{hospCode}
and a.visit_id in
#{visitId}
and date_format(b.visit_time, '%Y-%m-%d') >= date_format(#{startDate}, '%Y-%m-%d')
and date_format(b.visit_time, '%Y-%m-%d') <= date_format(#{endDate}, '%Y-%m-%d')
and a.type_code = #{typeCode}
and (b.name like concat('%', #{keyword}, '%') or b.visit_no = #{keyword})
and a.dept_id = #{deptId}
order by b.visit_time desc

select c.profile_id as profileId, c.name, c.age, c.age_unit_code, c.gender_code, b.apply_time, b.report_time, b.dept_name, b.doctor_name, a.item_id, a.item_name, a.result, a.result_type, a.result_unit, a.result_detail, a.reference_value, a.range_state, a.check_part, a.check_ways, a.check_type, a.pic_url, a.diagnosis_name, a.reporte_text, a.is_positive from medic_result a left join medic_apply b on a.apply_no = b.apply_no and a.hosp_code = b.hosp_code JOIN outpt_visit c on a.hosp_code = c.hosp_code and a.visit_id = c.id where a.hosp_code = #{hospCode}
and b.type_code = #{typeCode}
and b.apply_no = #{applyNo}

select
distinct
a.*
from outpt_register a
left join  outpt_register_detail b on a.hosp_code = b.hosp_code and a.id = b.register_id
where a.hosp_code=#{hospCode}
and b.status_code = '0'
 and a.is_cancel = <> '1' 

    and a.cert_no = #{certNo}


    and a.dept_id = #{deptId}


    and DATE_FORMAT(a.register_time,'%Y-%m-%d') = DATE_FORMAT(#{nowDate}, '%Y-%m-%d')


    and a.cq_id = #{cqId}

select odq.* from outpt_doctor_queue odq where odq.hosp_code=#{hospCode}
and odq.id = #{id}
limit 1

select
ocq.*
from outpt_classes_queue ocq
where ocq.hosp_code = #{hospCode,jdbcType=VARCHAR}

    and ocq.id in

        #{item}



    and ocq.dept_id = #{deptId}


    and ocq.cs_id = #{csId}


    and ocq.cy_id = #{cyId}


    and ocq.triage_id = #{triageId}


    and ocq.is_valid = #{isValid}


    and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') >=#{queueStartDate}


    and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') <=#{queueEndDate}

select
*
from outpt_register
where hosp_code = #{hospCode}

    and id = #{id}


    and register_no = #{registerNo}

order by register_time

select
*
from outpt_visit
where hosp_code = #{hospCode}

    and id = #{visitId}

select
id, hosp_code, register_id, visit_id, item_code, item_id, item_name, bfc_id, price, num, use_code, spec, preferential_id, total_price, preferential_price, reality_price, unit_code, status_code, origin_id, crte_id, crte_name, crte_time
from
outpt_register_detail
where hosp_code = #{hospCode}
and status_code = '0'

    and register_id = #{id}


    and visit_id = #{visitId}

order by crte_time

select id, hosp_code, register_id, total_price, preferential_price, reality_price, settle_time, daily_settle_id, status_code, origin_id, bill_id, bill_no, pay_code, order_no, crte_id, crte_name, crte_time from outpt_register_settle where hosp_code = #{hospCode} and status_code = '0'
and register_id = #{id}
order by settle_time

select
id, hosp_code, rs_id, visit_id, pay_code, order_no, price, service_price
from
outpt_register_pay
where hosp_code = #{hospCode}

    and visit_id = #{visitId}
