mapper.delete:
DELETE
FROM
  outpt_settle
WHERE
hosp_code = #{hospCode}
AND visit_id = #{visitId}
AND is_settle = #{isSettle}
AND status_code = #{statusCode}

mapper.select:
select
, d.name as xmlbname, oc.reality_price as lastrealityprice, d.value as xmlbcode
from
outpt_cost oc
LEFT JOIN
outpt_prescribe op
on oc.op_id = op.id
AND op.hosp_code = #{hospCode}
left join sys_code_detail d on oc.item_code = d.value and oc.hosp_code = d.hosp_code AND d.c_code = 'XMLB'
where
oc.hosp_code = #{hospCode}
AND oc.source_code != '1'
AND op.is_settle = '0'
AND op.is_cancel = '0'
AND op.is_submit = '1'
AND oc.visit_id = #{visitId}

    AND oc.settle_code IN (

        #{settleCode}

    )


    AND oc.status_code = #{statusCode}

select

from
outpt_cost oc
where
oc.hosp_code = #{hospCode}
AND oc.visit_id = #{visitId}

    AND oc.settle_code IN (

        #{settleCode}

    )


    AND oc.status_code = #{statusCode}

AND IFNULL(oc.op_id,'') = ''
AND IFNULL(oc.source_code,'') != ''
AND oc.source_code != '1'

select * from stro_stock a left join ( select a.biz_id,a.item_id, a.hosp_code,sum(totalNum) totalNum from ( select a.phar_id biz_id, a.item_id, a.hosp_code, case when a.item_code = '1' and a.num_unit_code = b.split_unit_code then a.total_num when a.item_code = '1' and a.num_unit_code = b.unit_code then a.total_num*b.split_ratio when a.item_code = '2' and a.num_unit_code = c.split_unit_code then a.total_num when a.item_code = '2' and a.num_unit_code = c.unit_code then a.total_num*c.split_ratio else 0 end totalNum from outpt_cost a left join base_drug b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = '1' left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = '2' where a.hosp_code = #{hospCode} and ifnull(a.is_dist,'0') = '0' and date_sub(a.crte_time, INTERVAL #{wjsykc} hour) > now() and a.phar_id = #{pharId} and a.item_id = #{itemId}
union all select a.biz_id, a.item_id, a.hosp_code, ifnull(a.stock_occupy, 0) totalNum from stro_stock a where a.hosp_code = #{hospCode} and a.biz_id = #{pharId} and a.item_id = #{itemId} union all select #{pharId}, a.id, a.hosp_code, case when a.split_unit_code = #{numUnitCode} then #{totalNum} when a.unit_code = #{numUnitCode} then #{totalNum}*a.split_ratio else #{totalNum} end totalNum from base_drug a where a.hosp_code = #{hospCode} and a.id = #{itemId}
and 1 != 1
union all select #{pharId}, a.id, a.hosp_code, case when a.split_unit_code = #{numUnitCode} then #{totalNum} when a.unit_code = #{numUnitCode} then #{totalNum}*a.split_ratio else #{totalNum} end totalNum from base_material a where a.hosp_code = #{hospCode} and a.id = #{itemId} )a group by a.biz_id,a.item_id, a.hosp_code )b on a.hosp_code = b.hosp_code and a.item_id = b.item_id and a.biz_id = b.biz_id where a.hosp_code = #{hospCode} and a.biz_id = #{pharId} and a.item_id = #{itemId} and (a.split_num - ifnull(b.totalNum,0)) >= 0

SELECT A.* FROM ( SELECT id as item_id, '1' item_code, hosp_code, type_code,
(SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, code, good_name as item_name, spec, price AS primaryPrice, prep_code, dosage, dosage_unit_code, usage_code, (select br.id from base_rate br where br.code = rate_code and hosp_code = #{hospCode}) rate_id, unit_code, avg_buy_price, avg_sell_price, split_ratio, split_unit_code, split_price, drug_remark, '0' as is_cg, good_pym, good_wbm FROM base_drug WHERE is_out = '1' AND is_valid = '1' AND hosp_code = #{hospCode} UNION ALL SELECT id as item_id, '2' item_code, hosp_code, type_code, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, code, NAME as item_name, spec, price AS primaryPrice, '' as prep_code, '' as dosage, '' as dosage_unit_code, '' as usage_code, '' as rate_id, unit_code, avg_buy_price, avg_sell_price, split_ratio, split_unit_code, split_price, remark AS drug_remark, '0' as is_cg, pym as good_pym, wbm as good_wbm FROM base_material WHERE is_valid = '1' AND hosp_code = #{hospCode} UNION ALL SELECT id as item_id, '3' AS item_code, hosp_code, type_code, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, CODE, NAME as item_name, spec, price AS primaryPrice, '' as prep_code, '' as dosage, '' as dosage_unit_code, '' as usage_code, '' as rate_id, unit_code, '' as avg_buy_price, '' as avg_sell_price, '' AS split_ratio, '' AS split_unit_code, '' AS split_price, remark AS drug_remark, is_cg, name_pym as good_pym, name_wbm as good_wbm FROM base_item WHERE is_out = '1' AND is_valid = '1' AND hosp_code = #{hospCode} ) A
#{item.itemId}

