mapper.select:
select
id, hosp_code, visit_id, settle_id, payment_settle_id, visit_no, total_price, payment_price, pay_code,
state_code, omsgid, oinfno, remark, crte_id, crte_name, crte_time, settle_code, one_settle_id, red_id,
old_settle_id, settle_time
from payment_settle


        and id = #{id}


        and hosp_code = #{hospCode}


        and visit_id = #{visitId}


        and settle_id = #{settleId}


        and payment_settle_id = #{paymentSettleId}


        and visit_no = #{visitNo}


        and total_price = #{totalPrice}


        and payment_price = #{paymentPrice}


        and pay_code = #{payCode}


        and state_code = #{stateCode}


        and omsgid = #{omsgid}


        and oinfno = #{oinfno}


        and remark = #{remark}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and settle_code = #{settleCode}


        and one_settle_id = #{oneSettleId}


        and red_id = #{redId}


        and old_settle_id = #{oldSettleId}


        and settle_time = #{settleTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from payment_settle


        and id = #{id}


        and hosp_code = #{hospCode}


        and visit_id = #{visitId}


        and settle_id = #{settleId}


        and payment_settle_id = #{paymentSettleId}


        and visit_no = #{visitNo}


        and total_price = #{totalPrice}


        and payment_price = #{paymentPrice}


        and pay_code = #{payCode}


        and state_code = #{stateCode}


        and omsgid = #{omsgid}


        and oinfno = #{oinfno}


        and remark = #{remark}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and settle_code = #{settleCode}


        and one_settle_id = #{oneSettleId}


        and red_id = #{redId}


        and old_settle_id = #{oldSettleId}


        and settle_time = #{settleTime}

select
  id, hosp_code, visit_id, settle_id, payment_settle_id, visit_no, total_price, payment_price, pay_code, state_code, omsgid, oinfno, remark, crte_id, crte_name, crte_time
from payment_settle
where hosp_code =#{hospCode}
and settle_id =#{settleId}
and visit_id =#{visitId}
and state_code ='1'
