mapper.select:
select * from ( select a.id, a.hosp_code hospCode, a.profile_id profileId, a.out_profile outProfile, a.register_id registerId, a.register_no registerNo, a.name ,a.gender_code genderCode, a.age,a.age_unit_code ageUnitCode, a.birthday, a.marry_code marryCode, a.nation_code nationCode, a.cert_code certCode, a.cert_no certNo, a.phone, a.visit_no visitNo, a.visit_code visitCode, a.patient_code patientCode, a.preferential_type_id preferentialTypeId, a.insure_code insureCode, a.insure_org_code insureOrgCode, a.insure_no insureNo, a.insure_biz_code insureBizCode, a.insure_treat_code insureTreatCode, a.insure_patient_id insurePatientId, a.insure_remark insureRemark, a.visit_time visitTime, a.remark, a.is_visit isVisit, a.is_first_visit isFirstVisit,a.doctor_id doctorId,a.doctor_name doctorName, a.crte_time, a.now_address as nowAddress,ifnull(a.tran_in_code,'0') as tranInCode, otv.triage_start_code as triageStatusCode, otv.is_call as isCanCall, case when b.doctor_id = #{doctorId} then '1' else '2' end type,c.disease_id diseaseId, d.name diseaseName, e.id isEk from outpt_visit a left join outpt_register b on a.id = b.visit_id and a.hosp_code = b.hosp_code left join outpt_diagnose c on a.id = c.visit_id and a.hosp_code = c.hosp_code and c.is_main = '1' and ifnull(c.disease_id,'') != '' left join base_disease d on c.disease_id = d.id and c.hosp_code = d.hosp_code left join sys_parameter e on e.code = 'MZYS_EKCFNL' and a.hosp_code = e.hosp_code and (DATE_FORMAT(FROM_DAYS(TO_DAYS(NOW())-TO_DAYS(a.birthday)), '%Y')+0) <= e.value left join outpt_triage_visit otv on a.hosp_code = otv.hosp_code and a.register_id = otv.register_id
left join sys_parameter f on f.code = 'GH_JS' and a.hosp_code = f.hosp_code left join outpt_register_settle g on b.id = g.register_id and b.hosp_code = g.hosp_code and g.status_code = '0'
left join sys_parameter sys on sys.code = 'GH_VALID_DAY' and a.hosp_code = sys.hosp_code and sys.is_valid = '1'
where a.hosp_code = #{hospCode}
and ((f.value = '0' and g.id is not null) or ifnull(f.value,'0') != '0' or f.id is null) and ( b.doctor_id = #{doctorId} or (b.dept_id = #{deptId} and ifnull(b.doctor_id,'') = '') ) and ifnull( a.is_visit, '0' ) = '0' and ifnull(b.is_cancel,'0') = '0'
and IF(sys.value > 0 , date(date_sub(now(), interval sys.value day)), date(now())) <= date(b.register_time)
and (a.name like concat('%',#{keyword},'%') or a.visit_no like concat('%',#{keyword},'%') or a.cert_no like concat('%',#{keyword},'%') or a.phone like concat('%',#{keyword},'%'))
and (b.dept_id = #{deptId} or a.dept_id = #{deptId} )
and a.dept_id = #{deptId} and ifnull( a.is_visit, '0' ) = '1' and date(a.visit_time) between #{startDate} and #{endDate} and (a.name like concat('%',#{keyword},'%') or a.visit_no like concat('%',#{keyword},'%') or a.cert_no like concat('%',#{keyword},'%') or a.phone like concat('%',#{keyword},'%'))
and b.dept_id = #{deptId} and ifnull( a.is_visit, '0' ) = '1' and date(a.visit_time) between #{startDate} and #{endDate} and (a.name like concat('%',#{keyword},'%') or a.visit_no like concat('%',#{keyword},'%') or a.cert_no like concat('%',#{keyword},'%') or a.phone like concat('%',#{keyword},'%'))
and b.dept_id = #{deptId}
and a.id = #{id}
) a order by a.type, a.visitTime,a.registerNo desc

mapper.insert:
insert into outpt_medical_record (
    id, hosp_code, visit_id, profile_id, disease_id, start_date, chief_complaint, present_illness, past_history, oneself_history, family_history,
    allergy_history, vaccination_history, auxiliary_inspect, disease_analysis, handle_suggestion, specialty_check, temperature, min_blood_pressure, max_blood_pressure,
    breath, height, blood_sugar, pulse, weight, bmi, entrust,remarke, crte_id, crte_name, crte_time,tcm_disease_id,tcm_syndromes_id,menstrual_history
)
values (
           #{id}, #{hospCode}, #{visitId}, #{profileId}, #{diseaseId}, #{startDate}, #{chiefComplaint}, #{presentIllness}, #{pastHistory}, #{oneselfHistory}, #{familyHistory},
           #{allergyHistory}, #{vaccinationHistory},  #{auxiliaryInspect}, #{diseaseAnalysis}, #{handleSuggestion}, #{specialtyCheck}, #{temperature}, #{minBloodPressure}, #{maxBloodPressure},
           #{breath}, #{height}, #{bloodSugar}, #{pulse}, #{weight}, #{bmi}, #{entrust},#{remarke}, #{crteId}, #{crteName}, now(),#{tcmDiseaseId},#{tcmSyndromesId},#{menstrualHistory}
       )

mapper.select:
select a.*,a.crte_time as crteTime, c.diagnose_ids, c.disease_names,
(select visit_no from outpt_visit where hosp_code = #{hospCode} and id = a.visit_id limit 1) as visitNo
from outpt_prescribe a
join outpt_settle b on a.hosp_code=b.hosp_code and a.settle_id=b.id
join(
select a.id,a.hosp_code, group_concat(b.id) diagnose_ids, group_concat(b.name) disease_names
from outpt_prescribe a
join base_disease b on find_in_set(b.id, a.diagnose_ids) and a.hosp_code = b.hosp_code
join outpt_settle c on a.hosp_code=c.hosp_code and a.settle_id=c.id
where a.hosp_code = #{hospCode} and c.settle_no=#{settleNo}
group by a.id ,a.hosp_code
)c on a.id = c.id and a.hosp_code = c.hosp_code
where a.hosp_code=#{hospCode}
and b.is_settle='1' and a.is_settle='1'

    and b.settle_no=#{settleNo}


    and a.type_code in (

        #{item}

    )

order by a.id desc

select a.id, a.hosp_code hospCode, a.visit_id visitId, a.op_id opId, a.opd_id opdId,
       a.self_ratio selfRatio, a.item_code itemCode, a.item_id itemId, a.item_name itemName,
       a.price, a.total_price totalPrice, a.spec, a.prep_code prepCode, a.dosage,
       a.dosage_unit_code dosageUnitCode, a.usage_code usageCode, a.rate_id rateId, a.use_days useDays,
       a.num, a.num_unit_code numUnitCode, a.total_num totalNum, a.use_code useCode
from outpt_cost a
where a.opd_id = #{opdId} and a.hosp_code = #{hospCode}

    and a.op_id = #{opId}


    and a.visit_id = #{visitId}

select a.*,( SELECT `name` FROM sys_code_detail WHERE c_code = 'FYLYFS' AND VALUE = a.source_code AND hosp_code = a.hosp_code ) AS sourceName,
(select `name` from sys_code_detail scd where scd.c_code='JSZT' and hosp_code = a.hosp_code and scd.value = a.settle_code) as settleStatus
from outpt_cost a
where a.hosp_code = #{hospCode}

    and a.visit_id = #{visitId}


    and  a.op_id = #{opId}


    and  a.source_code = '0'


    and  a.settle_code = '2'


    and  (a.settle_code = '0' or a.settle_code = '1')

select a.id, a.hosp_code hospCode, a.cy_id cyId, a.item_code itemCode, a.item_id itemId, a.num, a.use_code useCode, c.spec, case when a.item_code='2' then c.price when a.item_code='3' and #{levelCode}='1' then b.one_price when a.item_code='3' and #{levelCode}='2' then b.two_price when a.item_code='3' and #{levelCode}='3' then b.three_price else b.price end price,
case when a.item_code='2' then a.num*c.price when a.item_code='3' and #{levelCode}='1' then a.num*b.three_price when a.item_code='3' and #{levelCode}='2' then a.num*b.two_price when a.item_code='3' and #{levelCode}='3' then a.num*b.one_price else a.num*b.price end totalPrice, case when a.item_code='2' then a.num*c.price when a.item_code='3' and #{levelCode}='1' then a.num*b.three_price when a.item_code='3' and #{levelCode}='2' then a.num*b.two_price when a.item_code='3' and #{levelCode}='3' then a.num*b.one_price else a.num*b.price end realityPrice, 0 preferentialPrice, case when a.item_code='2' then c.name when a.item_code='3' then b.name end itemName, case when a.item_code='2' then c.bfc_code when a.item_code='3' then b.bfc_code end bfcCode, case when a.item_code='2' then e.id when a.item_code='3' then d.id end bfcId, case when a.item_code='2' then c.unit_code when a.item_code='3' then b.unit_code end unitCode from outpt_classify_cost a left join base_item b on a.item_id = b.id and a.hosp_code = b.hosp_code and a.item_code = '3' left join base_material c on a.item_id = c.id and a.hosp_code = c.hosp_code and a.item_code = '2' left join base_finance_classify d on b.bfc_code = d.code and b.hosp_code = d.hosp_code left join base_finance_classify e on c.bfc_code = e.code and c.hosp_code = e.hosp_code where a.hosp_code = #{hospCode}
and find_in_set(a.id,#{regDetailIds})
and a.cy_id = #{cfId}

select a.* from ( select a.id, a.hosp_code, a.bfc_code, a.code, a.good_name,a.usual_name as bName, a.spec, a.prep_code, a.dosage, a.dosage_unit_code, a.unit_code, a.rate_code, case when #{operType} = '1' and a.out_unit_code = a.unit_code then a.price when #{operType} = '1' and a.out_unit_code = a.split_unit_code then a.split_price when #{operType} = '2' and a.in_unit_code = a.unit_code then a.price when #{operType} = '2' and a.in_unit_code = a.split_unit_code then a.split_price end price, a.split_ratio, a.split_unit_code, a.split_price, a.is_lvp, a.usage_code, a.ddd, a.durg_code,
case when a.ph_code = '5' then '1' when a.ph_code = '1' or a.ph_code = '2' or a.ph_code = '3' then '4' when a.ph_code = '6' then '5' when a.ph_code = '4' then '6' else '1' end ph_code, a.antibacterial_code, a.is_skin, a.is_basic, a.basic_code, a.good_pym, a.good_wbm, null use_code,null outptUseCode, case when a.big_type_code = '2' then '3' when a.big_type_code = '3' then '7' else '1' end type, '1' itemCode, case when a.big_type_code = '2' then b.cyf when a.big_type_code = '1' then b.zcf when a.big_type_code = '0' or a.big_type_code = '3' then b.xyf end pharId, ifnull(c.id,'') rateId, d.id bfcId, ifnull(e.exec_dept_id, #{loginDeptId}) execDeptId,
'1' typeCode, #{loginDeptId} outDeptId, a.out_unit_code outUnitCode, a.in_unit_code inUnitCode, null isStopSame, null isStopSameNot, null isStopMyself, a.big_type_code bigTypeCode, a.drug_remark drugRemark, a.drug_img_path drugImgPath, f.name prodName, stock.num stockNum, convert((stock.stock_occupy/a.split_ratio), decimal(10, 4)) as stockOccupy,stock.split_num splitNum, '0' isWz, a.usual_name,a.usual_wbm, a.usual_pym, '' as technology_code, a.nation_code, a.nation_name nation_name, a.reimbursement_ratio from base_drug a left join( select max(if(b.type_code = '1',c.id,'0')) xyf, max(if(b.type_code = '2',c.id,'0')) zcf,max(if(b.type_code = '3',c.id,'0')) cyf, max(if(b.type_code = '4',c.id,'0')) clf, max(b.drugstore_code) drugstore_code from base_dept a join base_dept_drugstore b on a.code = b.dept_code and a.hosp_code = b.hosp_code join base_dept c on c.hosp_code = b.hosp_code
and SUBSTRING_INDEX(b.drugstore_code,',',1) = c.code
and c.id = #{pharId}
where a.id =#{loginDeptId} and a.hosp_code = #{hospCode} )b on 1 = 1 join stro_stock stock on ( case when a.big_type_code = '2' then b.cyf when a.big_type_code = '1' then b.zcf when a.big_type_code = '0' or a.big_type_code = '3' then b.xyf end ) = stock.biz_id and a.id = stock.item_id and a.hosp_code = stock.hosp_code left join base_rate c on a.rate_code = c.code and a.hosp_code = c.hosp_code left join base_finance_classify d on a.bfc_code = d.code and a.hosp_code = d.hosp_code left join base_outpt_exec e on find_in_set(a.usage_code , e.usage_codes) and FIND_IN_SET(#{loginDeptId} ,e.dept_ids) and a.hosp_code = e.hosp_code left join base_product f on a.prod_code = f.code and a.hosp_code = f.hosp_code
left join outpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
left join inpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
where a.hosp_code = #{hospCode}
and '2' = #{yzmlType}
and ( ((#{operType} = '1' and ifnull(a.is_out,'1') = '1' and (a.out_unit_code = a.unit_code or a.out_unit_code = a.split_unit_code or ifnull(a.out_unit_code,'')='') ) or (#{operType} = '2' and ifnull(a.is_in,'1') = '1' and (a.in_unit_code = a.unit_code or a.in_unit_code = a.split_unit_code or ifnull(a.in_unit_code,'')='')))
and visit.age between ifnull(a.min_age,0) and ifnull(a.max_age,1000) and (visit.gender_code = a.gender_code or ifnull(visit.gender_code,'0') in ('0','9') or ifnull(a.gender_code,'0') in ('0','9') or a.gender_code = '' or visit.gender_code = '')
)
and 1 = 2
and a.is_valid = '1' and (stock.num - ifnull(stock.stock_occupy,0)/stock.split_ratio) > 0 union all
select a.id, a.hosp_code, a.bfc_code, a.code, a.name good_name,a.other_name as bName, a.spec, null prep_code, null dosage, null dosage_unit_code, a.unit_code,null rate_code,a.split_price price, '1' split_ratio, a.split_unit_code split_unit_code, a.split_price split_price, null is_lvp,null usage_code, null ddd, null durg_code, '1' ph_code,null antibacterial_code, null is_skin, null is_basic, null basic_code, a.pym good_pym, a.wbm good_wbm, a.use_code,a.outpt_use_code outptUseCode, '2' type, '2' itemCode, b.clf pharId, null rateId, d.id bfcId, null execDeptId, '15' typeCode,#{loginDeptId} outDeptId, a.split_unit_code ouUtnitCode, a.split_unit_code inUnitCode, null isStopSame, null isStopSameNot, null isStopMyself, '3' bigTypeCode, '' drugRemark, '' drugImgPath, f.name prodName, stock.num stockNum, convert((stock.stock_occupy/a.split_ratio), decimal(10, 4)) stockOccupy, stock.split_num splitNum, '0' isWz, a.name usual_name,a.wbm usual_wbm, a.pym usual_pym ,'' as technology_code,a.nation_code, a.nation_name nation_name, a.reimbursement_ratio from base_material a left join( select max(if(b.type_code = '1',c.id,'0')) xyf, max(if(b.type_code = '2',c.id,'0')) zyf,max(if(b.type_code = '3',c.id,'0')) cyf, max(if(b.type_code = '4',c.id,'0')) clf, max(b.drugstore_code) drugstore_code from base_dept a join base_dept_drugstore b on a.code = b.dept_code and a.hosp_code = b.hosp_code join base_dept c on c.hosp_code = b.hosp_code
and SUBSTRING_INDEX(b.drugstore_code,',',1) = c.code
and c.id = #{pharId}
where a.id =#{loginDeptId} and a.hosp_code = #{hospCode} and b.type_code = '4' )b on 1 = 1 left join stro_stock stock on a.id = stock.item_id and a.hosp_code = stock.hosp_code
and stock.biz_id = #{pharId}
and b.clf = stock.biz_id
left join base_finance_classify d on a.bfc_code = d.code and a.hosp_code = d.hosp_code left join base_product f on a.prod_code = f.code and a.hosp_code = f.hosp_code where a.hosp_code = #{hospCode} and a.is_valid = '1' and (a.is_offical ='0' or a.is_offical is null)
and '3' = #{yzmlType}
union all
select a.id, a.hosp_code, null bfc_code, a.code, a.name good_name,a.other_name as bName, null spec, null prep_code, null dosage, null dosage_unit_code, a.unit_code, null rate_code, a.price,'1' split_ratio, a.unit_code split_unit_code, a.price split_price, null is_lvp, null usage_code, null ddd, null durg_code,'1' ph_code,null antibacterial_code,null is_skin, null is_basic, null basic_code, a.pym good_pym, a.wbm good_wbm, null use_code,null outptUseCode, case when a.type_code = '3' then '4' when a.type_code = '12' then '5' else '6' end type, '4' itemCode, '' pharId, '' rateId, '' bfcId, b.id execDeptId, a.type_code typeCode,c.id outDeptId, a.unit_code outUnitCode, a.unit_code inUnitCode, a.is_stop_same isStopSame, a.is_stop_same_not isStopSameNot, a.is_stop_myself isStopMyself, '4' bigTypeCode, '' drugRemark, '' drugImgPath, '' prodName, null stockNum, null stockOccupy,null splitNum, case when e.id is null then '0' else '1' end isWz, a.name usual_name,a.wbm usual_wbm, a.pym usual_pym, a.technology_code, a.union_nation_code as nation_code, a.union_nation_name as nation_name, a.reimbursement_ratio as reimbursement_ratio from base_advice a left join base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code left join base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code left join sys_parameter e on e.code = 'WZYZ_YZCODE' and a.code = e.value and a.hosp_code = e.hosp_code and e.is_valid = '1'
left join outpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
left join inpt_visit visit on visit.id = #{visitId} and a.hosp_code = visit.hosp_code
left join base_dept f on f.id = #{loginDeptId} and a.hosp_code = f.hosp_code where a.hosp_code = #{hospCode}
and '1' = #{yzmlType}
and e.id is not null
and 1 = 2
and '1' = #{yzmlType}
and (FIND_IN_SET(f.code ,a.dept_code) or ifnull(a.dept_code,'') = '') and a.is_valid = '1' and ( (#{operType} = a.use_scope_code or ifnull(a.use_scope_code,'0') = '0')
and visit.age between ifnull(a.min_age,0) and ifnull(a.max_age,1000) and (visit.gender_code = a.gender_code or ifnull(visit.gender_code,'0') in ('0','9') or ifnull(a.gender_code,'0') in ('0','9') or ifnull(visit.gender_code,'0') = '' or ifnull(a.gender_code,'0') = '')
) )a where 1=1
and ( a.good_pym like concat('%',#{keyword},'%') or a.good_wbm like concat('%',#{keyword},'%') or a.good_name like concat('%',#{keyword},'%') or a.usual_name like concat('%',#{keyword},'%') or a.usual_pym like concat('%',#{keyword},'%') or a.usual_wbm like concat('%',#{keyword},'%') or a.bName like concat('%',#{keyword},'%') )

mapper.delete:
delete from outpt_prescribe_exec  where hosp_code = #{hospCode} and visit_id = #{visitId} and opd_id in (
    select a.id from outpt_prescribe_detail a where a.visit_id = #{visitId} and a.op_id = #{id} and a.hosp_code = #{hospCode}
)

mapper.select:
select a.id, a.hosp_code hospCode, a.c_code code, a.name, a.value, a.remark, a.up_value upValue, a.is_end isEnd,
case when a.c_code = 'YPFL' then '1' when (a.c_code = 'LISXMLX' or a.c_code = 'PACSXMLX') then '2' else '0' end type
from sys_code_detail a
where a.hosp_code = #{hospCode} and a.c_code = #{code}

    and ifnull(a.up_value,'') = #{upValue}

order by a.seq_no asc

select a.id, a.hosp_code, null bfc_code, a.code, a.name good_name,a.other_name as bName, null spec, null prep_code, null dosage, null dosage_unit_code, a.unit_code, null rate_code, a.price,'1' split_ratio, a.unit_code split_unit_code, a.price split_price, null is_lvp, null usage_code, null ddd, null durg_code,'1' phCode,null antibacterial_code,null is_skin, null is_basic, null basic_code, a.pym good_pym, a.wbm good_wbm, null use_code, case when a.type_code = '3' then '4' when a.type_code = '12' then '5' else '6' end type, '4' itemCode, null pharId, null rateId, null bfcId, b.id execDeptId, c.id outDeptId, a.unit_code outUnitCode, a.unit_code inUnitCode, a.is_stop_same_not isStopSameNot, a.is_stop_myself isStopMyself,'4' bigTypeCode, a.type_code typeCode, case when e.id is null then '0' else '1' end isWz, a.technology_code as technologyCode from base_advice a left join base_dept b on a.out_dept_code = b.code and a.hosp_code = b.hosp_code left join base_dept c on a.in_dept_code = c.code and a.hosp_code = c.hosp_code left join sys_parameter e on e.code = 'WZYZ_YZCODE' and a.code = e.value and a.hosp_code = e.hosp_code and e.is_valid = '1' where a.hosp_code = #{hospCode} and a.is_valid = '1'
and ifnull( a.type_code, '0' ) = #{typeCode}
and ifnull( a.technology_code, '0' ) = #{typeCode} and a.type_code = (case when #{code} = 'LISXMLX' then '3' when #{code} = 'PACSXMLX' then '12' else '0' end)

select a.id, a.hosp_code, a.name, a.type_code, a.dept_id, a.dept_name, a.doctor_id, a.doctor_name, a.remark, a.is_valid, a.pym, a.wbm, a.audit_code, a.audit_id, a.audit_name, a.audit_time, a.crte_id, a.crte_name, a.crte_time from outpt_prescribe_temp a where a.hosp_code = #{hospCode} and a.audit_code = '1'
and a.type_code ='2' and ifnull( a.doctor_id, '0' ) = #{doctorId}
and a.type_code ='1' and ifnull( a.dept_id, '0' ) = #{deptId}
and a.type_code ='0'
