mapper.select:
select
ocq.*, ocy.name as classifyName, oc.name as classesName, bd.name as deptName,
(select `name` from sys_code_detail where c_code = 'FZFS' and hosp_code = #{hospCode} and value = ocq.triage_code) as triageTypeName
from outpt_classes_queue ocq, outpt_classify ocy, outpt_classes oc, base_dept bd
where
ocq.cy_id = ocy.id
and ocq.cs_id = oc.id
and ocq.dept_id = bd.id
and ocq.hosp_code=#{hospCode}

    and ocq.dept_id = #{deptId}


    and ocq.is_valid = #{isValid}


    and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') >=#{queueStartDate}


    and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') <=#{queueEndDate}

order by ocq.queue_date desc

select
ocq.*
from outpt_classes_queue ocq
where ocq.hosp_code = #{hospCode,jdbcType=VARCHAR}

    and ocq.dept_id = #{deptId}


    and ocq.cs_id = #{csId}


    and ocq.cy_id = #{cyId}


    and ocq.queue_date = #{queueDate}

SELECT oc.`name` as className, oc.start_date, oc.end_date, odq.doctor_id as doctorId, odq.doctor_name as doctorName, ocq.queue_date as queueDate, occ.weeks, ocy.`name` as classOfShift FROM outpt_classes oc left join outpt_classes_queue ocq on oc.hosp_code = ocq.hosp_code and oc.id = ocq.cs_id left join outpt_doctor_queue odq on ocq.hosp_code = odq.hosp_code and ocq.id = odq.cq_id left join outpt_classify_classes occ on ocq.cc_id = occ.id and ocq.hosp_code = occ.hosp_code left join outpt_classify ocy on occ.cy_id = ocy.id and occ.hosp_code = ocy.hosp_code
WHERE odq.doctor_id in
#{item}
and ocq.hosp_code = #{hospCode} and ocq.queue_date = date(#{queueDate}) and oc.id = #{classId}

mapper.delete:
DELETE a FROM outpt_classes_queue a
WHERE a.hosp_code = #{hospCode}








--         AND a.is_valid = '0'

AND (SELECT count(*) FROM outpt_doctor_queue b WHERE b.cq_id = a.id ) = 0

AND (SELECT count(*) FROM outpt_register c WHERE c.cq_id =  a.id) = 0

    AND a.triage_id = #{triageId}

DELETE a FROM outpt_classes_queue a
WHERE a.hosp_code = #{hospCode}
and a.id in

    #{item}

mapper.select:
select distinct t.* FROM(
SELECT a.*, k.name as dept_name FROM outpt_classify_classes a LEFT JOIN base_dept k ON k.id = a.dept_id WHERE a.hosp_code =#{hospCode}
and a.triage_id = #{triageId}
AND a.is_valid = '1' AND a.weeks IN
#{item}
)t

select
ocq.*,bd.`name` as triageName
from outpt_classes_queue ocq,
base_dept bd
where ocq.triage_id = bd.id and ocq.hosp_code = bd.hosp_code
  and ocq.hosp_code = #{hospCode,jdbcType=VARCHAR}

    and ocq.id in

        #{item}



    and ocq.dept_id = #{deptId}


    and ocq.cs_id = #{csId}


    and ocq.cy_id = #{cyId}


    and ocq.triage_id = #{triageId}


    and ocq.is_valid = #{isValid}


    and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') >=#{queueStartDate}


    and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') <=#{queueEndDate}

select
    *
from outpt_register
where
    hosp_code = #{hospCode,jdbcType=VARCHAR}
  and cq_id = #{id}

select
     ocq.*
from outpt_classes_queue ocq
where
     ocq.hosp_code=#{hospCode}
    AND cs_id = #{csId}
    AND cy_id = #{cyId}
    AND cc_id = #{ccId}
limit 1

select  occ.* from outpt_classify_classes occ
where occ.cs_id = #{csId} and occ.is_valid = '1' and hosp_code = #{hospCode}
 and occ.cy_id = #{cyId} and occ.weeks = #{weeks}
