  crte_time
from outpt_classify_classes
where  hosp_code = #{hospCode}
  AND weeks = #{weeks}
  AND cy_id = #{cyId}
  AND is_valid = '1'
  AND cs_id in

      #{item}

select c.start_date as startDate, c.end_date as endDate, c.name as classesName, cc.id as ccId, cc.cy_id as cyId, cc.cs_id as csId, cc.weeks as weeks from outpt_classes_doctor cd left join outpt_classify_classes cc on cd.cc_id = cc.id and cd.hosp_code = cc.hosp_code left join outpt_classes c on cc.cs_id = c.id and cc.hosp_code = c.hosp_code where cd.hosp_code = #{hospCode} and cd.doctor_id = #{doctorId} and cc.cy_id = #{cyId} and cd.is_valid = '1' and cc.is_valid = '1'
and cc.id != #{ccId}
and cc.weeks in
#{item}

select count(0) from outpt_classify_classes
where hosp_code = #{hospCode} and cy_id = #{cyId} and cs_id = #{csId} and weeks = #{weeks} and is_valid = #{isValid}

    and id != #{id}

