mapper.select:
SELECT

FROM outpt_cost oc join outpt_settle s on s.id=oc.settle_id and s.hosp_code=oc.hosp_code
where
    oc.hosp_code = #{hospCode}

        AND oc.visit_id = #{visitId}


        AND oc.op_id = #{opId}


        AND oc.settle_code = #{settleCode}


        AND oc.status_code = #{statusCode}


        AND s.settle_no = #{settleId}

SELECT A.*, ( case bp.out_code when '1' then A.primaryPrice * (1-bp.out_scale) when '2' then bp.out_scale else 0 end ) AS reducedPrice, /* 优惠金额 */ ( case bp.out_code when '1' then A.primaryPrice * bp.out_scale when '2' then A.primaryPrice - bp.out_scale else A.primaryPrice end ) AS reducedAfterPrice, /* 优惠后金额 */ ( case bp.out_code when '1' then A.split_price * (1-bp.out_scale) when '2' then bp.out_scale / A.split_ratio else 0 end ) AS splitReducedPrice, /* 拆零优惠金额 */ ( case bp.out_code when '1' then A.split_price * bp.out_scale when '2' then (A.primaryPrice - bp.out_scale) / A.split_ratio else A.split_price end ) AS splitReducedAfterPrice /* 拆零优惠后金额 */ FROM ( /* 药品 */ SELECT d.name as ykName, ss.biz_id as pharId, ss.item_id, ss.item_code, bd.type_code, bd.big_type_code, bd.code, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bd.bfc_code and hosp_code = #{hospCode}) bfc_id, ss.item_name, bd.spec, bd.dosage, bd.price primaryPrice, bd.unit_code, bd.out_unit_code, bd.avg_buy_price, bd.avg_sell_price, bd.split_ratio, bd.split_unit_code, bd.split_price, bd.usage_code, bd.ndan, bd.good_pym, bd.good_wbm, '0' as is_cg, ss.num as stock_num, ss.split_num, ss.stock_occupy, bd.rate_code rateCode FROM stro_stock ss join base_drug bd on ss.hosp_code = #{hospCode} AND bd.hosp_code = #{hospCode} AND (bd.is_out = '1' or bd.is_out is null) AND (bd.out_unit_code = bd.unit_code or bd.out_unit_code = bd.split_unit_code or ifnull(bd.out_unit_code,'')='') AND bd.is_valid = '1' AND ss.item_id = bd.id
join base_dept d ON ss.biz_id = d.id and (d.type_code = '4' or d.type_code = '5') and d.hosp_code = #{hospCode} where bd.type_code is not null
and (ss.split_num - IFNULL(ss.stock_occupy,0)) > 0
and ss.biz_id = #{pharId}
UNION ALL /* 材料 */ SELECT d.name as ykName, ss.biz_id as pharId, ss.item_id, ss.item_code, bm.type_code, '' AS big_type_code, bm.code, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bm.bfc_code and hosp_code = #{hospCode}) bfc_id, ss.item_name, bm.spec, null AS dosage, bm.price primaryPrice, bm.unit_code, bm.unit_code as out_unit_code, bm.avg_buy_price, bm.avg_sell_price, bm.split_ratio, bm.split_unit_code, bm.split_price, '' AS usage_code, bm.reg_cert_no AS ndan, bm.pym AS good_pym, bm.wbm AS good_wbm, '0' as is_cg, ss.num as stock_num, ss.split_num, ss.stock_occupy, '' rateCode FROM stro_stock ss join base_material bm on ss.hosp_code = #{hospCode} AND bm.hosp_code = #{hospCode} AND bm.is_valid = '1' AND ss.item_id = bm.id join base_dept d ON ss.biz_id = d.id and d.hosp_code = #{hospCode} where bm.type_code is not null and bm.is_valid = '1'
and ss.biz_id = #{pharId}
UNION ALL /* 项目 */ SELECT '' as ykName, '' as pharId, id as item_id, '3' AS item_code, type_code, '' AS big_type_code, CODE, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, NAME as item_name, spec, null as dosage, price primaryPrice, unit_code, unit_code as out_unit_code, '' AS avg_buy_price, '' AS avg_sell_price, '' AS split_ratio, '' AS split_unit_code, '' AS split_price, '' AS usage_code, '' AS ndan, name_pym as good_pym, name_wbm as good_wbm, is_cg, null as stock_num, null as split_num, null as stock_occupy, '' rateCode FROM base_item WHERE is_valid = '1' AND (is_out = '1' or is_out is null) AND hosp_code = #{hospCode} ) A LEFT JOIN base_preferential bp ON A.item_id = bp.biz_code AND bp.pf_type_code = #{pfTypeCode} AND bp.hosp_code = #{hospCode}
A.type_code like concat('%',#{keyword},'%') OR A.item_name like concat('%',#{keyword},'%') OR A.good_pym like concat('%',#{keyword},'%') OR A.good_wbm like concat('%',#{keyword},'%')
#{item.itemId}

SELECT A.* FROM ( SELECT id as item_id, '1' item_code, hosp_code, type_code,
(SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, code, good_name as item_name, spec, price AS primaryPrice, prep_code, dosage, dosage_unit_code, usage_code, (select br.id from base_rate br where br.code = rate_code and hosp_code = #{hospCode}) rate_id, unit_code, avg_buy_price, avg_sell_price, split_ratio, split_unit_code, split_price, drug_remark, '0' as is_cg, good_pym, good_wbm FROM base_drug WHERE is_valid = '1' AND (is_out = '1' or is_out is null) AND hosp_code = #{hospCode} UNION ALL SELECT id as item_id, '2' item_code, hosp_code, type_code, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, code, NAME as item_name, spec, price AS primaryPrice, '' as prep_code, '' as dosage, '' as dosage_unit_code, '' as usage_code, '' as rate_id, unit_code, avg_buy_price, avg_sell_price, split_ratio, split_unit_code, split_price, remark AS drug_remark, '0' as is_cg, pym as good_pym, wbm as good_wbm FROM base_material WHERE is_valid = '1' AND hosp_code = #{hospCode} UNION ALL SELECT id as item_id, '3' AS item_code, hosp_code, type_code, (SELECT bfc.id FROM base_finance_classify bfc where bfc.code = bfc_code and hosp_code = #{hospCode}) bfc_id, CODE, NAME as item_name, spec, price AS primaryPrice, '' as prep_code, '' as dosage, '' as dosage_unit_code, '' as usage_code, '' as rate_id, unit_code, '' as avg_buy_price, '' as avg_sell_price, '' AS split_ratio, '' AS split_unit_code, '' AS split_price, remark AS drug_remark, is_cg, name_pym as good_pym, name_wbm as good_wbm FROM base_item WHERE is_valid = '1' AND (is_out = '1' or is_out is null ) AND hosp_code = #{hospCode} ) A
#{item.itemId}

