mapper.select:
select , d.name as xmlbname, oc.reality_price as lastrealityprice, d.value as xmlbcode, (select insure_item_code from insure_item_match iim where iim.hosp_item_id = oc.item_id group by item_id) insure_item_code, (select name from base_dept iim where iim.id = oc.dept_id) deptName, (select hosp_item_code from insure_item_match iim where iim.hosp_item_id = oc.item_id group by item_id) hosp_item_code from outpt_cost oc LEFT JOIN outpt_prescribe op on oc.op_id = op.id AND op.hosp_code = #{hospCode} left join sys_code_detail d on oc.item_code = d.value and oc.hosp_code = d.hosp_code AND d.c_code = 'XMLB' where oc.hosp_code = #{hospCode} AND oc.source_code != '1' AND op.is_settle = '0' AND op.is_cancel = '0' AND op.is_submit = '1' AND oc.visit_id = #{visitId}
AND oc.settle_code IN (
#{settleCode}
)
AND oc.status_code = #{statusCode}
AND oc.op_id IN (
#{opId}
)

select

from
outpt_cost oc
where
oc.hosp_code = #{hospCode}
AND oc.visit_id = #{visitId}

    AND oc.settle_code IN (

        #{settleCode}

    )


    AND oc.status_code = #{statusCode}

AND IFNULL(oc.op_id,'') = ''
AND IFNULL(oc.source_code,'') != ''
AND oc.source_code != '1'

SELECT
  bw.id AS id,
  COUNT(por.window_id) AS len
FROM
  base_window bw
  LEFT JOIN base_dept bd
    ON bw.dept_code = bd.code
    AND bd.hosp_code = #{hospCode}
  LEFT JOIN phar_out_receive por
    ON por.window_id = bw.id
    AND por.hosp_code = #{hospCode}
WHERE
bw.hosp_code = #{hospCode}
AND bd.id = #{pharId}
AND bw.is_valid = #{isValid}
AND bd.is_valid = #{isValid}
GROUP BY bw.id
ORDER BY len ASC

select
`name`
from
base_dept
where
hosp_code = #{hospCode}
AND is_valid = #{isValid}
AND id = #{pharId}

