mapper.select:
SELECT a.id , b.id as opd_id, b.group_no, a.sign_code, a.visit_id, c.name, c.gender_code, c.age, c.age_unit_code, d.crte_time, d.doctor_name, a.plan_exec_date, a.exec_id, a.exec_name, a.exec_time, (SELECT name FROM base_dept where id =a.exec_dept_id and hosp_code = a.hosp_code) as exec_dept_name, a.second_exec_id, a.second_exec_name, a.is_print, a.remark, b.content, b.item_name, b.spec, b.num, b.num_unit_code, b.dosage, b.dosage_unit_code, b.usage_code FROM outpt_prescribe_exec a left JOIN outpt_prescribe_detail b on a.hosp_code = b.hosp_code and a.opd_id = b.id left join outpt_visit c on a.visit_id = c.id and c.hosp_code = a.hosp_code LEFT JOIN outpt_prescribe d on b.op_id = d.id and b.visit_id = d.visit_id and b.hosp_code = d.hosp_code WHERE 1=1 and a.hosp_code = #{outptPrescribeDetailsDTO.hospCode} and b.exec_num > 0 and d.is_settle = '1' and d.is_cancel = '0'
and b.usage_code in
#{item}
and c.dept_id = #{outptPrescribeDetailsDTO.loginDeptId}
and (c.name like CONCAT('%',#{outptPrescribeDetailsDTO.keyword},'%') or c.visit_no like CONCAT('%',#{outptPrescribeDetailsDTO.keyword},'%'))
and a.sign_code = #{outptPrescribeDetailsDTO.type}
and d.crte_time >= STR_TO_DATE(#{outptPrescribeDetailsDTO.startTime},'%Y-%m-%d')
and d.crte_time <= DATE_ADD(STR_TO_DATE(#{outptPrescribeDetailsDTO.endTime},'%Y-%m-%d'),INTERVAL 1 day)
order by d.crte_time desc, a.plan_exec_date desc, b.group_no asc , b.group_seq_no asc

mapper.update:
UPDATE outpt_prescribe_exec
SET sign_code = '2'

    , exec_id = #{execId}


    , exec_name = #{execName}


    , exec_time = #{execDate}

WHERE 1=1

    and opd_id in

        #{item}

update outpt_prescribe_detail
set exec_id = #{execId}

    , exec_name = #{execName}


    , exec_date = #{execDate}

where 1=1

    and id in

        #{item}

update outpt_infusion_register
set exec_id = #{execId}

    , exec_name = #{execName}


    , exec_date = #{execDate}

where 1=1
and hosp_code = #{hospCode}

    and opd_id in

        #{item}

update outpt_prescribe_exec
set sign_code = '3'

    , exec_id = #{execId}


    , exec_name = #{execName}


    , exec_dept_id = #{execDeptId}


    , exec_time = #{execDate}

WHERE 1=1
and hosp_code = #{hospCode}

    and opd_id in

        #{item}

update.foreach:
update outpt_infusion_register
exec_id = #{item.execId},
exec_name = #{item.execName},
exec_date = #{item.execTime},
where 1=1 and hosp_code = #{item.hospCode}
and opd_id = #{item.opdId}

update outpt_prescribe_detail
set exec_date = NULL , exec_id = NULL, exec_name = NULL
where hosp_code = #{item.hospCode}
and id = #{item.opdId}

update outpt_prescribe_exec
set exec_time = NULL , exec_id = NULL, exec_name = NULL, sign_code = #{item.signCode}
where hosp_code = #{item.hospCode}

and id = #{item.id}

update outpt_infusion_register
set exec_date = NULL , exec_id = NULL, exec_name = NULL
where hosp_code = #{item.hospCode}

and opd_id = #{item.opdId}
