mapper.select:
SELECT a.id, a.status_code, a.settle_no, b.name, b.gender_code, b.age, b.age_unit_code, a.patient_code, b.dept_name, c.doctor_name, a.total_price, a.reality_price, IFNULL(a.self_price,0) as self_price, (IFNULL(a.mi_price,0)-IFNULL(a.acct_pay,0)) as mi_price, IFNULL(a.trunc_price,0) as trunc_price, IFNULL(a.acct_pay,0) as acct_pay, ifnull(a.credit_price, 0) as creditPrice, a.is_settle, a.crte_name, a.settle_time, a.card_price as cardPrice, (select name from base_preferential_type bp where bp.id = b.preferential_type_id and bp.hosp_code = b.hosp_code) as preferentialName, CONCAT( b.name,'/', (CASE b.gender_code WHEN '0' THEN '未知的性别' WHEN '1' THEN '男' WHEN '2' THEN '女' WHEN '9' THEN '未说明的性别' ELSE '' END ), '/',IFNULL( b.age, '-' ), (CASE b.age_unit_code WHEN '1' THEN '岁' WHEN '2' THEN '月' WHEN '3' THEN '周' WHEN '4' THEN '天' ELSE '' END ) ) AS personInfo, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '1' ) xjzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '2' ) skzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '3' ) zpzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '4' ) zzzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '5' ) wxzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '6' ) zfbzf FROM outpt_settle a JOIN outpt_visit b on a.visit_id = b.id and a.hosp_code = b.hosp_code
left join outpt_cost c on a.id = c.settle_id and a.hosp_code = c.hosp_code and c.settle_code = '2' WHERE a.hosp_code = #{hospCode} and a.is_settle='1'
and ( b.name LIKE CONCAT('%',#{keyword},'%') or a.settle_no = #{keyword} )
and a.settle_time >= #{startTime}
and a.settle_time <= #{endTime}
and a.crte_name LIKE CONCAT('%',#{chargeId},'%')
and c.doctor_id = #{doctorId}
and a.patient_code = #{patientCode}
group by a.id order by a.settle_no desc, a.settle_time desc

SELECT a.id, a.red_id, a.visit_id, a.settle_no, b.name, b.visit_no, b.gender_code, b.age, b.cert_no, b.out_profile, b.age_unit_code, a.patient_code, b.dept_name, oc.doctor_name, a.total_price, a.total_price as invoice_price, IFNULL(a.self_price,0) as self_price, IFNULL(a.mi_price,0) as mi_price, a.is_settle, a.crte_name, a.settle_time, a.source_pay_code, a.order_no, a.status_code, a.actual_price, a.reality_price, d.name as preferentialTypeName, a.crte_time, a.one_settle_id, a.card_price as cardPrice, c.card_no as cardNo, b.profile_id as profileId, b.is_phys as isPhys, a.credit_price as creditPrice, a.trunc_price as truncPrice, a.online_pay as onlinePay FROM outpt_settle a left join outpt_visit b on a.visit_id = b.id left join base_preferential_type d on b.preferential_type_id = d.id left join base_card_recharge_change bcrc on bcrc.settle_id = a.id and a.status_code = '0' and a.is_settle = '1' left join base_card c on c.hosp_code = a.hosp_code and c.id = bcrc.card_id
left join outpt_cost oc on a.id = oc.settle_id and a.hosp_code = oc.hosp_code and oc.settle_code = '2' where 1=1 and a.hosp_code = #{hospCode} and b.id is not null
and ( b.name LIKE CONCAT('%',#{keyword},'%') or b.cert_no LIKE CONCAT('%',#{keyword},'%'))
and a.settle_time >= #{startTime}
and a.settle_time <= #{endTime}
and oc.doctor_id = #{doctorId}
and b.dept_id = #{deptId}
and a.is_settle = #{isSettle}
group by a.id order by a.settle_no desc, a.settle_time desc

mapper.delete:
DELETE
FROM
  outpt_settle
WHERE
hosp_code = #{hospCode}
AND visit_id = #{visitId}
AND is_settle = #{isSettle}
AND status_code = #{statusCode}

mapper.select:
select b.id,b.name as diseaseName from outpt_diagnose a
left join base_disease b on a.disease_id = b.id
where a.hosp_code = #{hospCode} and a.visit_id = #{visitId}

mapper.update:
update insure_individual_visit set
pay_ord_id = #{payOrdId},
pay_token = #{payToken}
where visit_id = #{visitId} and hosp_code = #{hospCode}

mapper.select:
select * from ( SELECT a.id, a.status_code, a.settle_no as settleNo, b.name, b.gender_code, b.age, b.age_unit_code, a.patient_code, b.dept_name, b.doctor_name, a.total_price, a.reality_price, IFNULL(a.self_price,0) as self_price, (IFNULL(a.mi_price,0)-IFNULL(a.acct_pay,0)) as mi_price, IFNULL(a.trunc_price,0) as trunc_price, IFNULL(a.acct_pay,0) as acct_pay, ifnull(a.credit_price, 0) as creditPrice, a.is_settle, a.crte_name, a.settle_time as settleTime, a.card_price as cardPrice, (select name from base_preferential_type bp where bp.id = b.preferential_type_id and bp.hosp_code = b.hosp_code) as preferentialName, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '1' ) xjzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '2' ) skzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '3' ) zpzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '4' ) zzzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '5' ) wxzf, (select ifnull(sum(price),0) price from outpt_pay pay where a.id = pay.settle_id and a.hosp_code = pay.hosp_code and pay.pay_code = '6' ) zfbzf, ifnull(a.credit_is_pay,0) as creditIsPay,a.back_pay_time as backPayTime, a.back_pay_id as backPayId, a.back_pay_name as backPayName, b.id as visitId, case when b.patient_code>0 then '医保病人' else '自费病人' end as bka006Name, ifnull((select GROUP_CONCAT(b.name) as payCode from outpt_pay a join sys_code_detail b on b.value = a.pay_code and b.hosp_code =#{hospCode} and b.c_code ='ZFFS' where a.settle_id =#{settleId} and a.hosp_code = #{hospCode} GROUP BY a.settle_id),'现金') as payCode, b.visit_code as visitCode, b.visit_no as visitNo, '-' as personalNo, '1' as type, '' as printStatus, '' as registerId FROM outpt_settle a JOIN outpt_visit b on a.visit_id = b.id and a.hosp_code = b.hosp_code
left join outpt_cost c on a.id = c.settle_id and a.hosp_code = c.hosp_code and c.settle_code = '2' WHERE a.hosp_code = #{hospCode} and a.is_settle='1' and a.credit_price > 0 and a.credit_price is not null and a.credit_price !='' and a.status_code ='0'
and ( b.name LIKE CONCAT('%',#{keyword},'%') or a.settle_no = #{keyword} )
and a.settle_time >= #{startTime}
and a.settle_time <= #{endTime}
and a.back_pay_time >= #{startBackTime}
and a.back_pay_time <= #{endBackTime}
and a.crte_id = #{chargeId}
and c.doctor_id = #{doctorId}
and a.patient_code = #{patientCode}
and (a.credit_is_pay = #{creditIsPay} or a.credit_is_pay is null or a.credit_is_pay = '')
and a.credit_is_pay = #{creditIsPay}
union all select a.id, a.status_code, opr.register_no as settleNo, b.name, b.gender_code, b.age, b.age_unit_code, b.patient_code, b.dept_name, b.doctor_name, a.total_price, a.reality_price, a.total_price as self_price, 0 as mi_price, 0 as trunc_price, 0 as acct_pay, ifnull(a.credit_price, 0) as creditPrice, '1' as isSettle, a.crte_name, a.crte_time as settleTime, a.card_price as cardPrice, (select name from base_preferential_type bp where bp.id = b.preferential_type_id and bp.hosp_code = b.hosp_code) as preferentialName, (select ifnull(sum(price),0) price from outpt_register_pay pay where a.id = pay.rs_id and a.hosp_code = pay.hosp_code and pay.pay_code = '1' ) xjzf, (select ifnull(sum(price),0) price from outpt_register_pay pay where a.id = pay.rs_id and a.hosp_code = pay.hosp_code and pay.pay_code = '2' ) skzf, (select ifnull(sum(price),0) price from outpt_register_pay pay where a.id = pay.rs_id and a.hosp_code = pay.hosp_code and pay.pay_code = '3' ) zpzf, (select ifnull(sum(price),0) price from outpt_register_pay pay where a.id = pay.rs_id and a.hosp_code = pay.hosp_code and pay.pay_code = '4' ) zzzf, (select ifnull(sum(price),0) price from outpt_register_pay pay where a.id = pay.rs_id and a.hosp_code = pay.hosp_code and pay.pay_code = '5' ) wxzf, (select ifnull(sum(price),0) price from outpt_register_pay pay where a.id = pay.rs_id and a.hosp_code = pay.hosp_code and pay.pay_code = '6' ) zfbzf, ifnull(a.credit_is_pay,0) as creditIsPay,a.back_pay_time as backPayTime, a.back_pay_id as backPayId, a.back_pay_name as backPayName, b.id as visitId, case when ifnull(b.patient_code,0)>0 then '医保病人' else '自费病人' end as bka006Name, ifnull((select GROUP_CONCAT(b.name) as payCode from outpt_pay a join sys_code_detail b on b.value = a.pay_code and b.hosp_code =#{hospCode} and b.c_code ='ZFFS' where a.settle_id = #{settleId} and a.hosp_code = #{hospCode} GROUP BY a.settle_id),'现金') as payCode, b.visit_code as visitCode, b.visit_no as visitNo, '-' as personalNo, '0' as type, case when a.bill_no is not null and a.bill_no !='' then '1' else '0' end as printStatus, opr.id as registerId from outpt_register opr left join outpt_register_settle a on opr.hosp_code = a.hosp_code and opr.id=a.register_id inner join outpt_visit b on opr.hosp_code = b.hosp_code and opr.visit_id = b.id WHERE a.hosp_code = #{hospCode} and a.credit_price > 0 and a.credit_price is not null and a.credit_price !='' and a.status_code ='0'
and ( b.name LIKE CONCAT('%',#{keyword},'%') or a.order_no = #{keyword} )
and a.crte_time >= #{startTime}
and a.crte_time <= #{endTime}
and a.back_pay_time >= #{startBackTime}
and a.back_pay_time <= #{endBackTime}
and a.crte_id = #{chargeId}
and b.doctor_id = #{doctorId}
and b.patient_code = #{patientCode}
and (a.credit_is_pay = #{creditIsPay} or a.credit_is_pay is null or a.credit_is_pay = '')
and a.credit_is_pay = #{creditIsPay}
) tab group by tab.id order by tab.settleNo desc, tab.settleTime desc

SELECT a.id, a.red_id, a.visit_id, a.settle_no, b.name, b.visit_no, b.gender_code, b.age, b.cert_no, b.out_profile, b.age_unit_code, a.patient_code, b.dept_name, b.doctor_name, a.total_price, a.total_price as invoice_price, IFNULL(a.self_price,0) as self_price, IFNULL(a.mi_price,0) as mi_price, a.is_settle, a.crte_name, a.settle_time, a.source_pay_code, a.order_no, a.status_code, a.actual_price, a.reality_price, d.name as preferentialTypeName, a.crte_time, a.one_settle_id, a.card_price as cardPrice, c.card_no as cardNo, b.profile_id as profileId, b.is_phys as isPhys, a.credit_price as creditPrice, a.trunc_price as truncPrice, a.online_pay as onlinePay FROM outpt_settle a left join outpt_visit b on a.visit_id = b.id left join base_preferential_type d on b.preferential_type_id = d.id left join base_card_recharge_change bcrc on bcrc.settle_id = a.id and a.status_code = '0' and a.is_settle = '1' left join base_card c on c.hosp_code = a.hosp_code and c.id = bcrc.card_id left join (select settle_id, max(status) as status from outpt_refund_apply where hosp_code = #{hospCode} GROUP BY settle_id) m on m.settle_id = a.id or m.settle_id = a.old_settle_id where 1=1 and a.hosp_code = #{hospCode} and b.id is not null and m.status ='2'
and ( b.name LIKE CONCAT('%',#{keyword},'%') or b.cert_no LIKE CONCAT('%',#{keyword},'%'))
and a.settle_time >= #{startTime}
and a.settle_time <= #{endTime}
and b.doctor_id = #{doctorId}
and b.dept_id = #{deptId}
and a.is_settle = #{isSettle}
and a.status_code ='0'
and a.status_code in ('1','2')
order by a.settle_no desc, a.settle_time desc

select  id, hosp_code, visit_id, settle_id, payment_settle_id, visit_no, total_price, payment_price, pay_code, status_code, omsgid, oinfno, remark, crte_id, crte_name, crte_time,
settle_code, one_settle_id, red_id, old_settle_id, settle_time, is_settle
from payment_settle
where hosp_code =#{hospCode}
and visit_id =#{visitId}
and settle_id =#{settleId}
and status_code ='0'

