mapper.select:
SELECT a.id,a.hosp_code, a.register_id,a.visit_id,a.dq_id,a.doctor_id,a.doctor_name,a.clinic_id, ifnull( a.clinic_name, bc.NAME ) AS clinic_name, a.triage_id, a.triage_no,ifnull( a.sort_no, 0 ) AS sort_no, a.NAME,a.triage_start_code,a.is_call, a.call_time,a.call_number, ifnull( a.is_loss, '0' ) AS is_loss, a.loss_time, a.remark,a.triage_peo_id, a.triage_peo_name,a.triage_peo_time,a.crte_id, a.crte_name, a.crte_time,a.dept_id,a.dept_name,b.visit_no,d.NAME AS gh_name, c.register_time AS register_time,b.age,b.age_unit_code, b.gender_code AS sex,b.cert_no, DATE_FORMAT( b.birthday, '%Y-%m-%d' ) AS birthday,b.marry_code,b.patient_code,b.visit_time,b.phone FROM outpt_triage_visit a left join outpt_visit b on a.visit_id = b.id left join outpt_register c on a.register_id = c.id left join outpt_classify d on c.cf_id = d.id left join base_clinic bc on a.clinic_id = bc.id WHERE a.hosp_code = #{hospCode}
and a.dept_id = #{deptId}
and a.clinic_id is not null and a.triage_id is not null and a.triage_start_code != '4' and a.triage_start_code != '5' and (is_loss = '0' or is_loss is null)
and a.triage_id = #{triageId}
and a.triage_start_code = #{triageStartCode}
and a.triage_start_code != '1'
and a.name like concat('%',#{name},'%')
and date(c.register_time) <= date(#{endTime})
and date(c.register_time) >= date(#{startTime})
order by a.sort_no asc,c.register_time asc

SELECT a.id,a.hosp_code, a.register_id,a.visit_id,a.dq_id,a.doctor_id,a.doctor_name,a.clinic_id, ifnull( a.clinic_name, bc.NAME ) AS clinic_name, a.triage_id, a.triage_no,ifnull( a.sort_no, 0 ) AS sort_no, a.NAME,a.triage_start_code,a.is_call, a.call_time,a.call_number, ifnull( a.is_loss, '0' ) AS is_loss, a.loss_time, a.remark,a.triage_peo_id, a.triage_peo_name,a.triage_peo_time,a.crte_id, a.crte_name, a.crte_time,a.dept_id,a.dept_name,b.visit_no,d.NAME AS gh_name, c.register_time AS register_time,b.age,b.age_unit_code, b.gender_code AS sex,b.cert_no, DATE_FORMAT( b.birthday, '%Y-%m-%d' ) AS birthday,b.marry_code,b.patient_code,b.visit_time,b.phone FROM outpt_triage_visit a left join outpt_visit b on a.visit_id = b.id left join outpt_register c on a.register_id = c.id left join outpt_classify d on c.cf_id = d.id left join base_clinic bc on a.clinic_id = bc.id WHERE a.hosp_code = #{hospCode}
and a.dept_id = #{deptId}
and a.triage_id = #{triageId}
and a.clinic_id is not null and a.triage_id is not null and ( a.triage_start_code = '2') and (is_loss = '0' or is_loss is null)
and c.register_time >= date(#{keyword})
order by c.register_time asc, a.sort_no asc

SELECT a.id,a.hosp_code, a.register_id,a.visit_id,a.dq_id,a.doctor_id,a.doctor_name,a.clinic_id, ifnull( a.clinic_name, bc.NAME ) AS clinic_name, a.triage_id, a.triage_no,ifnull( a.sort_no, 0 ) AS sort_no, a.NAME,a.triage_start_code,a.is_call, a.call_time,a.call_number, ifnull( a.is_loss, '0' ) AS is_loss, a.loss_time, a.remark,a.triage_peo_id, a.triage_peo_name,a.triage_peo_time,a.crte_id, a.crte_name, a.crte_time,a.dept_id,a.dept_name,b.visit_no,d.NAME AS gh_name, c.register_time AS register_time,b.age,b.age_unit_code, b.gender_code AS sex,b.cert_no, DATE_FORMAT( b.birthday, '%Y-%m-%d' ) AS birthday,b.marry_code,b.patient_code,b.visit_time,b.phone FROM outpt_triage_visit a left join outpt_visit b on a.visit_id = b.id left join outpt_register c on a.register_id = c.id left join outpt_classify d on c.cf_id = d.id left join base_clinic bc on a.clinic_id = bc.id WHERE a.hosp_code = #{hospCode}
and a.triage_id = #{triageId}
and a.clinic_id is not null and a.triage_id is not null and a.triage_start_code = '3' and (is_loss = '0' or is_loss is null)
and c.register_time >= date(#{keyword})
order by c.register_time desc

SELECT

FROM outpt_triage_visit WHERE
hosp_code = #{hospCode}

    and register_id = #{registerId}


    and visit_id = #{visitId}


    and dq_id = #{dqId}


    and clinic_id = #{clinicId}


    and triage_id = #{triageId}


    and triage_no = #{triageNo}


    and name = #{name}


    and triage_start_code = #{triageStartCode}

SELECT

FROM outpt_triage_visit WHERE
hosp_code = #{hospCode}
and id =#{id}

SELECT (count(a.id)+1) as sequenceNo FROM outpt_visit a JOIN outpt_register b ON a.id = b.visit_id AND a.hosp_code = b.hosp_code LEFT JOIN outpt_diagnose c ON a.id = c.visit_id AND a.hosp_code = c.hosp_code AND c.is_main = '1' AND ifnull( c.disease_id, '' ) != '' LEFT JOIN base_disease d ON c.disease_id = d.id AND c.hosp_code = d.hosp_code LEFT JOIN sys_parameter e ON e.CODE = 'MZYS_EKCFNL' AND a.hosp_code = e.hosp_code AND ( DATE_FORMAT( FROM_DAYS( TO_DAYS(#{registerTime})- TO_DAYS( a.birthday )), '%Y' )+ 0 ) <= e. VALUE LEFT JOIN sys_parameter f ON f.CODE = 'GH_JS' AND a.hosp_code = f.hosp_code LEFT JOIN outpt_register_settle g ON b.id = g.register_id AND b.hosp_code = g.hosp_code AND g.status_code = '0' WHERE a.hosp_code = #{hospCode} AND (( f. VALUE = '0' AND g.id IS NOT NULL ) OR ifnull( f.VALUE, '0' ) != '0' OR f.id IS NULL )
AND b.doctor_id =#{doctorId}
AND (b.dept_id = #{deptId} and ifnull(b.doctor_id,'') = '')
AND ifnull( b.is_cancel, '0' ) = '0' AND date( b.register_time ) = date( #{registerTime}) AND b.register_time <= #{registerTime}

mapper.update:
update outpt_triage_visit
    set clinic_id =#{clinicId} ,
    set doctor_id =#{doctorId} ,
    set doctor_name =#{doctorName} ,
    set clinic_name =#{clinicName} ,
    set triage_start_code = #{triageStartCode} ,
    set sort_no = #{sortNo}
    set remark =#{remark}
where id = #{id}
