mapper.select:
select

from
outpt_register
where 1=1

    and id = #{id}


    and visit_id = #{visitId}

and hosp_code = #{hospCode}

mapper.update:
update
    outpt_register_settle
set
    status_code = #{statusCode}
where 1=1
  and register_id = #{registerId}
  and hosp_code = #{hospCode}

mapper.select:
select
*
from
outpt_doctor_register
where
hosp_code = #{hospCode}

    and dq_id = #{dqId}


    and is_use = #{isUse}


    and is_lock = #{isLock}


    and is_add = #{isAdd}

select odq.id, odq.doctor_id as doctorId, odq.doctor_name as doctorName, css.name as classesName, sum(odq.register_num) as registerNum, odq.cq_id as cqId, sum((select count(*) from outpt_doctor_register where dq_id = odq.id and (is_use = 0 and is_lock = 0))) as isUserRegister from outpt_doctor_queue odq left join outpt_classes_queue ocq on odq.cq_id = ocq.id left join outpt_classes css on css.id = ocq.cs_id where odq.hosp_code = #{hospCode} and DATE_FORMAT(ocq.queue_date,'%Y-%m-%d') = DATE_FORMAT(now(),'%Y-%m-%d')
and ocq.cy_id = #{id}
group by odq.id,odq.doctor_id,odq.doctor_name,odq.cq_id order by odq.id,odq.doctor_id

select
id, hosp_code, type_code, nation_code, code, name, ward_code, phone, up_dept_code, mg_dept_code, intro, place, remark, pym, wbm, is_valid, crte_id, crte_name, crte_time
from base_dept where hosp_code=#{hospCode}

    and id = #{id}


    and type_code = #{typeCode}


    and nation_code = #{nationCode}


    and code = #{code}


    and name = #{name}


    and ward_code = #{wardCode}


    and phone = #{phone}


    and up_dept_code = #{upDeptCode}


    and mg_dept_code = #{mgDeptCode}


    and intro = #{intro}


    and place = #{place}


    and remark = #{remark}


    and pym = #{pym}


    and wbm = #{wbm}


    and is_valid = #{isValid}


    and crte_id = #{crteId}


    and crte_name = #{crteName}


    and crte_time = #{crteTime}

order by  crte_time desc

select
*
from outpt_register where
hosp_code=#{hospCode}

    and cert_no = #{certNo}


    and dept_id = #{deptId}


    and DATE_FORMAT(register_time,'%Y-%m-%d') = DATE_FORMAT(now(),'%Y-%m-%d')


    and cq_id = #{cqId}

select ov.name,ov.visit_id,ov.dept_name,ov.register_no,ov.cert_no, ir.id, ir.hosp_code, ir.visit_id, ir.psn_no, ( case ir.insutype when '310' then '职工基本医疗保险' when '320' then '公务员医疗补助' when '330' then '大额医疗费用补助' when '340' then '离休人员医疗保障' when '390' then '城乡居民基本医疗保险' when '392' then '城乡居民大病医疗保险' when '510' then '生育保险' end) as insutype, ir.opsp_dise_code, ir.opsp_dise_name, ir.tel, ir.addr, ir.insu_optins, ir.ide_fixmedins_no, ir.ide_fixmedins_name, ir.hosp_ide_date, ir.diag_dr_codg, ir.diag_dr_name,ir. begndate, ir.enddate, ir.trt_dcla_detl_sn, ir.memo from outpt_register ov left join insure_record ir on ov.visit_id = ir.visit_id and ov.hosp_code = ir.hosp_code where ov.hosp_code =#{hospCode}
and ov.cert_no=#{keyword}
order by ir.crte_time desc
