mapper.select:
select DATE_FORMAT(a.crte_time, '%Y-%m-%d %H:%i:%s') createDate,(select name from base_dept where id=a.dept_id and hosp_code=#{hospCode}) deptName,
a.order_no orderNo,(select name from base_window where id=a.window_id and hosp_code=#{hospCode}) windowName,a.window_id windowId,
a.total_price totalPrice,a.status_code statusCode,a.assign_user_name assignUserName,
DATE_FORMAT(a.assign_time, '%Y-%m-%d %H:%i:%s') assignDate,a.id,
(select name from base_order_receive where hosp_code=a.hosp_code and id=a.order_receive_id) orderReceiveName,
crte_name as crteName
from phar_in_receive a
where 1=1 and a.hosp_code=#{hospCode}

  and a.status_code != '3'


    and a.phar_id=#{pharId}


    and a.order_no like CONCAT('%',#{orderNo},'%')


    and a.status_code = #{status}



        and DATE_FORMAT(a.crte_time,'%Y-%m-%d') = #{queryDate}


        and DATE_FORMAT(a.assign_time,'%Y-%m-%d') = #{queryDate}


        and (DATE_FORMAT(a.assign_time,'%Y-%m-%d') = #{queryDate} or DATE_FORMAT(a.crte_time,'%Y-%m-%d') = #{queryDate})



    and a.dept_id=#{deptId}

order by a.assign_time desc,a.crte_time desc

        select DATE_FORMAT(a.crte_time, '%Y-%m-%d') createDate,(select name from base_dept where id=a.dept_id and hosp_code=#{hospCode}) deptName,
        a.order_no orderNo,(select name from base_window where id=a.window_id and hosp_code=#{hospCode}) windowName,a.window_id windowId,
        a.total_price totalPrice,a.status_code statusCode,a.assign_user_name assignUserName,
        DATE_FORMAT(a.assign_time, '%Y-%m-%d %H:%i:%s') assignDate,a.id,a.dept_id,
        (select name from base_order_receive where hosp_code=a.hosp_code and id=a.order_receive_id) orderReceiveName
        from phar_in_receive a
        where 1=1 and a.hosp_code=#{hospCode} and a.status_code != '1'

            and a.phar_id=#{pharId}


            and a.order_no like CONCAT('%',#{orderNo},'%')


            and a.assign_user_id = #{assignUserId}


            and a.window_id = #{windowId}


            and a.status_code = #{status}











			and DATE_FORMAT(a.assign_time,'%Y-%m-%d') = #{queryDate}


            and a.dept_id=#{deptId}

        order by a.assign_time desc

select a.id,a.visit_id,a.item_name itemName,a.spec,(case a.item_code when '1' then (select prep_code from base_drug where id=a.item_id and a.item_code='1' and hosp_code=a.hosp_code) else '' end) prepCode, a.price,a.split_price splitPrice,a.unit_code unitCode,a.item_code itemCode,a.item_id itemId,ifnull(a.num,0) num,a.total_price total, (select ifnull(sum(split_num),0) from stro_stock where item_code=a.item_code and item_id=a.item_id and biz_id=a.phar_id and hosp_code=a.hosp_code) stockNum, (select ifnull(stock_occupy,0) from stro_stock where item_code=a.item_code and item_id=a.item_id and biz_id=a.phar_id and hosp_code=a.hosp_code) stockOccupy, (select bed_name from inpt_visit where id=a.visit_id and hosp_code=a.hosp_code) bedName, (select bd.seq_no from base_bed bd where bd.visit_id=a.visit_id and bd.hosp_code=a.hosp_code and bd.status_code = '1') seqNo, (select name from inpt_visit where id=a.visit_id and hosp_code=a.hosp_code) name,ifnull(a.dosage,0) dosage,a.dosage_unit_code dosageUnitCode, (concat(a.name, '/',(ifnull((case a.gender_code when '1' then '男' when '2' then '女' when '0' then '未知' when '9' then '未说明的性别' else '' end),'岁') ), '/',a.age, (ifnull((case a.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '周' else '' end),'')))) patientInfo, a.split_unit_code splitUnitCode,ifnull(a.splitNum,0) splitNum,a.split_ratio splitRatio, (select name from base_product e join base_drug f on f.prod_code=e.code where e.hosp_code=a.hosp_code and f.hosp_code=a.hosp_code and f.id=a.item_id) prodName, case a.item_code when '1' then (select f.nation_code from base_drug f where a.item_id = f.id and f.hosp_code=a.hosp_code) when '2' then (select f.nation_code from base_material f where a.item_id = f.id and f.hosp_code=a.hosp_code) end nationCode, case a.item_code when '1' then (select f.nation_name from base_drug f where a.item_id = f.id and f.hosp_code=a.hosp_code) when '2' then (select f.nation_name from base_material f where a.item_id = f.id and f.hosp_code=a.hosp_code) end nationName, a.in_no from ( select a.id,a.receive_id,a.item_name,a.spec, a.price,max(a.split_price) split_price,a.unit_code,a.item_code,a.item_id,a.total_price, a.hosp_code,b.phar_id,a.visit_id,c.gender_code,c.age_unit_code,a.dosage,c.name,c.age ,sum(a.num) num,a.split_unit_code,ifnull(sum(a.split_num), 0) splitNum,a.split_ratio,a.dosage_unit_code, c.in_no from phar_in_receive_detail a join phar_in_receive b on a.receive_id=b.id and a.hosp_code = b.hosp_code join inpt_visit c on a.visit_id=c.id and a.hosp_code = c.hosp_code where a.hosp_code=#{hospCode}
and a.receive_id=#{receiveId}
and b.window_id=#{windowId}
group by a.receive_id,a.item_name,a.spec,a.price,a.unit_code,a.item_code,a.item_id,a.id, a.hosp_code,b.phar_id,a.visit_id,c.gender_code,c.age_unit_code,a.dosage,c.name,c.age,a.split_unit_code,a.split_ratio,a.dosage_unit_code ) a order by seqNo asc,patientInfo

SELECT a.receive_id, a.item_name AS itemName, a.spec, a.price, a.unit_code AS unitCode, a.item_code AS itemCode, a.item_id AS itemId, a.hosp_code, b.phar_id, a.visit_id AS visitId, ifnull(a.num,0) num, ifnull(a.num*a.price,0) total, c.bed_name AS bedName, ifnull((select seq_no from base_bed where base_bed.visit_id=a.visit_id and hosp_code=a.hosp_code),0) seqNo, c.in_no AS inNo, (case a.item_code when '1' then (select code from base_drug where hosp_code=a.hosp_code and id=a.item_id ) else (select code from base_material where id=a.item_id and a.item_code='2' and hosp_code=a.hosp_code) end) code,
ifnull(a.dosage,0) AS dosage, ifnull( a.num, 0 ) num, a.split_unit_code AS splitUnitCode, ifnull( a.split_num, 0 ) AS splitNum, a.dosage_unit_code AS dosageUnitCode, ifnull(d.is_long, '' ) AS isLong, d.crte_id AS crteId, d.crte_name AS crteName, d.rate_id, br.`name` as rateName, stock.location_no AS locationNo, c.`name`, d.usage_code AS usageCode, c.in_disease_name AS inDiseaseName, c.patient_code AS patientCode, (concat(c.name, '/',(case c.gender_code when '0' then '未知' when '1' then '男' when '2' then '女' when '9' then '未说明' else '-' end), '/',c.age, (case c.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' else '天' end))) patientInfo FROM phar_in_receive_detail a JOIN phar_in_receive b ON a.hosp_code =b.hosp_code AND a.receive_id = b.id JOIN inpt_visit c ON a.hosp_code = c.hosp_code AND a.visit_id = c.id AND a.hosp_code = c.hosp_code LEFT JOIN inpt_advice d ON a.advice_id = d.id AND a.hosp_code = d.hosp_code left join base_rate br on d.rate_id = br.id left join stro_stock stock ON stock.hosp_code=a.hosp_code AND stock.item_code=a.item_code and stock.item_id=a.item_id and stock.biz_id=b.phar_id
WHERE a.hosp_code = #{hospCode}
and a.receive_id=#{receiveId}
and b.window_id=#{windowId}
order by seqNo asc, patientInfo

select c.*, ifnull((select num from stro_stock where item_id=c.itemId and item_code=c.itemCode and biz_id=c.phar_id and hosp_code=c.hosp_code),0) stockNum, (select unit_code from stro_stock where item_id=c.itemId and item_code=c.itemCode and biz_id=c.phar_id and hosp_code=c.hosp_code) stockUnionCode, ifnull((select stock_occupy from stro_stock where item_id=c.itemId and item_code=c.itemCode and biz_id=c.phar_id and hosp_code=c.hosp_code),0) stockOccupy, total * price as totalPrice from ( select a.item_code itemCode,a.item_id itemId,a.item_name itemName,a.spec,a.price,a.unit_code unitCode,b.phar_id,a.split_ratio splitRatio, ifnull(sum(a.dosage),0) dosage,ifnull(sum(a.num),0) total,a.hosp_code,ifnull(sum(a.split_num),0) splitNum,a.split_unit_code splitUnitCode, a.dosage_unit_code dosageUnitCode, (select name from base_product c join base_drug d on d.prod_code=c.code where c.hosp_code=a.hosp_code and d.hosp_code=a.hosp_code and d.id=a.item_id) prodName, case a.item_code when '1' then (select f.nation_code from base_drug f where a.item_id = f.id and f.hosp_code=a.hosp_code) when '2' then (select f.nation_code from base_material f where a.item_id = f.id and f.hosp_code=a.hosp_code) end nationCode, case a.item_code when '1' then (select f.nation_name from base_drug f where a.item_id = f.id and f.hosp_code=a.hosp_code) when '2' then (select f.nation_name from base_material f where a.item_id = f.id and f.hosp_code=a.hosp_code) end nationName from phar_in_receive_detail a join phar_in_receive b on a.receive_id=b.id where 1=1
and a.receive_id=#{receiveId}
group by a.item_code,a.item_id,a.item_name,a.spec,a.price,a.unit_code,b.phar_id,a.hosp_code,a.split_unit_code,a.split_ratio,a.dosage_unit_code ) c order by totalPrice

select c.* from phar_in_receive_detail a
join phar_in_wait_receive b on a.wr_id=b.id
join inpt_cost c on b.cost_id=c.id
where 1=1 and a.hosp_code=#{hospCode} and b.hosp_code=#{hospCode} and c.hosp_code=#{hospCode}

    and (a.id) in

         #{item.id}

update.foreach:
update phar_in_wait_receive set assign_user_id=#{item.assignUserId},assign_user_name=#{item.assignUserName},
assign_time=#{item.assignTime},status_code=#{item.statusCode}
where (id=#{item.wrId} or old_wr_id=#{item.wrId}) and hosp_code=#{item.hospCode}

update phar_in_wait_receive set dist_user_id=#{item.distUserId},dist_user_name=#{item.distUserName},
dist_time=#{item.distTime},status_code=#{item.statusCode}
where (id=#{item.wrId} or old_wr_id=#{item.wrId}) and hosp_code=#{item.hospCode}

mapper.update:
update inpt_cost set is_dist='1' where 1=1 and hosp_code=#{hospCode}

    and (id) in

        #{item.id}

mapper.select:
select distinct a.visit_id
from phar_in_receive_detail a
join base_drug b on b.id = a.item_id and b.big_type_code = '2' and a.hosp_code = b.hosp_code
left join phar_in_receive c on c.id = a.receive_id and a.hosp_code = c.hosp_code and c.phar_id = #{pharId}
where a.hosp_code = #{hospCode} and a.receive_id = #{id}
and item_code = '1'

select distinct a.visit_id
from phar_in_distribute_detail a
join base_drug b on b.id = a.item_id and b.big_type_code = '2' and a.hosp_code = b.hosp_code
left join phar_in_distribute c on c.id = a.distribute_id and a.hosp_code = c.hosp_code and c.phar_id = #{pharId}
where a.hosp_code = #{hospCode} and a.distribute_id = #{id}
and item_code = '1'

select a.*, (CONCAT(a.name,'/', (ifnull((case a.gender_code when '1' then '男' when '2' then '女' when '0' then '未知' when '9' then '未说明的性别' else '' end), '' )),'/', a.age, (ifnull((case a.age_unit_code when '1' then '岁' when '2' then '月' when '3' then '周' when '4' then '天' else '' end), '岁' )))) as patientInfo from inpt_visit a where a.hosp_code = #{hospCode}
and (a.id) in
#{item}

select a.*,pc.total_price as printTotalPrice from inpt_advice a
join phar_in_receive_detail pc on pc.advice_id = a.id and pc.hosp_code = a.hosp_code
and pc.receive_id = #{id} and pc.visit_id = #{visitId}
--       left join phar_in_receive b on pc.receive_id = b.id and pc.hosp_code = b.hosp_code
where a.hosp_code = #{hospCode}

select a.*,pc.total_price as printTotalPrice from inpt_advice a
join phar_in_distribute_detail pc on pc.distribute_id = a.id and pc.hosp_code = a.hosp_code
and pc.distribute_id = #{id} and pc.visit_id = #{visitId}
where a.hosp_code = #{hospCode}

update.foreach:
update inpt_cost a set a.distribute_all_detail_id = #{item.distributeAllDetailId}
 where a.hosp_code = #{item.hospCode}
 and a.id = (select b.cost_id from phar_in_wait_receive b where b.id = #{item.wrId} and b.hosp_code = #{item.hospCode})

mapper.delete:
delete from phar_in_receive_detail where hosp_code = #{hospCode} and receive_id = #{receiveId}

  and id in

    #{item}



  and item_id in

    #{item}

