mapper.select:
select a.visit_id visitId,b.name name,b.gender_code genderCode,b.age_unit_code ageUnitCode, b.cert_no certNo,b.age age,a.total_price totalPrice,a.id id,a.assign_user_name assignUserName,DATE_FORMAT(a.assign_time, '%Y-%m-%d %H:%i:%s') assignTimeStr, (select name from base_window where id=a.window_id) windowName, a.crte_name crteName,DATE_FORMAT(a.crte_time, '%Y-%m-%d %H:%i:%s') createTime,c.settle_no settleNo,a.status_code statusCode, (concat(b.name, '/',(ifnull((select e.name from sys_code_detail e where e.value=b.gender_code and e.c_code='XB'),'') ), '/',ifnull(b.age,''), (ifnull((select name from sys_code_detail where c_code='NLDW' and value=b.age_unit_code and hosp_code=b.hosp_code),'岁')))) patientInfo,b.visit_no visitNo, ( select d.name from base_disease d join outpt_diagnose e on d.id=e.disease_id where e.visit_id = b.id and d.hosp_code=b.hosp_code and e.hosp_code=b.hosp_code limit 1 ) as diseaseName, b.patient_code patientCode,b.insure_no insureNo,b.out_profile outProfile,b.phone phone,b.doctor_name doctorName,b.dept_name deptName from phar_out_receive a join outpt_visit b on a.visit_id=b.id join outpt_settle c on a.settle_id=c.id where 1=1 and a.hosp_code=#{hospCode} and b.hosp_code=#{hospCode} and c.hosp_code=#{hospCode}
and a.phar_id=#{pharId}
and a.window_id=#{windowId}
and a.assign_user_id=#{assignUserId}
and a.status_code=#{status}
and a.status_code = '2'
and ((c.settle_no like CONCAT('%',#{settleNo},'%')) or (b.name like CONCAT('%',#{settleNo},'%')) or (b.visit_no like CONCAT('%',#{settleNo},'%')))
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') = DATE_FORMAT(#{queryDate},'%Y-%m-%d')
and DATE_FORMAT(a.assign_time,'%Y-%m-%d') = DATE_FORMAT(#{queryDate},'%Y-%m-%d')
and (DATE_FORMAT(a.assign_time,'%Y-%m-%d') = DATE_FORMAT(#{queryDate},'%Y-%m-%d') or DATE_FORMAT(a.crte_time,'%Y-%m-%d') = DATE_FORMAT(#{queryDate},'%Y-%m-%d'))
order by a.assign_time desc,a.crte_time desc

select a.visit_id visitId,b.name name,b.gender_code genderCode,b.age_unit_code ageUnitCode, b.cert_no certNo,b.age age,a.total_price totalPrice,a.id id,a.dist_user_name distUserName,DATE_FORMAT(a.dist_time, '%Y-%m-%d %H:%i:%s') distTimeStr, (select name from base_window where id=a.window_id) windowName,a.assign_user_name assignUserName,DATE_FORMAT(a.assign_time, '%Y-%m-%d %H:%i:%s') assignTimeStr, a.crte_name crteName,DATE_FORMAT(a.crte_time, '%Y-%m-%d %H:%i:%s') createTime,c.settle_no settleNo, (concat(b.name, '/',(ifnull((select e.name from sys_code_detail e where e.value=b.gender_code and e.c_code='XB'),'') ), '/',ifnull(b.age,''), (ifnull((select name from sys_code_detail where c_code='NLDW' and value=b.age_unit_code and hosp_code=b.hosp_code),'岁')))) patientInfo,b.visit_no visitNo, ( select d.name from base_disease d join outpt_diagnose e on d.id=e.disease_id where e.visit_id = b.id and d.hosp_code=b.hosp_code and e.hosp_code=b.hosp_code limit 1 ) as diseaseName, b.patient_code patientCode,b.insure_no insureNo,b.out_profile outProfile,b.phone phone from phar_out_distribute a join outpt_visit b on a.visit_id=b.id join outpt_settle c on a.settle_id=c.id where 1=1 and a.hosp_code=#{hospCode} and b.hosp_code=#{hospCode} and c.hosp_code=#{hospCode} and a.status_code='0'
and a.phar_id=#{pharId}
and a.window_id=#{windowId}
and a.assign_user_id=#{assignUserId}
and DATE_FORMAT(a.dist_time,'%Y-%m-%d') = DATE_FORMAT(#{queryDate},'%Y-%m-%d')
and ((c.settle_no like CONCAT('%',#{settleNo},'%')) or (b.name like CONCAT('%',#{settleNo},'%')) or (b.visit_no like CONCAT('%',#{settleNo},'%')))
order by a.dist_time desc,a.crte_time desc

select a.item_name itemName,a.spec spec,a.unit_code unitCode,a.dosage dosage,a.dosage_unit_code dosageUnitCode, a.num num,a.price price,a.total_price totalPrice,a.split_ratio splitRatio,a.split_num splitNum,a.split_unit_code splitUnitCode, a.usage_code,a.split_price as splitPrice, (select name from base_dept where id=b.phar_id and hosp_code=b.hosp_code) bizName, (select num from stro_stock where item_code=a.item_code and item_id=a.item_id and biz_id=b.phar_id and hosp_code=a.hosp_code) stockNum, (select name from base_product e join base_drug f on f.prod_code=e.code where e.hosp_code=a.hosp_code and f.hosp_code=a.hosp_code and f.id=a.item_id) prodName, opd.op_id opId,opd.group_no groupNo,opd.speed_code as speedCode, opd.use_days useDays, b.assign_time assignTime, b.assign_user_name assignUserName,d.type_code typeCode, br.name as rateName,e.big_type_code, br.daily_times as dailyTimes, d.herb_num as chineseDrugNum, br.remark as rateRemark, case when e.big_type_code='2' then d.herb_num else opd.use_days end useDays, case a.item_code when '1' then e.nation_code when '2' then bm.nation_code else '' end nationCode, case a.item_code when '1' then e.nation_name when '2' then bm.nation_name else '' end nationName from phar_out_receive_detail a join phar_out_receive b on a.or_id=b.id left join base_window c on b.window_id=c.id and c.hosp_code=#{hospCode} left join outpt_prescribe d on a.op_id=d.id and d.hosp_code=#{hospCode} left join base_drug e on a.hosp_code=e.hosp_code and a.item_code='1' and a.item_id=e.id left join base_material bm on a.hosp_code=bm.hosp_code and a.item_code='2' and a.item_id=bm.id left join outpt_prescribe_detail opd on a.opd_id = opd.id and a.hosp_code = opd.hosp_code left join base_rate br on opd.rate_id = br.id and a.hosp_code = br.hosp_code where 1=1 and a.hosp_code=#{hospCode} and b.hosp_code=#{hospCode}
and a.or_id = #{orId}
and c.id = #{windowId}
and d.order_no = #{orderNo}
and opd.usage_code in
#{item}

select value
from sys_parameter
where is_valid = '1'
  and hosp_code = #{hospCode}
  and code = #{code}

select a.item_name itemName,a.spec spec,a.unit_code unitCode,a.dosage dosage,a.dosage_unit_code dosageUnitCode, a.num num,a.price price,a.total_price totalPrice,a.split_ratio splitRatio,a.split_num splitNum,a.split_unit_code splitUnitCode, (select name from base_dept where id=b.phar_id and hosp_code=b.hosp_code) bizName,a.usage_code, (select name from base_product e join base_drug f on f.prod_code=e.code where e.hosp_code=a.hosp_code and f.hosp_code=a.hosp_code and f.id=a.item_id) prodName, case a.item_code when '1' then e.nation_code when '2' then (select f.nation_code from base_material f where a.item_id = f.id and f.hosp_code=a.hosp_code) end nationCode, case a.item_code when '1' then e.nation_name when '2' then (select f.nation_name from base_material f where a.item_id = f.id and f.hosp_code=a.hosp_code) end nationName, br.name as rateName, br.daily_times as dailyTimes, br.remark as rateRemark, d.herb_num as chineseDrugNum, opd.speed_code as speedCode, opd.group_no groupNo, opd.is_skin as isSkin, opd.use_days as useDays, d.doctor_name as doctorName from phar_out_distribute_all_detail a join phar_out_distribute b on a.distribute_id=b.id left join outpt_prescribe_detail opd on a.opd_id = opd.id and a.hosp_code = opd.hosp_code left join base_window c on b.window_id=c.id and c.hosp_code=#{hospCode} left join outpt_prescribe d on a.op_id=d.id and d.hosp_code=#{hospCode} left join base_drug e on a.hosp_code=e.hosp_code and a.item_code='1' and a.item_id=e.id left join base_rate br on opd.rate_id = br.id and a.hosp_code = br.hosp_code where 1=1 and a.hosp_code=#{hospCode} and b.hosp_code=#{hospCode} and a.num > 0
and a.distribute_id = #{orId}
and c.id = #{windowId}
and d.order_no = #{orderNo}
and opd.usage_code in
#{item}

select a.id, a.hosp_code, a.distribute_id, a.ir_id, a.ird_id, a.advice_id, a.group_no, a.visit_id, a.baby_id, a.item_code, a.item_id, a.item_name, a.num, a.price, a.unit_code, a.spec, a.dosage, a.dosage_unit_code, a.total_price, a.split_ratio, a.split_unit_code, a.split_num, a.split_price, a.chinese_drug_num, a.usage_code, a.curr_unit_code,a.crte_id,a.crte_name,a.crte_time,a.old_dist_id, (CONCAT(c.name,'/', (ifnull(( SELECT e.NAME FROM sys_code_detail e WHERE e.VALUE = c.gender_code and e.hosp_code = c.hosp_code AND e.c_code = 'XB' ), '' )),'/', ifnull(c.age,''), (ifnull(( SELECT NAME FROM sys_code_detail WHERE c_code = 'NLDW' AND VALUE = c.age_unit_code AND hosp_code = c.hosp_code ), '岁' )))) as patientInfo, (select bd.name from base_dept bd where bd.id = b.dept_id and bd.hosp_code = b.hosp_code) as deptName, c.status_code, case when a.item_code = '1' then bd.code when a.item_code = '2' then d.code end code, (case when a.item_code = '1' then bd.type_code when a.item_code = '2' then d.type_code end) typeCode, CASE a.item_code WHEN '1' THEN ( SELECT NAME FROM base_product WHERE CODE = bd.prod_code AND hosp_code = bd.hosp_code ) WHEN '2' THEN ( SELECT NAME FROM base_product WHERE CODE = d.prod_code AND hosp_code = d.hosp_code ) END prodName from phar_in_distribute_all_detail a left join phar_in_distribute pid on a.distribute_id = pid.id and a.hosp_code = pid.hosp_code left join inpt_advice b on a.advice_id = b.id and a.hosp_code = b.hosp_code join inpt_visit c on c.id = a.visit_id and c.hosp_code = a.hosp_code left join base_drug bd on a.item_code='1' and bd.id=a.item_id and bd.hosp_code=a.hosp_code left join base_material d on a.item_code='2' and d.id=a.item_id and d.hosp_code=a.hosp_code where a.hosp_code = #{hospCode} and a.num > 0
and (a.item_name like concat('%',#{keyword},'%') or c.name like concat('%',#{keyword},'%') or bd.good_pym like concat('%',#{keyword},'%') or bd.good_wbm like concat('%',#{keyword},'%') or bd.usual_pym like concat('%',#{keyword},'%') or bd.usual_wbm like concat('%',#{keyword},'%') or d.pym like concat('%',#{keyword},'%') or d.wbm like concat('%',#{keyword},'%'))
and b.dept_id = #{deptId}
and pid.phar_id = #{pharId}
and date(a.crte_time) >= date(#{startTime})
and date(a.crte_time) <= date(#{endTime})
and c.status_code = #{statusCode}
order by a.crte_time desc
