mapper.select:
select
  id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, num, unit_code, price, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id, crte_id, crte_name, crte_time
from phar_in_wait_receive
where hosp_code = #{hospCode}
and id = #{id}

select
  id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, num, unit_code, price, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id, crte_id, crte_name, crte_time
from phar_in_wait_receive
where hosp_code = #{hospCode}
and item_id = #{itemId}

select
id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, unit_code, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id,curr_unit_code,
(select crte_time from inpt_cost where inpt_cost.id = cost_id) as costTime,
case curr_unit_code = unit_code when true then num else split_num end as num,
case curr_unit_code = unit_code when true then price else split_price end as price
from phar_in_wait_receive
where
  hosp_code = #{hospCode}

    and id = #{id}


    and advice_id = #{adviceId}


    and group_no = #{groupNo}


    and visit_id = #{visitId}


    and baby_id = #{babyId}


    and item_code = #{itemCode}


    and item_id = #{itemId}


    and item_name = #{itemName}


    and spec = #{spec}


    and dosage = #{dosage}


    and dosage_unit_code = #{dosageUnitCode}


    and num = #{num}


    and unit_code = #{unitCode}


    and price = #{price}


    and split_ratio = #{splitRatio}


    and split_unit_code = #{splitUnitCode}


    and split_num = #{splitNum}


    and split_price = #{splitPrice}


    and total_price = #{totalPrice}


    and chinese_drug_num = #{chineseDrugNum}


    and status_code = #{statusCode}


    and phar_id = #{pharId}


    and assign_user_id = #{assignUserId}


    and assign_user_name = #{assignUserName}


    and assign_time = #{assignTime}


    and dist_user_id = #{distUserId}


    and dist_user_name = #{distUserName}


    and dist_time = #{distTime}


    and usage_code = #{usageCode}


    and use_code = #{useCode}


    and dept_id = #{deptId}


    and is_emergency = #{isEmergency}


    and is_back = #{isBack}


    and cost_id = #{costId}


    and old_wr_id = #{oldWrId}


    and crte_id = #{crteId}


    and crte_name = #{crteName}


    and crte_time = #{crteTime}

  order by crte_time desc

select id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, num, unit_code, price, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id, crte_id, crte_name, crte_time,curr_unit_code from phar_in_wait_receive where hosp_code = #{hospCode}
and cost_id in
#{id}
and num > 0
and id in
#{id}

select b.id, b.hosp_code, b.advice_id, b.group_no, b.visit_id, b.baby_id, b.item_code, b.item_id, b.item_name, b.spec, b.dosage, b.dosage_unit_code, b.num, b.unit_code, b.price, b.split_ratio, b.split_unit_code, b.split_num, b.split_price, b.total_price, b.chinese_drug_num, b.status_code, b.phar_id, b.assign_user_id, b.assign_user_name, b.assign_time, b.dist_user_id, b.dist_user_name, b.dist_time, b.usage_code, b.use_code, b.dept_id, b.is_emergency, b.is_back, a.cost_id, b.old_wr_id, b.crte_id, b.crte_name, b.crte_time from phar_in_wait_receive a join phar_in_wait_receive b on a.hosp_code=b.hosp_code and a.old_wr_id=b.id where a.hosp_code = #{hospCode}
and a.cost_id in
#{id}

select id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, num, unit_code, price, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id, crte_id, crte_name, crte_time from phar_in_wait_receive where hosp_code = #{hospCode} and is_back ='1'
and cost_id in
#{id}

mapper.delete:
delete from phar_in_wait_receive where hosp_code = #{hospCode}
and hosp_code = #{hospCode}
and id = #{id}

mapper.select:
SELECT tab.dept_id,tab.deptName,ifnull(sum(tab.num),0) num FROM ( SELECT e.dept_id,sum(e.num) num, (select t.name from base_dept t where t.hosp_code = e.hosp_code and t.id = e.dept_id) deptName FROM phar_in_wait_receive e join inpt_cost c on e.cost_id = c.id and e.hosp_code=c.hosp_code WHERE e.hosp_code = #{hospCode} AND e.is_back = '1' AND e.status_code='3' AND e.phar_id = #{crteId}
and c.back_code=#{backCode}
AND c.is_ok='0'
AND e.crte_time >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND e.crte_time < date_add(STR_TO_DATE(#{endDate},'%Y-%m-%d'),interval 1 day)
and e.dept_id=#{deptId}
GROUP BY e.dept_id,e.item_id )tab GROUP BY tab.dept_id

SELECT e.item_id, max(e.item_name) item_name, max(e.spec) spec, max(e.dosage) dosage, max(e.price) price, max(e.split_price) split_price, max(e.curr_unit_code) curr_unit_code, max(e.unit_code) unit_code, max(e.split_unit_code) split_unit_code, sum( e.num ) num, sum(e.split_num) split_num, abs(sum(e.split_num)) as showSplitNum, abs(sum(e.total_price)) total_price, abs(sum( e.num )) showNum, max(e.crte_name) crte_name, case when max(e.item_code)='1' then bd.code when max(e.item_code)='2' then bm.code end drugCode FROM phar_in_wait_receive e join inpt_cost c on e.cost_id = c.id and e.hosp_code=c.hosp_code left join base_drug bd on bd.id = e.item_id and bd.hosp_code = #{hospCode} left join base_material bm on bm.id = e.item_id and bm.hosp_code = #{hospCode} WHERE e.hosp_code = #{hospCode} AND e.is_back = '1' AND e.status_code='3' AND e.phar_id = #{crteId}
and c.back_code=#{backCode}
AND c.is_ok='0'
AND e.crte_time >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND e.crte_time < date_add(STR_TO_DATE(#{endDate},'%Y-%m-%d'),interval 1 day)
AND e.dept_id = #{deptId} GROUP BY e.item_id,e.curr_unit_code

SELECT e.id, e.crte_time, e.crte_name, v.name visitName, e.item_name, e.spec, e.price, e.split_price, e.curr_unit_code curr_unit_code, e.unit_code unit_code, e.split_unit_code split_unit_code, e.num, e.split_num, abs(e.total_price) total_price, abs(e.split_num) as showSplitNum, abs(e.num) showNum, v.in_no, v.name, v.bed_name, case when e.item_code='1' then bd.code when e.item_code='2' then bm.code end drugCode FROM phar_in_wait_receive e join inpt_cost c on e.cost_id = c.id and e.hosp_code=c.hosp_code left join base_drug bd on bd.id = e.item_id and bd.hosp_code = e.hosp_code left join base_material bm on bm.id = e.item_id and bm.hosp_code = e.hosp_code left join inpt_visit v on v.id = e.visit_id and v.hosp_code = e.hosp_code WHERE e.hosp_code = #{hospCode} AND e.is_back = '1' AND e.status_code='3' AND e.phar_id = #{crteId}
and c.back_code=#{backCode}
AND c.is_ok='0'
AND e.crte_time >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND e.crte_time < date_add(STR_TO_DATE(#{endDate},'%Y-%m-%d'),interval 1 day)
AND e.dept_id = #{deptId} order by e.crte_time desc

select id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, num, unit_code, price, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id, crte_id, crte_name, crte_time, (select status_code from phar_in_wait_receive ie where ie.hosp_code = e.hosp_code and ie.id = e.old_wr_id) oldStatusCode,curr_unit_code from phar_in_wait_receive e where hosp_code = #{hospCode} and status_code='3' and id in
#{id}
AND dept_id = #{deptId} AND is_back = '1'

select e.id, e.hosp_code, e.advice_id, e.group_no, e.visit_id, e.baby_id, e.item_code, e.item_id, e.item_name, e.spec, e.dosage, e.dosage_unit_code , e.num, e.unit_code, e.price, e.split_ratio, e.split_unit_code, e.split_num, e.split_price, e.total_price, e.chinese_drug_num, e.status_code , e.phar_id, e.assign_user_id, e.assign_user_name, e.assign_time, e.dist_user_id, e.dist_user_name, e.dist_time, e.usage_code, e.use_code , e.dept_id, e.is_emergency, e.is_back, e.cost_id, e.old_wr_id, e.crte_id, e.crte_name, e.crte_time, (select status_code from phar_in_wait_receive ie where ie.hosp_code = e.hosp_code and ie.id = e.old_wr_id) oldStatusCode,curr_unit_code from phar_in_wait_receive e join inpt_cost f on e.hosp_code=f.hosp_code and e.cost_id=f.id where e.hosp_code = #{hospCode} and e.status_code='3' and e.is_back='1' and f.back_code='1' and f.is_ok='0' and e.item_id in
#{itemId}
AND e.dept_id = #{deptId}

