mapper.select:
select
id, hosp_code, advice_id, group_no, visit_id, baby_id, item_code, item_id, item_name, spec, dosage, dosage_unit_code, num, unit_code, price, split_ratio, split_unit_code, split_num, split_price, total_price, chinese_drug_num, status_code, phar_id, assign_user_id, assign_user_name, assign_time, dist_user_id, dist_user_name, dist_time, usage_code, use_code, dept_id, is_emergency, is_back, cost_id, old_wr_id, crte_id, crte_name, crte_time,
(select status_code from phar_in_wait_receive ie where ie.hosp_code = e.hosp_code and ie.id = e.old_wr_id) oldStatusCode
from phar_in_wait_receive e
where hosp_code = #{hospCode}
and item_code = #{itemCode}
and item_id = #{itemId}
and advice_id = #{adviceId}
and visit_id = #{visitId}

select a.*, b.ph_code as phCode, b.is_lvp as isLvp, b.prep_code as prepCode, b.big_type_code as bigTypeCode, c.bed_name as bedName, c.name as name, d.name as pahrName, e.dosage as adviceDosage, e.usage_code as usageCode, e.crte_name as tellName, e.exec_name as execName, e.dosage as adviceDosage, bp.name as productName, b.spec, (select f.name from base_rate f where f.id = e.rate_id) as rateName, if(e.is_long = '1','临时','长期') as adviceType, -- 医嘱类型 ifnull((select location_no from stro_stock where item_id= a.item_id and item_code= a.item_code and biz_id=a.phar_id and hosp_code=a.hosp_code),0) locationNo from phar_in_wait_receive a left join base_drug b on a.item_id = b.id left join base_product bp on b.prod_code = bp.code left join inpt_visit c on a.visit_id = c.id left join base_dept d on a.phar_id = d.id left join inpt_advice e on a.advice_id = e.id where a.hosp_code = #{hospCode} and c.status_code in ('2','3','4')
and a.dept_id = #{deptId}
and a.status_code = #{statusCode}
and a.is_back = #{isBack}
and a.is_emergency = #{isEmergency}
/*用于紧急领药中查询长期或临时的医嘱类型*/
and find_in_set(e.is_long,#{isLong})
/*仅查询出提前领药的待领数据*/
and a.advance_id is not null and a.advance_id != ''
and a.visit_id in
#{id}

update.foreach:
update phar_in_wait_receive
set cost_id = #{dto.id}
where hosp_code = #{dto.hospCode}
and cost_id = #{dto.oldCostId}

