mapper.select:
select
so.id, so.hosp_code, so.out_code, so.order_no,so.out_stock_id,so.in_stock_id, so.buy_price_all, so.sell_price_all, so.remark,so.audit_code,
so.audit_id, so.audit_name, so.audit_time, so.ok_audit_code, so.ok_audit_id, so.ok_audit_name, so.ok_audit_time, so.crte_id, so.crte_name, so.crte_time,
base_dept.name as outDeptName,sys_code_detail.name as djlxName
from stro_out so
left join base_dept on so.in_stock_id = base_dept.id and  base_dept.hosp_code = so.hosp_code
left join sys_code_detail on so.out_code = sys_code_detail.value and sys_code_detail.hosp_code = so.hosp_code and c_code='CRFS'
where so.hosp_code = #{hospCode}









  and so.out_code = #{outCode}


  and so.out_code in ('4','5','10')


  and so.order_no like concat('%',#{orderNo},'%')


    and so.order_no like concat('%',#{orderNo},'%')

/*开始日期*/

    and so.crte_time >= #{startDate}

/*结束日期*/

    and so.crte_time <= DATE_FORMAT(DATE_ADD(#{endDate},INTERVAL 1 DAY),'%Y-%m-%d')


    and so.out_stock_id =#{outStockId}


   and so.in_stock_id =#{inStockId}


    and so.audit_code =#{auditCode}


    and so.ok_audit_code=#{flag}

order by so.audit_time desc

mapper.update:
update stro_out set ok_audit_code = #{okAuditCode},ok_audit_id=#{okAuditId},ok_audit_name=#{okAuditName},
ok_audit_time=#{okAuditTime}, in_stock_id =#{inStockId}
where id in

    #{item}

and hosp_code = #{hospCode}

mapper.select:
select id,ok_audit_code,order_no,out_code from stro_out
where id in

    #{item}

and hosp_code=#{hospCode}
and audit_code=#{auditCode}

select id,hosp_code, out_id, item_code, item_id, item_name, spec, num, unit_code, dosage, dosage_unit_code, buy_price, sell_price, buy_price_all, sell_price_all, split_ratio, split_num, split_price, split_unit_code, expiry_date, batch_no as batchNo, expiry_date, #{okAuditId} as crteId,#{okAuditName} as crteName, (select stro_out.in_stock_id from stro_out where out_id = stro_out.id and stro_out.hosp_code = #{hospCode} and stro_out.out_code = #{outCode}) as biz_id, (select stro_out.order_no from stro_out where out_id = stro_out.id and stro_out.hosp_code = #{hospCode} and stro_out.out_code = #{outCode}) as orderNo, ( case stro_out_detail.item_code when '1' then (SELECT base_drug.prep_code FROM base_drug WHERE base_drug.id = item_id and base_drug.hosp_code = #{hospCode}) end ) as prepCode, (select dp.name from base_dept dp where id = (select stro_out.in_stock_id from stro_out where out_id = stro_out.id and stro_out.hosp_code = #{hospCode} and stro_out.out_code = #{outCode}) and dp.hosp_code = #{hospCode}) as invoicingTargetName, (select stro_out.in_stock_id from stro_out where out_id = stro_out.id and stro_out.hosp_code = #{hospCode} and stro_out.out_code = #{outCode}) as invoicingTargetId from stro_out_detail
and hosp_code = #{hospCode}
and out_id in
#{item}
