mapper.select:
select
  at.id, at.hosp_code, at.biz_id, at.order_no, at.before_price, at.after_price, at.remark, at.audit_id, at.audit_name, at.audit_code, at.audit_time, at.crte_id, at.crte_name, at.crte_time,
  (select name from base_dept dt where dt.id = at.biz_id and hosp_code = at.hosp_code) biz_name
from stro_adjust at
where  at.hosp_code = #{hospCode}
and at.id = #{id}

select
at.id, at.hosp_code, at.biz_id, at.order_no, at.before_price, at.after_price, at.remark, at.audit_id, at.audit_name, at.audit_code, at.audit_time, at.crte_id, at.crte_name, at.crte_time,
(select name from base_dept dt where dt.id = at.biz_id and hosp_code = at.hosp_code) biz_name
from stro_adjust at
where at.hosp_code = #{hospCode}

    and at.biz_id = #{bizId}


    and at.order_no like concat("%",#{orderNo},"%")


    and at.audit_code = #{auditCode}

/*开始日期*/

    and date(at.crte_time) >= date(#{startDate})

/*结束日期*/

    and date(at.crte_time) <= date(#{endDate})


  and at.id in

    #{id}


order by audit_code,crte_time desc

select at.id, at.hosp_code, at.biz_id, at.order_no, at.before_price, at.after_price, at.remark, at.audit_id, at.audit_name, at.audit_code, at.audit_time, at.crte_id, at.crte_name, at.crte_time, (select name from base_dept dt where dt.id = at.biz_id and hosp_code = at.hosp_code) biz_name from stro_adjust at where at.hosp_code = #{hospCode} and at.id in
#{id}
order by audit_code,crte_time

SELECT '21' as outCode, b.item_Id as itemId, CONCAT(b.item_name,':',bd.name) as itemName FROM `stro_out` a join stro_out_detail b on a.id = b.out_id left join base_dept bd on bd.id = a.in_stock_id where a.audit_code = '1' and a.ok_audit_code='0' and a.hosp_code= #{hospCode} and a.hosp_code = b.hosp_code and b.item_id in
#{item}
and a.out_code != '4'

mapper.update:
update stro_adjust
set audit_id = #{auditId},
audit_name = #{auditName},
audit_code = #{auditCode},
audit_time = #{auditTime}
where hosp_code = #{hospCode}
and id in

 #{id}

update.foreach:
update stro_adjust
set before_price = #{item.beforePrice},
    after_price = #{item.afterPrice}
where hosp_code = #{item.hospCode} and id  = #{item.id}
