mapper.select:
select
  id, hosp_code, in_code, order_no, stock_id, supplier_id, buy_price_all, sell_price_all, remark, audit_code, audit_id, audit_name, audit_time, crte_id, crte_name, crte_time,
  (select b.name from base_supplier b where b.id = supplier_id and b.hosp_code = #{hospCode}) as name
from stro_in
where
    hosp_code = #{hospCode}

      and in_code = #{inCode}


      and id = #{id}


      and order_no = #{orderNo}


      and stock_id = #{stockId}


      and supplier_id = #{supplierId}


      and buy_price_all = #{buyPriceAll}


      and sell_price_all = #{sellPriceAll}


      and remark = #{remark}


      and audit_code = #{auditCode}

    /*开始日期*/

      and date(crte_time) >= date(#{startDate})

    /*结束日期*/

      and date(crte_time) <= date(#{endDate})


      and (order_no like concat('%',#{keyword},'%') or
      supplier_id in (select b.id from base_supplier b where (b.name like concat('%',#{keyword},'%')
      or b.abbr_pym like concat('%',#{keyword},'%') or b.abbr_wbm like concat('%',#{keyword},'%')
      or b.name_pym like concat('%',#{keyword},'%') or b.name_wbm like concat('%',#{keyword},'%')) and b.hosp_code = #{hospCode})              )


      and id in

        #{id}


    order by crte_time desc

mapper.update:
update stro_in

  audit_code = #{auditCode},

    audit_id = #{auditId},


    audit_name = #{auditName},


    audit_time = #{auditTime}


where hosp_code = #{hospCode} and audit_code = '0'

  and in_code = #{inCode}


  and id in

    #{id}

mapper.select:
select
a.id, a.hosp_code, a.in_id, a.item_code, a.item_id, a.item_name, a.spec, a.num, a.unit_code, a.dosage, a.dosage_unit_code, a.buy_price, a.sell_price, a.buy_price_all, a.sell_price_all, a.split_ratio, a.split_num, a.split_price, a.split_unit_code, a.batch_no, a.expiry_date,a.ndan,
a.prod_code,
(select p.name from base_product p where p.hosp_code = #{hospCode} and a.prod_code = p.code) as prodName,
case when a.item_code='1' then bd.code when a.item_code='2' then bm.code end code,
case when a.item_code='2' then bm.model end model
from stro_in_detail a
left join base_drug bd on bd.id = a.item_id and bd.hosp_code = #{hospCode}
left join base_material bm on bm.id = a.item_id and bm.hosp_code = #{hospCode}

  and a.hosp_code = #{hospCode}

    and a.id = #{id}


    and a.in_id = #{inId}


    and a.item_code = #{itemCode}


    and a.item_id = #{itemId}


    and (
       a.item_name like concat('%',#{itemName},'%')
       or bd.usual_pym like concat('%',#{itemName},'%')
       or bd.good_pym like concat('%',#{itemName},'%')
       or bd.good_name like concat('%',#{itemName},'%')
       or bd.usual_name like concat('%',#{itemName},'%')
       or bm.name like concat('%',#{itemName},'%')
       or bm.pym like concat('%',#{itemName},'%')
    )

select id, hosp_code,item_code, item_id, item_name, spec, num, unit_code, buy_price, sell_price, buy_price_all, sell_price_all, split_ratio, split_num, split_price, split_unit_code, batch_no as batchNo, expiry_date, #{auditId} as crteId,#{auditName} as crteName,prod_code,ndan, (select b.stock_id from stro_in b where in_id = b.id and b.hosp_code = #{hospCode} and b.in_code = #{inCode}) as biz_id, (select dp.name from base_dept dp where dp.id = (select b.stock_id from stro_in b where in_id = b.id and b.hosp_code = #{hospCode} and b.in_code = #{inCode}) and dp.hosp_code = #{hospCode} ) as invoicingTargetName, (select b.stock_id from stro_in b where in_id = b.id and b.hosp_code = #{hospCode} and b.in_code = #{inCode}) as invoicingTargetId, (select c.order_no from stro_in c where in_id = c.id and c.hosp_code = #{hospCode} and c.in_code = #{inCode}) as orderNo, ( case stro_in_detail.item_code when '1' then (SELECT d.prep_code FROM base_drug d WHERE d.id = item_id and d.hosp_code = #{hospCode}) end ) as prepCode from stro_in_detail where hosp_code = #{hospCode}
and in_id in
#{id}

select id, hosp_code,item_code, item_id, item_name, spec, num, unit_code, buy_price, sell_price, buy_price_all, sell_price_all, split_ratio, split_num, split_price, split_unit_code, batch_no as batchNo, expiry_date, #{auditId} as crteId,#{auditName} as crteName,prod_code,ndan, (select b.stock_id from stro_in b where in_id = b.id and b.hosp_code = #{hospCode} and b.in_code = #{inCode}) as biz_id, (select dp.name from base_supplier dp where dp.id = (select b.supplier_id from stro_in b where in_id = b.id and b.hosp_code = #{hospCode} and b.in_code = #{inCode}) and dp.hosp_code = #{hospCode}) as invoicingTargetName, (select b.supplier_id from stro_in b where in_id = b.id and b.hosp_code = #{hospCode} and b.in_code = #{inCode}) as invoicingTargetId, (select c.order_no from stro_in c where in_id = c.id and c.hosp_code = #{hospCode} and c.in_code = #{inCode}) as orderNo, ( case stro_in_detail.item_code when '1' then (SELECT d.prep_code FROM base_drug d WHERE d.id = item_id and d.hosp_code = #{hospCode}) end ) as prepCode from stro_in_detail where hosp_code = #{hospCode}
and in_id in
#{id}

mapper.delete:
delete from stro_in_detail where
 hosp_code = #{hospCode}

  and id in

    #{id}

mapper.select:
select a.id as itemId, a.last_buy_price, a.hosp_code, a.type_code, a.bfc_code, a.code,a.good_name as itemName, a.spec, a.prod_code, a.price, a.unit_code,a.split_ratio, a.split_unit_code, a.split_price as splitPrice, a.is_valid, a.crte_id, a.crte_name, a.crte_time,"1" as itemCode,a.dosage, a.dosage_unit_code,a.ndan,a.dan, (select name from base_product c where a.prod_code = c.code and c.hosp_code = #{hospCode}) as prodName from base_drug a where a.hosp_code = #{hospCode}
and a.is_valid = #{isValid}
and big_type_code in
#{item}
and (a.good_name like concat('%',#{keyword},'%') or a.usual_name like concat('%',#{keyword},'%') or a.good_pym like concat('%',#{keyword},'%') or a.good_wbm like concat('%',#{keyword},'%') or a.usual_pym like concat('%',#{keyword},'%') or a.usual_wbm like concat('%',#{keyword},'%'))

select a.id as itemId, a.last_buy_price,a.hosp_code, a.type_code, a.bfc_code, a.code, a.name as itemName, a.spec, a.price, a.unit_code, a.split_ratio, a.split_unit_code, a.split_price, a.use_code, a.remark, a.prod_code, a.pym, a.wbm, a.is_valid, a.crte_id, a.crte_name, a.crte_time, "2" as itemCode,a.model, (select name from base_product c where a.prod_code = c.code and c.hosp_code = #{hospCode}) as prodName from base_material a where a.hosp_code = #{hospCode}
and a.type_code = #{typeCode}
and a.bfc_code like concat('%',#{bfcCode},'%')
and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))
and a.is_valid = #{isValid}

select a.id as itemId, a.last_buy_price, a.hosp_code, a.type_code, a.bfc_code, a.code,a.good_name as itemName, a.spec, a.prod_code, a.price, a.unit_code,a.split_ratio, a.split_unit_code, a.split_price as splitPrice, a.is_valid, a.crte_id, a.crte_name, a.crte_time,"1" as itemCode,a.dosage, a.dosage_unit_code,a.ndan,"" as model, (select name from base_product c where a.prod_code = c.code and c.hosp_code = #{hospCode}) as prodName from base_drug a where a.hosp_code = #{hospCode}
and a.is_valid = #{isValid}
and big_type_code in
#{item}
and (a.good_name like concat('%',#{keyword},'%') or a.usual_name like concat('%',#{keyword},'%') or a.good_pym like concat('%',#{keyword},'%') or a.good_wbm like concat('%',#{keyword},'%') or a.usual_pym like concat('%',#{keyword},'%') or a.usual_wbm like concat('%',#{keyword},'%'))
union all select a.id as itemId, a.last_buy_price,a.hosp_code, a.type_code, a.bfc_code, a.code, a.name as itemName, a.spec, a.prod_code, a.price, a.unit_code, a.split_ratio, a.split_unit_code, a.split_price as splitPrice, a.is_valid, a.crte_id, a.crte_name, a.crte_time, "2" as itemCode,"" as dosage,"" as dosageUnitCode,"" as ndan,a.model, (select name from base_product c where a.prod_code = c.code and c.hosp_code = #{hospCode}) as prodName from base_material a where a.hosp_code = #{hospCode}
and a.is_valid = #{isValid}
and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))

select id, hosp_code, in_id, item_code, item_id, item_name, spec, num, unit_code, dosage, dosage_unit_code, buy_price, sell_price, buy_price_all, sell_price_all, split_ratio, split_num, split_price, split_unit_code, batch_no, expiry_date,prod_code from stro_in_detail
and hosp_code = #{hospCode}
and in_id = (select id from stro_in where hosp_code = #{hospCode} and order_no = #{orderNo})

select id, (select b.name from base_supplier b where b.id = supplier_id and b.hosp_code = #{hospCode}) as name from stro_in where hosp_code = #{hospCode}
and id in
#{id}

select a.id, a.hosp_code, a.in_id, a.item_code, a.item_id, a.item_name, a.spec, a.num, a.unit_code, a.dosage, a.dosage_unit_code, a.buy_price, a.sell_price, a.buy_price_all, a.sell_price_all, a.split_ratio, a.split_num, a.split_price, a.split_unit_code, a.batch_no, a.expiry_date,a.ndan, a.prod_code,b.audit_time,b.crte_time, (select p.name from base_product p where p.hosp_code = #{hospCode} and a.prod_code = p.code) as prodName, case when a.item_code='1' then bd.code when a.item_code='2' then bm.code end code, case when a.item_code='2' then bm.model end model from stro_in_detail a join stro_in b on b.id = a.in_id and b.hosp_code = a.hosp_code left join base_drug bd on bd.id = a.item_id and bd.hosp_code = #{hospCode} left join base_material bm on bm.id = a.item_id and bm.hosp_code = #{hospCode} where a.hosp_code = #{hospCode} and a.in_id in
#{id}
order by in_id
