mapper.select:
select x.*,ifnull((select name from base_product bp where x.prod_code = bp.code and bp.hosp_code= #{hospCode}),'')as prodName from ( select a.id, a.hosp_code, a.inventory_id, a.item_code, a.item_id, a.item_name, a.batch_no, a.expiry_date, a.result_code, ifnull(a.before_num,0) before_num, ifnull(a.before_split_num,0) before_split_num, ifnull(a.final_num,0) final_num, ifnull(a.incdec_num,0) incdec_num, ifnull(a.final_split_num,0) final_split_num, a.unit_code, ifnull(a.buy_price,0) buy_price, (ifnull(a.buy_price,0) * ifnull(a.final_num,0)) afterBuyPriceAll, (ifnull(a.buy_price,0) * ifnull(a.before_num,0)) beforBuyPriceAll, ifnull(a.sell_price,0) sell_price, (ifnull(a.sell_price,0) * ifnull(a.final_num,0)) afterSellPriceAll, (ifnull(a.sell_price,0) * ifnull(a.before_num,0)) beforSellPriceAll, a.split_unit_code, ifnull(a.split_buy_price,0) split_buy_price, ifnull(a.split_sell_price,0) split_sell_price, ifnull(a.incdec_buy_price,0) incdec_buy_price, ifnull(a.incdec_sell_price,0) incdec_sell_price,a.location_no, case when a.item_code='1' then bd.prod_code when a.item_code='2' then bm.prod_code end prod_code, case when a.item_code='1' then bd.split_ratio when a.item_code='2' then bm.split_ratio end splitRatio, case when a.item_code='1' then bd.spec when a.item_code='2' then bm.spec end spec, case when a.item_code='1' then bd.code when a.item_code='2' then bm.code end code, case when a.item_code='2' then bm.model end model from stro_inventory_detail a left join base_drug bd on bd.id = a.item_id and bd.hosp_code = #{hospCode} left join base_material bm on bm.id = a.item_id and bm.hosp_code = #{hospCode}
and a.hosp_code = #{hospCode}
and a.inventory_id = #{inventoryId}
)x order by x.location_no is null,x.location_no,x.code
