mapper.select:
select a.id, a.hosp_code, a.outin_code, a.order_no, a.biz_id, a.item_code, a.item_id, a.item_name, ifnull(a.num,0) num, a.unit_code, a.curr_unit_code, ifnull(a.sell_price,0) sell_price, ifnull(a.buy_price,0) buy_price, a.split_ratio, ifnull(a.split_price,0) split_price, ifnull(a.split_num,0) split_num, a.split_unit_code, a.batch_no, ifnull(a.batch_surplus_num,0) batch_surplus_num, ifnull(a.up_surplus_num,0) up_surplus_num, ifnull(a.surplus_num,0) surplus_num, ifnull(a.up_buy_price_all,0) up_buy_price_all, ifnull(a.buy_price_all,0) buy_price_all, ifnull(a.up_sell_price_all,0) up_sell_price_all, ifnull(a.sell_price_all,0) sell_price_all, DATE_FORMAT(a.expiry_date,'%Y-%m-%d') expiry_date, a.crte_id, a.crte_name, a.crte_time, b.name bizName,a.invoicing_target_id, a.invoicing_target_name,round(ifnull((a.surplus_num*a.split_ratio),0),0) splitSurplusNum, (case a.item_code when '1' then c.spec when '2' then d.spec end ) spec, (case a.item_code when '1' then c.code when '2' then d.code end ) code, (case a.item_code when '1' then c.nation_code when '2' then d.nation_code end ) nation_code, (case a.item_code when '1' then (select name from base_product where code = c.prod_code) when '2' then (select name from base_product where code = d.prod_code) end ) prodName, ifnull(a.new_price,0) new_price, ifnull(a.new_split_price,0) new_split_price from stro_invoicing a left join base_dept b on a.biz_id=b.id and a.hosp_code=b.hosp_code left join base_drug c on a.item_code='1' and c.id=a.item_id and c.hosp_code=a.hosp_code left join base_material d on a.item_code='2' and d.id=a.item_id and d.hosp_code=a.hosp_code
and a.hosp_code = #{hospCode}
and (a.item_name like concat('%',#{keyword},'%') or c.good_pym like concat('%',#{keyword},'%') or c.good_wbm like concat('%',#{keyword},'%') or c.usual_pym like concat('%',#{keyword},'%') or c.usual_wbm like concat('%',#{keyword},'%') or d.pym like concat('%',#{keyword},'%') or d.wbm like concat('%',#{keyword},'%') )
/*新增项目编码查询条件*/
and c.code=#{code}
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') >=#{starttime}
and DATE_FORMAT(a.crte_time,'%Y-%m-%d') <=#{endtime}
and a.biz_id=#{bizId}
and a.outin_code=#{outinCode}
and (c.spec like concat('%',#{spec},'%') or d.spec like concat('%',#{spec},'%'))
order by a.id desc

select id, hosp_code, outin_code, order_no, biz_id, item_code, item_id, item_name, num, unit_code, curr_unit_code, sell_price, buy_price, split_ratio, split_price, split_num, split_unit_code, batch_no, batch_surplus_num, up_surplus_num, surplus_num, up_buy_price_all, buy_price_all, up_sell_price_all, sell_price_all, expiry_date, crte_id, crte_name, crte_time,invoicing_target_id,invoicing_target_name from stro_invoicing
and hosp_code = #{hospCode}
and item_name like concat('%',#{itemName},'%')
and DATE_FORMAT(crte_time,'%Y-%m-%d') >=#{starttime}
and DATE_FORMAT(crte_time,'%Y-%m-%d') <=#{endtime}
and outin_code in (1,4)
order by crte_time desc

select id, hosp_code, outin_code, order_no, biz_id, item_code, item_id, item_name, num, unit_code, curr_unit_code, sell_price, buy_price, split_ratio, split_price, split_num, split_unit_code, batch_no, batch_surplus_num, up_surplus_num, surplus_num, up_buy_price_all, buy_price_all, up_sell_price_all, sell_price_all, expiry_date, crte_id, crte_name, crte_time, invoicing_target_id,invoicing_target_name from stro_invoicing
and hosp_code = #{hospCode}
and item_name like concat('%',#{itemName},'%')
and DATE_FORMAT(crte_time,'%Y-%m-%d') >=#{starttime}
and DATE_FORMAT(crte_time,'%Y-%m-%d') <=#{endtime}
and outin_code in (2,3,9,10)
order by crte_time desc
