mapper.select:
		select
        a.id, a.hosp_code, a.out_code, a.order_no, a.out_stock_id, a.in_stock_id, a.buy_price_all,
        a.sell_price_all, a.remark, a.audit_code, a.audit_id, a.audit_name, a.audit_time, a.ok_audit_code,
        a.ok_audit_id, a.ok_audit_name, a.ok_audit_time, a.in_order_no, a.crte_id, a.crte_name, a.crte_time,
		b.name as deptName
		from stro_out a
		left join base_dept b on a.hosp_code = b.hosp_code and a.in_stock_id = b.id

			a.hosp_code = #{hospCode} and a.out_code in ('4','5','10','26')

				and a.id = #{id}


				and a.out_stock_id = #{outStockId}


				and a.buy_price_all = #{buyPriceAll}


				and a.sell_price_all = #{sellPriceAll}


				and a.remark = #{remark}


				and a.audit_code = #{auditCode}


				and a.audit_id = #{auditId}


				and a.audit_name = #{auditName}


				and a.audit_time = #{auditTime}


				and a.ok_audit_code = #{okAuditCode}


				and a.ok_audit_id = #{okAuditId}


				and a.ok_audit_name = #{okAuditName}


				and a.ok_audit_time = #{okAuditTime}


				and in_order_no = #{inOrderNo}


				and a.crte_id = #{crteId}


				and a.crte_name = #{crteName}


				and a.crte_time = #{crteTime}

			/*开始日期*/

				and date(a.crte_time) >= date(#{startDate})

			/*结束日期*/

				and date(a.crte_time) <= date(#{endDate})


				and (a.order_no like '%${keyword}%' or
				a.in_stock_id in
				(select c.id from base_dept c where (c.name like '%${keyword}%' or c.pym like '%${keyword}%' or c.wbm like '%${keyword}%')))

select
a.id, a.hosp_code, a.out_code, a.order_no, a.out_stock_id, a.in_stock_id, a.buy_price_all,
a.sell_price_all, a.remark, a.audit_code, a.audit_id, a.audit_name, a.audit_time, a.ok_audit_code,
a.ok_audit_id, a.ok_audit_name, a.ok_audit_time, a.in_order_no, a.crte_id, a.crte_name, a.crte_time,
b.name as deptName
from stro_out a
left join base_dept b on a.hosp_code = b.hosp_code and a.in_stock_id = b.id

	a.hosp_code = #{hospCode} and a.out_code in ('4','5','10','26')

		and a.id = #{id}


		and a.out_stock_id = #{outStockId}


		and a.buy_price_all = #{buyPriceAll}


		and a.sell_price_all = #{sellPriceAll}


		and a.remark = #{remark}


		and a.audit_code = #{auditCode}


		and a.audit_id = #{auditId}


		and a.audit_name = #{auditName}


		and a.audit_time = #{auditTime}


		and a.ok_audit_code = #{okAuditCode}


		and a.ok_audit_id = #{okAuditId}


		and a.ok_audit_name = #{okAuditName}


		and a.ok_audit_time = #{okAuditTime}


		and in_order_no = #{inOrderNo}


		and a.crte_id = #{crteId}


		and a.crte_name = #{crteName}


		and a.crte_time = #{crteTime}

	/*开始日期*/

		and a.crte_time >=#{startDate}

	/*结束日期*/

		and a.crte_time <= DATE_FORMAT(DATE_ADD(#{endDate},INTERVAL 1 DAY),'%Y-%m-%d')


		and (a.order_no like '%${keyword}%' or
		a.in_stock_id in
		(select c.id from base_dept c where (c.name like '%${keyword}%' or c.pym like '%${keyword}%' or c.wbm like '%${keyword}%')))


		and a.id in

			#{id}



order by a.crte_time desc

select sum(b.num) as remainNum,a.id, a.hosp_code, a.out_id, a.item_code, a.item_id, a.item_name, a.spec, a.num, a.unit_code, a.dosage, a.dosage_unit_code, a.buy_price, a.sell_price, a.buy_price_all, a.sell_price_all, a.split_ratio, a.split_num, a.split_price, a.split_unit_code, a.expiry_date,a.batch_no, ( CASE WHEN a.item_code = '1' THEN (select name from base_product where code = bd.prod_code and hosp_code = a.hosp_code) end ) as DrugProductName, ( CASE WHEN a.item_code = '2' THEN (select name from base_product where code = bm.prod_code and hosp_code = a.hosp_code) end )as materialProductName, case when a.item_code='1' then bd.code when a.item_code='2' then bm.code end code, case when a.item_code='1' then bd.type_code when a.item_code='2' then bm.type_code end type_code, case when a.item_code='2' then bm.model end model, case when a.item_code='1' then bd.nation_code when a.item_code='2' then bm.nation_code end nation_code, case when a.item_code='1' then bd.nation_name when a.item_code='2' then bm.nation_name end nation_name from stro_out_detail as a left join base_drug bd on bd.id = a.item_id and bd.hosp_code = #{hospCode} left join base_material bm on bm.id = a.item_id and bm.hosp_code = #{hospCode} LEFT JOIN stro_stock_detail as b on a.item_name = b.item_name and a.item_code = b.item_code and a.item_id = b.item_id and a.batch_no = b.batch_no and b.biz_id = #{outStockId} and a.hosp_code = b.hosp_code
a.hosp_code = #{hospCode}
and a.out_id = #{id}
and a.out_id in
#{id}
and ( a.item_name like concat('%',#{itemNameKey},'%') or bd.usual_pym like concat('%',#{itemNameKey},'%') or bd.good_pym like concat('%',#{itemNameKey},'%') or bd.good_name like concat('%',#{itemNameKey},'%') or bd.usual_name like concat('%',#{itemNameKey},'%') or bm.name like concat('%',#{itemNameKey},'%') or bm.pym like concat('%',#{itemNameKey},'%') )
group by a.batch_no,a.id, a.hosp_code, a.out_id, a.item_code, a.item_id, a.item_name, a.spec, a.num, a.unit_code, a.dosage, a.dosage_unit_code, a.buy_price, a.sell_price, a.buy_price_all, a.sell_price_all, a.split_ratio, a.split_num, a.split_price, a.split_unit_code, a.expiry_date order by a.id

update.foreach:
update stro_out_detail
out_id = #{item.outId}, item_code = #{item.itemCode}, item_id = #{item.itemId}, item_name = #{item.itemName}, spec = #{item.spec}, num = #{item.num}, unit_code = #{item.unitCode}, dosage = #{item.dosage}, dosage_unit_code = #{item.dosageUnitCode}, buy_price = #{item.buyPrice}, sell_price = #{item.sellPrice}, buy_price_all = #{item.buyPriceAll}, sell_price_all = #{item.sellPriceAll}, split_ratio = #{item.splitRatio}, split_num = #{item.splitNum}, split_price = #{item.splitPrice}, split_unit_code = #{item.splitUnitCode}, batch_no = #{item.batchNo}, expiry_date = #{item.expiryDate},
where hosp_code = #{item.hospCode} and id = #{item.id}

mapper.delete:
delete from stro_out_detail where hosp_code = #{hospCode} and
id in
#{id}

delete from stro_out_detail
where hosp_code = #{hospCode} and
out_id = #{id}

mapper.select:
select id, hosp_code, out_id, item_code, item_id, item_name, spec, num, unit_code, dosage, dosage_unit_code, buy_price, sell_price, buy_price_all, sell_price_all, split_ratio, split_num, split_price, split_unit_code, expiry_date, batch_no, expiry_date, #{auditId} as crteId,#{auditName} as crteName, (select stro_out.out_stock_id from stro_out where out_id = stro_out.id and stro_out.hosp_code = #{hospCode} ) as biz_id, (select dp.name from base_dept dp where id = (select stro_out.in_stock_id from stro_out where out_id = stro_out.id and stro_out.hosp_code =#{hospCode}) and dp.hosp_code = #{hospCode}) as invoicingTargetName, (select stro_out.in_stock_id from stro_out where out_id = stro_out.id and stro_out.hosp_code =#{hospCode} ) as invoicingTargetId, (select stro_out.order_no from stro_out where out_id = stro_out.id and stro_out.hosp_code = #{hospCode} ) as orderNo, ( case stro_out_detail.item_code when '1' then (SELECT base_drug.prep_code FROM base_drug WHERE base_drug.id = item_id and base_drug.hosp_code = #{hospCode}) end ) as prepCode from stro_out_detail
and hosp_code = #{hospCode}
and out_id in
#{item}

SELECT a.id, a.hosp_code, a.out_id, a.item_code, a.item_id, a.item_name, a.spec, a.num, a.unit_code, a.dosage, a.dosage_unit_code, a.buy_price, a.sell_price, a.buy_price_all, a.sell_price_all, a.split_ratio, a.split_num, a.split_price, a.split_unit_code, a.expiry_date, a.batch_no, c.name as deptName, CASE WHEN a.item_code = '1' THEN ( SELECT NAME FROM base_product WHERE CODE = bd.prod_code AND hosp_code = a.hosp_code ) WHEN a.item_code = '2' THEN ( SELECT NAME FROM base_product WHERE CODE = bm.prod_code AND hosp_code = a.hosp_code ) END prodName, CASE WHEN a.item_code = '1' THEN bd.CODE WHEN a.item_code = '2' THEN bm.CODE END CODE, CASE WHEN a.item_code = '2' THEN bm.model END model FROM stro_out_detail AS a LEFT JOIN base_drug bd ON bd.id = a.item_id AND bd.hosp_code = #{hospCode} LEFT JOIN base_material bm ON bm.id = a.item_id AND bm.hosp_code = #{hospCode} left join stro_out as b on a.out_id = b.id and a.hosp_code = b.hosp_code left join base_dept as c on c.id = b.in_stock_id and a.hosp_code = c.hosp_code
a.hosp_code = #{hospCode}
AND a.out_id = #{outId}
AND a.out_id IN
#{id}
AND ( a.item_name LIKE concat('%',#{itemName},'%') OR bd.usual_pym LIKE concat('%',#{itemName},'%') OR bd.good_pym LIKE concat('%',#{itemName},'%') OR bd.good_name LIKE concat('%',#{itemName},'%') OR bd.usual_name LIKE concat('%',#{itemName},'%') OR bm.NAME LIKE concat('%',#{itemName},'%') OR bm.pym LIKE concat('%', #{itemName},'%') )
GROUP BY a.batch_no, a.id, a.hosp_code, a.out_id, a.item_code, a.item_id, a.item_name, a.spec, a.num, a.unit_code, a.dosage, a.dosage_unit_code, a.buy_price, a.sell_price, a.buy_price_all, a.sell_price_all, a.split_ratio, a.split_num, a.split_price, a.split_unit_code, a.expiry_date ORDER BY a.id

mapper.update:
