SELECT oc.dept_id as deptId, -- 科室id bd.name as deptName, -- 科室名称 bfc.name as bfcName, -- 财务分类名称 oc.doctor_name as doctorName, -- 医生姓名 podd.batch_no as batchNo, -- 批号 podd.item_id as itemId, -- 项目id podd.item_name as itemName, -- 项目名称 podd.spec as itemSpec, -- 项目规格 ssd.buy_price as buyPrice, -- 购进单价 ssd.sell_price as sellPrice, -- 零售单价 ssd.split_ratio as splitRatio, -- 拆分比 podd.unit_code as unitCode, sum(podd.num - ifnull(podd.back_num,0)) as sellCount, sum(ssd.buy_price *(podd.num - ifnull(podd.back_num,0))) as buyPriceAll, sum(ssd.sell_price *(podd.num- ifnull(podd.back_num,0))) as sellPriceAll, sum((ssd.sell_price - ssd.buy_price) *(podd.num -ifnull(podd.back_num,0))) as profit, round(sum((ssd.sell_price - ssd.buy_price) *(podd.num -ifnull(podd.back_num,0))) /sum(ssd.buy_price *(podd.num - ifnull(podd.back_num,0))),4) as profitRate FROM outpt_cost oc join outpt_settle os on os.id = oc.settle_id and os.hosp_code = #{hospCode} join phar_out_distribute_all_detail podad on podad.cost_id = oc.id and podad.hosp_code = #{hospCode} and oc.distribute_all_detail_id = podad.id join phar_out_distribute_detail podd on podd.distribute_all_detail_id = podad.id and podd.hosp_code = #{hospCode} and podd.cost_id = oc.id left join stro_stock_detail ssd on ssd.id = podd.stock_detail_id and ssd.hosp_code = #{hospCode} left join base_dept bd on bd.id = oc.dept_id and bd.hosp_code = #{hospCode} left join base_finance_classify bfc on bfc.id = oc.bfc_id and bfc.hosp_code = #{hospCode} where oc.is_dist ='1' and oc.settle_code='2' and oc.item_code in('1','2') and oc.hosp_code= #{hospCode}
and oc.dept_id = #{deptId}
and oc.doctorId = #{doctorId}
and os.settle_time >= #{startDate}
and os.settle_time <= #{endDate}
group by oc.dept_id
group by podd.item_id,podd.batch_no,oc.dept_id,oc.doctor_id
