        and (
            a.item_name like CONCAT('%',#{itemName},'%')
            or b.usual_pym like CONCAT('%',#{itemName},'%')
            or b.usual_wbm like CONCAT('%',#{itemName},'%')
            or b.good_pym like CONCAT('%',#{itemName},'%')
