from stro_stock a
JOIN base_material b on a.item_id = b.id and a.hosp_code=b.hosp_code
left join stro_stock_detail ssd on ssd.item_id = a.item_id and a.hosp_code= ssd.hosp_code
and ssd.id = (select stock_detail_id from stro_in_record where item_id = a.item_id order by crte_time desc limit 1)

    and a.hosp_code = #{hospCode} and b.hosp_code = #{hospCode}

        and a.biz_id = #{bizId}


        and b.is_valid = #{isValid}


        and a.location_no = #{locationNo}


        and a.item_code = #{itemCode}


        and a.item_id = #{itemId}


        and (
        a.item_name like CONCAT('%',#{itemName},'%')
        or b.pym like CONCAT('%',#{itemName},'%')
        or b.wbm like CONCAT('%',#{itemName},'%')
        )


        and a.spec = #{spec}


        and a.prep_code = #{prepCode}


        and a.unit_code = #{unitCode}


        and a.buy_price_all = #{buyPriceAll}


        and a.sell_price_all = #{sellPriceAll}


        and a.split_num = #{splitNum}


        and a.split_unit_code = #{splitUnitCode}


        and a.stock_max = #{stockMax}


        and a.stock_min = #{stockMin}


        and ifnull(a.stock_occupy,0) = #{stockOccupy}


        and ifnull(a.split_num,0)>0


        and ifnull(a.num,0) > ifnull(a.stock_min,0)


      and ifnull(a.stock_min,0) > a.num


      and ifnull(a.stock_min,0) < a.num and ifnull(a.stock_max,0) > a.num


      and ifnull(a.stock_max,0) < a.num

select a.id, a.hosp_code hospCode, a.biz_id bizId, a.item_code itemCode, a.item_id itemId, a.item_name itemName, a.num, a.buy_price_all buyPriceAll, a.sell_price_all sellPriceAll, a.split_num splitNum, a.split_price splitPrice, a.unit_code unitCode, a.batch_no batchNo, a.expiry_date expiryDate, a.split_unit_code splitUnitCode,a.split_ratio splitRatio,a.buy_price buyPrice,a.sell_price sellPrice from stro_stock_detail a where hosp_code = #{hospCode} and biz_id = #{bizId} and item_code = #{itemCode} and item_id = #{itemId}
and batch_no = #{batchNo}
order by a.id

select a.id, a.hosp_code hospCode, a.biz_id bizId, a.item_code itemCode, a.item_id itemId, a.item_name itemName, a.num, a.buy_price_all buyPriceAll, a.sell_price_all sellPriceAll, a.split_num splitNum, a.split_price splitPrice, a.unit_code unitCode, a.batch_no batchNo, a.expiry_date expiryDate, a.split_unit_code splitUnitCode,a.split_ratio splitRatio,a.buy_price buyPrice,a.sell_price sellPrice from stro_stock_detail a where hosp_code = #{hospCode} and biz_id = #{bizId} and item_code = #{itemCode} and item_id = #{itemId} and buy_price=#{buyPrice}
and batch_no = #{batchNo}
order by a.id asc

select a.biz_id bizId,a.item_code itemCode,a.item_id itemId,sum(ifnull(a.num,0)) num,sum(ifnull(a.split_num,0)) splitNum from stro_stock_detail a where hosp_code = #{hospCode} and biz_id = #{bizId} and item_code = #{itemCode} and item_id = #{itemId} and a.split_num > 0
and batch_no = #{batchNo}
group by a.biz_id, item_code, item_id asc

update.foreach:
update stro_stock set
stock_occupy=(case when (IFNULL(stock_occupy,0)+#{item.stockNum})>0 then IFNULL(stock_occupy,0)+#{item.stockNum}
when (IFNULL(stock_occupy,0)+#{item.stockNum})<=0 then 0 end)
where item_id=#{item.itemId} and item_code=#{item.itemCode} and hosp_code=#{item.hospCode} and biz_id=#{item.bizId}

update stro_stock a,(
    select ifnull(sum(split_num),0) split_num, ifnull(sum(num),0) num,ifnull(sum(buy_price_all),0) buy_price_all,
    ifnull(sum(sell_price_all),0) sell_price_all
    from stro_stock_detail
    where hosp_code=#{item.hospCode} and biz_id=#{item.bizId} and item_code=#{item.itemCode} and item_id=#{item.itemId}
) b set a.num=b.num,a.split_num=b.split_num,a.buy_price_all=b.buy_price_all,a.sell_price_all=b.sell_price_all
where hosp_code=#{item.hospCode} and biz_id=#{item.bizId} and item_code=#{item.itemCode} and item_id=#{item.itemId}

update stro_stock set stock_occupy = IFNULL(stock_occupy,0) + #{item.num}
where item_id=#{item.itemId} and item_code=#{item.itemCode} and hosp_code=#{item.hospCode} and
biz_id=#{item.bizId}

mapper.select:
select a.id, a.hosp_code hospCode, a.biz_id bizId, a.item_code itemCode, a.item_id itemId, a.item_name itemName, a.num, a.buy_price_all buyPriceAll, a.sell_price_all sellPriceAll, a.split_num splitNum, a.split_price splitPrice, a.unit_code unitCode, a.batch_no batchNo, a.expiry_date expiryDate, a.split_unit_code splitUnitCode,a.split_ratio splitRatio,a.buy_price buyPrice,a.sell_price sellPrice, (select dp.name from base_dept dp where dp.id = a.biz_id and dp.hosp_code = a.hosp_code) as invoicingTargetName, a.biz_id as invoicingTargetId from stro_stock_detail a LEFT JOIN base_dept bd on a.biz_id = bd.id and bd.hosp_code = #{hospCode} where a.hosp_code = #{hospCode} and a.split_num > 0 and a.item_id = #{itemId}
and bd.type_code in ('3','4','5','13','14')
order by a.id

select a.hosp_code, a.biz_id, a.item_code, a.item_id, a.item_name,a.batch_no,a.unit_code, a.split_unit_code, a.split_ratio,sum(a.num) num, max(a.buy_price) as buy_price,max(a.sell_price) as sell_price, round(sum(a.num * a.buy_price),4) as buy_price_all, round(sum(a.num * a.sell_price),4) as sell_price_all,max(a.expiry_date) as expiry_date, sum(a.split_num) as split_num, max(a.split_price) as split_price, (case a.item_code when '1' then b.spec when '2' then d.spec end ) spec, (case a.item_code when '1' then b.code when '2' then d.code end ) code, (case a.item_code when '1' then b.prep_code when '2' then '' end ) prepCode, (case a.item_code when '1' then b.type_code when '2' then d.type_code end ) typeCode,b.big_type_code as bigTypeCode, case when a.item_code = '1' then (select bp.name from base_product bp where b.prod_code = bp.code and b.hosp_code = bp.hosp_code) when a.item_code = '2' then (select bp.name from base_product bp where d.prod_code = bp.code and d.hosp_code = bp.hosp_code) end prodName from stro_stock_detail a left join base_drug b on a.item_code='1' and b.id=a.item_id and b.hosp_code=a.hosp_code left join base_material d on a.item_code='2' and d.id=a.item_id and d.hosp_code=a.hosp_code where a.biz_id = #{bizId} and a.hosp_code = #{hospCode}
and item_id = #{itemId}
group by a.hosp_code,a.biz_id, a.item_code, a.item_id, a.item_name,a.batch_no,a.unit_code, a.split_unit_code,a.split_ratio
