mapper.select:
select a.id as itemId,a.`name` as itemName,a.itemTypeCode,a.type_code,a.split_price,a.price,a.split_unit_code,a.unit_code,a.code as itemCode,a.use_code, a.spec ,a.out_dept_code,a.in_dept_code,a.hosp_code,b.name as inDeptName,c.name as outDeptName,bfcId,bfcName, a.nation_code, a.nation_name,a.checkUnitCode,a.checkPrice, a.other_name FROM ( select id,`name`,type_code as itemTypeCode,price as split_price,price, unit_code as split_unit_code ,unit_code,spec,out_dept_code,in_dept_code,hosp_code,code,'' as use_code, '3' as type_code, m.nation_code,m.nation_name, m.other_name, (select d.id from base_finance_classify d where m.bfc_code = d.code and m.hosp_code = d.hosp_code limit 1) bfcId, (select d.name from base_finance_classify d where m.bfc_code = d.code and m.hosp_code = d.hosp_code limit 1) bfcName,unit_code as checkUnitCode,price as checkPrice from base_item m where is_valid = '1' and hosp_code = #{hospCode}
and (name like concat('%',#{keyword},'%') or name_pym like concat('%',#{keyword},'%') or name_wbm like concat('%',#{keyword},'%'))
UNION ALL select id,`name`,type_code as itemTypeCode,split_price , price,split_unit_code , unit_code,spec,'','',hosp_code,code,use_code, '2' as type_code, l.nation_code, l.nation_name, l.other_name, (select d.id from base_finance_classify d where l.bfc_code = d.code and l.hosp_code = d.hosp_code limit 1) bfcId, (select d.name from base_finance_classify d where l.bfc_code = d.code and l.hosp_code = d.hosp_code limit 1) bfcName,l.split_unit_code as checkUnitCode,l.split_price as checkPrice from base_material l where is_valid = '1' and hosp_code = #{hospCode}
and (name like concat('%',#{keyword},'%') or pym like concat('%',#{keyword},'%') or wbm like concat('%',#{keyword},'%'))
) a left join base_dept b on a.in_dept_code = b.`code` and a.hosp_code = b.hosp_code left join base_dept c on a.out_dept_code = c.`code` and a.hosp_code = c.hosp_code where a.hosp_code = #{hospCode}

select a.id as itemId,a.`name` as itemName,a.itemTypeCode,a.type_code,a.price,a.unit_code,a.splitUnitCode as split_unit_code,a.code as itemCode,a.use_code, a.spec ,a.out_dept_code,a.in_dept_code,a.hosp_code,a.inDeptName,a.outDeptName,a.bfcId,a.bfcName, a.nation_code, a.nation_name, case when a.type_code = '2' then sst.num else ifnull(sst.num,0) end stockNum,/*库存数量*/ case when a.type_code = '2' then sst.stock_occupy else ifnull(sst.stock_occupy,0) end stockOccupy,/*库存数量*/ case when a.type_code = '2' then (select b.name from base_dept b where sst.biz_id = b.id) else '' end strockName/*库存数量*/ FROM ( select m.id,m.`name`,m.type_code as itemTypeCode,m.price,m.unit_code,m.unit_code as splitUnitCode, m.spec,m.out_dept_code,m.in_dept_code,m.hosp_code,m.code,'' as use_code, '3' as type_code, (select d.id from base_finance_classify d where m.bfc_code = d.code and m.hosp_code = d.hosp_code limit 1) bfcId, (select d.name from base_finance_classify d where m.bfc_code = d.code and m.hosp_code = d.hosp_code limit 1) bfcName, b.name as inDeptName,c.name as outDeptName, m.nation_code, m.nation_name from base_item m left join base_dept b on m.in_dept_code = b.`code` and m.hosp_code = b.hosp_code left join base_dept c on m.out_dept_code = c.`code` and m.hosp_code = c.hosp_code where m.is_valid = '1' and m.hosp_code = #{hospCode} and m.is_supp_curtain = '1' and m.is_in = '1'
and (m.name like concat('%',#{keyword},'%') or m.name_pym like concat('%',#{keyword},'%') or m.name_wbm like concat('%',#{keyword},'%'))
UNION ALL select a.id,a.`name`,a.type_code as itemTypeCode,a.price,a.unit_code, a.split_unit_code ,a.spec,'','',a.hosp_code,a.code,a.use_code, '2' as type_code,d.id as bfcId,d.name as bfcName, b.inDeptName,b.outDeptName, a.nation_code, a.nation_name from base_material a left join( select max(if(b.type_code = '1',c.id,'0')) xyf, max(if(b.type_code = '2',c.id,'0')) zyf,max(if(b.type_code = '3',c.id,'0')) cyf, max(if(b.type_code = '4',c.id,'0')) clf, max(b.drugstore_code) drugstore_code, a.name as inDeptName, c.name as outDeptName from base_dept a join base_dept_drugstore b on a.code = b.dept_code and a.hosp_code = b.hosp_code join base_dept c on c.hosp_code = b.hosp_code
and SUBSTRING_INDEX(b.drugstore_code,',',1) = c.code
and c.id = #{pharId}
where a.id =#{loginDeptId} and a.hosp_code = #{hospCode} and b.type_code = '4' )b on 1 = 1
join stro_stock stock on a.id = stock.item_id and a.hosp_code = stock.hosp_code and stock.biz_id = #{pharId}
left join base_finance_classify d on a.bfc_code = d.code and a.hosp_code = d.hosp_code left join base_product f on a.prod_code = f.code and a.hosp_code = f.hosp_code where a.hosp_code = #{hospCode} and a.is_valid = '1' and a.is_supp_curtain = '1'
and (a.name like concat('%',#{keyword},'%') or a.pym like concat('%',#{keyword},'%') or a.wbm like concat('%',#{keyword},'%'))
) a left join stro_stock sst on a.id = sst.item_id where 1=1

select
id, hosp_code, advice_code, type_code, item_code, item_name, num, unit_code, price, spec, use_code, total_price, is_alone_cost, is_frist_cost, is_appoint_rate, rate_code, insure_list_code
from base_advice_detail
where hosp_code = #{hospCode}

    and id = #{id}


    and advice_code = #{adviceCode}


    and type_code = #{typeCode}


    and item_code = #{itemCode}


    and item_name = #{itemName}


    and num = #{num}


    and unit_code = #{unitCode}


    and price = #{price}


    and spec = #{spec}


    and use_code = #{useCode}


    and total_price = #{totalPrice}


    and is_alone_cost = #{isAloneCost}


    and is_frist_cost = #{isFristCost}


    and is_appoint_rate = #{isAppointRate}


    and rate_code = #{rateCode}


    and insure_list_code = #{insureListCode}


    and id in

        #{item}

select
id, hosp_code, advice_code, type_code, item_code, item_name, num, unit_code, price, spec, use_code, total_price,
is_alone_cost, is_frist_cost, is_appoint_rate, rate_code, insure_list_code
from base_advice_detail

    and hosp_code = #{hospCode}


        and id = #{id}


        and advice_code = #{adviceCode}


        and type_code = #{typeCode}


        and item_code = #{itemCode}


        and item_name = #{itemName}


        and num = #{num}


        and unit_code = #{unitCode}


        and price = #{price}


        and spec = #{spec}


        and use_code = #{useCode}


        and total_price = #{totalPrice}


        and is_alone_cost = #{isAloneCost}


        and is_frist_cost = #{isFristCost}


        and is_appoint_rate = #{isAppointRate}


        and rate_code = #{rateCode}


        and insure_list_code = #{insureListCode}


        and id in

            #{item}

select
    id, hosp_code, advice_code, type_code, item_code, item_name, num, unit_code, price, spec, use_code, total_price,
    is_alone_cost, is_frist_cost, is_appoint_rate, rate_code, insure_list_code
from base_advice_detail
where hosp_code = #{hospCode}
and item_code = #{itemCode}

SELECT a.id AS itemId, a.`name` AS itemName, a.itemTypeCode, a.type_code, a.price, a.unit_code, a.CODE AS itemCode, a.use_code, a.spec, a.out_dept_code, a.in_dept_code, a.hosp_code, b.NAME AS inDeptName, c.NAME AS outDeptName, bfcId, bfcName, bigTypeCode, dosage, dosageUnitCode, prepCode, pharId, inUnitCode, type, stockNum,stockOccupy,splitNum FROM ( SELECT id, `name`, type_code AS itemTypeCode, price, unit_code, spec, out_dept_code, in_dept_code, hosp_code, CODE, '' AS use_code, '3' AS type_code, ( SELECT d.id FROM base_finance_classify d WHERE m.bfc_code = d.CODE AND m.hosp_code = d.hosp_code LIMIT 1 ) bfcId, ( SELECT d.NAME FROM base_finance_classify d WHERE m.bfc_code = d.CODE AND m.hosp_code = d.hosp_code LIMIT 1 ) bfcName, null as bigTypeCode, null as dosage, null dosageUnitCode, null prepCode, null pharId, unit_code inUnitCode, '9' type, null stockNum, null stockOccupy,null splitNum FROM base_item m WHERE is_valid = '1' AND hosp_code = #{hospCode}
and (name like concat('%',#{keyword},'%') or name_pym like concat('%',#{keyword},'%') or name_wbm like concat('%',#{keyword},'%') or abbr_pym like concat('%',#{keyword},'%') or abbr_wbm like concat('%',#{keyword},'%'))
UNION ALL
SELECT l.id, l.`name`, l.type_code AS itemTypeCode, l.split_price price, l.split_unit_code unit_code, l.spec, '', '', l.hosp_code, l.CODE, l.use_code, '2' AS type_code, ( SELECT d.id FROM base_finance_classify d WHERE l.bfc_code = d.CODE AND l.hosp_code = d.hosp_code LIMIT 1 ) bfcId, ( SELECT d.NAME FROM base_finance_classify d WHERE l.bfc_code = d.CODE AND l.hosp_code = d.hosp_code LIMIT 1 ) bfcName, null as bigTypeCode, null as dosage, null dosageUnitCode, null prepCode, b.clf pharId, l.split_unit_code inUnitCode, '2' type, null stockNum, null stockOccupy,null splitNum FROM base_material l left join( select max(if(b.type_code = '1',c.id,'')) xyf, max(if(b.type_code = '2',c.id,'')) zcf,max(if(b.type_code = '3',c.id,'')) cyf, max(if(b.type_code = '4',c.id,'')) clf, max(b.drugstore_code) drugstore_code from base_dept a join base_dept_drugstore b on a.code = b.dept_code and a.hosp_code = b.hosp_code join base_dept c on b.drugstore_code = c.code and c.hosp_code = b.hosp_code where a.id =#{loginDeptId} and a.hosp_code = #{hospCode} )b on 1 = 1 WHERE l.is_valid = '1' AND l.hosp_code = #{hospCode}
and (name like concat('%',#{keyword},'%') or pym like concat('%',#{keyword},'%') or wbm like concat('%',#{keyword},'%'))
UNION ALL
SELECT a.id, a.good_name AS `name`, a.type_code AS itemTypeCode, CASE WHEN a.in_unit_code = a.unit_code THEN a.price WHEN a.in_unit_code = a.split_unit_code THEN a.split_price END price, a.split_unit_code unit_code, a.spec, '', '', a.hosp_code, a.`CODE`, a.durg_code, '1' AS type_code, ( SELECT d.id FROM base_finance_classify d WHERE a.bfc_code = d.CODE AND a.hosp_code = d.hosp_code LIMIT 1 ) bfcId, ( SELECT d.NAME FROM base_finance_classify d WHERE a.bfc_code = d.CODE AND a.hosp_code = d.hosp_code LIMIT 1 ) bfcName, a.big_type_code AS bigTypeCode, a.dosage, a.dosage_unit_code dosageUnitCode, a.prep_code prepCode, CASE WHEN a.big_type_code = '2' THEN b.cyf WHEN a.big_type_code = '1' THEN b.zcf WHEN a.big_type_code = '0' or a.big_type_code ='3' THEN b.xyf END pharId, a.in_unit_code inUnitCode, CASE WHEN a.big_type_code = '2' THEN '3' ELSE '1' END type, stock.num stockNum, convert((stock.stock_occupy/a.split_ratio), decimal(10, 4)) as stockOccupy,stock.split_num splitNum FROM base_drug a LEFT JOIN ( SELECT max( IF ( b.type_code = '1', c.id, '0' )) xyf, max( IF ( b.type_code = '2', c.id, '0' )) zcf, max( IF ( b.type_code = '3', c.id, '0' )) cyf, max( IF ( b.type_code = '4', c.id, '0' )) clf, max( b.drugstore_code ) drugstore_code FROM base_dept a JOIN base_dept_drugstore b ON a.CODE = b.dept_code AND a.hosp_code = b.hosp_code JOIN base_dept c ON b.drugstore_code = c.CODE AND c.hosp_code = b.hosp_code WHERE a.hosp_code = #{hospCode} AND a.id = #{loginDeptId} AND a.is_valid = '1' ) b ON 1 = 1 JOIN stro_stock stock ON ( CASE
WHEN a.big_type_code = '2' THEN b.cyf WHEN a.big_type_code = '1' THEN b.zcf WHEN a.big_type_code = '0' or a.big_type_code = '3' THEN b.xyf END ) = stock.biz_id AND a.id = stock.item_id AND a.hosp_code = stock.hosp_code WHERE a.hosp_code = #{hospCode} AND a.is_valid = '1' AND ( stock.num - ifnull( stock.stock_occupy, 0 )/ stock.split_ratio ) > 0
and (usual_name like concat('%',#{keyword},'%') or usual_pym like concat('%',#{keyword},'%') or usual_wbm like concat('%',#{keyword},'%') or good_name like concat('%',#{keyword},'%') or good_pym like concat('%',#{keyword},'%') or good_wbm like concat('%',#{keyword},'%'))
) a LEFT JOIN base_dept b ON a.in_dept_code = b.`code` AND a.hosp_code = b.hosp_code LEFT JOIN base_dept c ON a.out_dept_code = c.`code` AND a.hosp_code = c.hosp_code WHERE a.hosp_code = #{hospCode}

update.foreach:
update base_advice_detail
hosp_code = #{item.hospCode}, advice_code = #{item.adviceCode}, type_code = #{item.typeCode}, item_code = #{item.itemCode}, item_name = #{item.itemName}, num = #{item.num}, unit_code = #{item.checkUnitCode}, price = #{item.checkPrice}, spec = #{item.spec}, use_code = #{item.useCode}, total_price = #{item.totalPrice}, is_alone_cost = #{item.isAloneCost}, is_frist_cost = #{item.isFristCost}, is_appoint_rate = #{item.isAppointRate}, rate_code = #{item.rateCode}, insure_list_code = #{item.insureListCode},
where id = #{item.id} and hosp_code = #{item.hospCode}

mapper.delete:
delete from base_advice_detail
where id in

    #{id}

and hosp_code = #{hospCode}
