mapper.select:
select
id, hosp_code, code, name,other_name, oper_disease_icd9, type_code, technology_code, price, unit_code, out_dept_code, in_dept_code, biz_type, biz_code, container_code, specimen_code, use_scope_code, dept_code, doctor_level_code, opeartion_code, insure_list_code, gender_code, min_age, max_age, is_cost, is_stop_same, is_stop_same_not, is_stop_myself, pym, wbm, is_valid, crte_id, crte_name, crte_time
from base_advice

    hosp_code = #{hospCode}

        and is_valid = #{isValid}


        and is_valid = '1'


        and id in

            #{item}

select
id, hosp_code, code, name,other_name, oper_disease_icd9, type_code, technology_code, price, unit_code, out_dept_code, in_dept_code, biz_type, biz_code, container_code, specimen_code, use_scope_code, dept_code, doctor_level_code, opeartion_code, insure_list_code, gender_code, min_age, max_age, is_cost, is_stop_same, is_stop_same_not, is_stop_myself, pym, wbm, is_valid, crte_id, crte_name, crte_time,
(select name from base_dept where base_dept.code = out_dept_code and base_dept.hosp_code = #{hospCode})as outDeptName,
(select name from base_dept where base_dept.code = in_dept_code and base_dept.hosp_code = #{hospCode})as inDeptName
from base_advice

    and hosp_code = #{hospCode}

        and id = #{id}


        and code = #{code}

    /*使用范围条件*/

        and  use_scope_code = #{useScopeCode}


        and name = #{name}


        and type_code = #{typeCode}


        and technology_code = #{technologyCode}


        and price = #{price}


        and unit_code = #{unitCode}


        and out_dept_code = #{outDeptCode}


        and in_dept_code = #{inDeptCode}


        and biz_type = #{bizType}


        and biz_code = #{bizCode}


        and container_code = #{containerCode}


        and specimen_code = #{specimenCode}


        and dept_code = #{deptCode}


        and doctor_level_code = #{doctorLevelCode}


        and opeartion_code = #{opeartionCode}


        and insure_list_code = #{insureListCode}


        and gender_code = #{genderCode}


        and min_age = #{minAge}


        and max_age = #{maxAge}


        and is_cost = #{isCost}


        and is_stop_same = #{isStopSame}


        and is_stop_same_not = #{isStopSameNot}


        and is_stop_myself = #{isStopMyself}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and (name like concat('%',#{keyword},'%') or pym like concat('%',#{keyword},'%') or wbm like concat('%',#{keyword},'%')or code like concat('%',#{keyword},'%'))



        and type_code in

            #{item}



order by crte_time desc

mapper.update:
update base_advice
hosp_code = #{hospCode}, code = #{code}, name = #{name}, other_name = #{otherName}, type_code = #{typeCode}, technology_code = #{technologyCode}, price = #{price}, unit_code = #{unitCode}, out_dept_code = #{outDeptCode}, in_dept_code = #{inDeptCode}, biz_type = #{bizType}, biz_code = #{bizCode}, container_code = #{containerCode}, specimen_code = #{specimenCode}, use_scope_code = #{useScopeCode}, dept_code = #{deptCode}, doctor_level_code = #{doctorLevelCode}, opeartion_code = #{opeartionCode}, insure_list_code = #{insureListCode}, gender_code = #{genderCode}, min_age = #{minAge}, max_age = #{maxAge}, is_cost = #{isCost}, is_stop_same = #{isStopSame}, is_stop_same_not = #{isStopSameNot}, is_stop_myself = #{isStopMyself}, pym = #{pym}, wbm = #{wbm}, is_valid = #{isValid}, reimbursement_ratio = #{reimbursementRatio}, oper_disease_icd9 = #{operDiseaseIcd9}, oper_nation_code = #{operNationCode}, union_nation_code = #{unionNationCode}
where id = #{id} and hosp_code = #{hospCode}

update.foreach:
update base_advice
hosp_code = #{item.hospCode}, code = #{item.code}, name = #{item.name}, other_name = #{item.otherName}, type_code = #{item.typeCode}, technology_code = #{item.technologyCode}, price = #{item.price}, unit_code = #{item.unitCode}, out_dept_code = #{item.outDeptCode}, in_dept_code = #{item.inDeptCode}, biz_type = #{item.bizType}, biz_code = #{item.bizCode}, container_code = #{item.containerCode}, specimen_code = #{item.specimenCode}, use_scope_code = #{item.useScopeCode}, dept_code = #{item.deptCode}, doctor_level_code = #{item.doctorLevelCode}, opeartion_code = #{item.opeartionCode}, insure_list_code = #{item.insureListCode}, gender_code = #{item.genderCode}, min_age = #{item.minAge}, max_age = #{item.maxAge}, is_cost = #{item.isCost}, is_stop_same = #{item.isStopSame}, is_stop_same_not = #{item.isStopSameNot}, is_stop_myself = #{item.isStopMyself}, pym = #{item.pym}, wbm = #{item.wbm}, is_valid = #{item.isValid},
where id = #{item.id} and hosp_code = #{item.hospCode}

mapper.delete:
update  base_advice

    is_valid = #{isValid}

where id in

    #{id}

and hosp_code = #{hospCode}

mapper.select:
select b.* from inpt_advice a join base_advice b on a.item_id=b.id
where a.hosp_code=#{hospCode} and b.hosp_code=#{hospCode} and a.item_code='4' and a.visit_id=#{visitId}

    and a.id in

        #{id}

select * from base_advice where hosp_code=#{hospCode}
and
type_code = #{typeCode}

    and
    is_valid = #{isValid}

select * from base_advice where hosp_code=#{hospCode}
and
type_code = #{typeCode}

    and
    is_valid = #{isValid}


    and (name like concat('%',#{keyword},'%') or pym like concat('%',#{keyword},'%') or wbm like concat('%',#{keyword},'%')or code like concat('%',#{keyword},'%'))

select code,name from
base_advice
where code in(
select advice_code from base_advice_detail where hosp_code = #{hospCode} and item_code = #{code}
) and hosp_code = #{hospCode} and is_valid = '1'

select code,name from
base_advice
where code in(
select advice_code from base_advice_detail where hosp_code = #{hospCode} and item_code = #{code}
) and hosp_code = #{hospCode} and is_valid = '1'

select * from (select ma.merge_id,max(ov.dept_name ) dept_name,max(ov.visit_no ) visit_no,max( ba.container_code ) container_code, max( ba.specimen_code ) specimen_code, GROUP_CONCAT( DISTINCT opd.content SEPARATOR ',' ) content, max( ma.document_sta ) document_sta,max(ma.apply_no ) apply_no, max( ov.NAME ) `name`,max( ov.gender_code ) gender_code, max( ov.age ) age,max( ov.age_unit_code ) age_unit_code, max(ma.crte_time ) crte_time, max(ma.bar_code ) bar_code, max(ba.technology_code) technology_code, '' as bed_name, max(ma.print_time) printTime, ifnull(max(ma.print_times), 0) printTimes from medic_apply ma JOIN outpt_prescribe_detail opd ON ma.opd_id = opd.id AND ma.hosp_code = opd.hosp_code JOIN base_advice ba ON opd.item_id = ba.id AND opd.hosp_code = ba.hosp_code JOIN outpt_visit ov ON ma.visit_id = ov.id AND ma.hosp_code = ov.hosp_code JOIN outpt_prescribe op ON opd.op_id = op.id AND op.hosp_code = opd.hosp_code where op.is_settle ='1' and ma.hosp_code = #{hospCode}
and ov.`name` like concat('%',#{name},'%')
and ov.dept_id = #{deptId}
and op.type_code = #{typeCode}
and ov.visit_no like concat('%',#{visitNo},'%')
and ma.crte_time >= DATE_ADD(#{startDate},INTERVAL 0 day)
and ma.crte_time < DATE_ADD(#{endDate},INTERVAL 1 day)
and 1=2
and ma.print_time is null
and ma.print_time is not null
and op.is_cancel = #{isValid}
and (document_sta is null or document_sta in
#{item}
)
group by ma.merge_id
union all SELECT ma.merge_id, max(iv.in_dept_name) AS dept_name, max(iv.in_no) AS visit_no, max(ba.container_code) container_code, max(ba.specimen_code) specimen_code, GROUP_CONCAT(DISTINCT ia.content separator ',') content, max(ma.document_sta) document_sta, max(ma.apply_no) apply_no, max(iv.name) name, max(iv.gender_code) gender_code, max(iv.age) age, max(iv.age_unit_code) age_unit_code, max(ma.crte_time) crte_time, max(ma.bar_code) bar_code, max(ba.technology_code) technology_code, max(iv.bed_name) bed_name, max(ma.print_time) printTime, ifnull(max(ma.print_times), 0) printTimes FROM
medic_apply ma join inpt_advice ia on ma.hosp_code = ia.hosp_code and ma.order_no = ia.order_no join inpt_advice_detail iad on ia.id = iad.ia_id and ia.hosp_code = iad.hosp_code join base_advice ba on iad.hosp_code = ba.hosp_code and iad.ba_id = ba.id and ba.type_code = '3' join inpt_visit iv on iad.hosp_code = iv.hosp_code and iad.visit_id = iv.id
where ma.hosp_code = #{hospCode}
and iv.`name` like concat('%',#{name},'%')
and iv.in_dept_id = #{deptId}
and iv.in_no like concat('%',#{visitNo},'%')
and ma.crte_time >= DATE_ADD(#{startDate},INTERVAL 0 day)
and ma.crte_time < DATE_ADD(#{endDate},INTERVAL 1 day)
and 1=2
and ma.print_time is null
and ma.print_time is not null
and (document_sta is null or document_sta in
#{item}
)
group by ma.merge_id ) tab1 order by tab1.crte_time desc

update.foreach:
update medic_apply
set is_merge = #{item.isMerge},
merge_id = #{item.mergeId},
bar_code = #{item.barCode}
where hosp_code= #{item.hospCode} and id = #{item.id}
