mapper.select:
select
id, hosp_code, ac_code, type_code, item_code, use_code, start_time, end_time, unit_code,num, is_valid, crte_id, crte_name, crte_time
from base_assist_detail

        and hosp_code = #{hospCode}

        and ac_code = #{acCode}

SELECT b.id, b.hosp_code, b.item_code, b.num, b.type_code, b.ac_code, b.start_time AS startTime, b.end_time AS endTime, b.item_code AS itemCode, b.hosp_code AS hospCode, b.type_code AS typeCode, a.itemName, a.itemTypeCode, a.typeCode, a.out_dept_code, a.in_dept_code, a.inDeptName, a.outDeptName, a.price, b.unit_code as checkUnitCode, a.unit_code, a.split_price as splitPrice, a.split_unit_code as splitUnitCode, a.spec, a.id AS itemId FROM base_assist_detail b LEFT JOIN ( SELECT id, name AS itemName, CODE, price, unit_code, split_price, split_unit_code, spec, itemTypeCode, out_dept_code, in_dept_code, hosp_code, inDeptName, outDeptName, typeCode FROM ( SELECT id, name, CODE, price, unit_code, price as split_price, unit_code as split_unit_code, spec, type_code AS itemTypeCode, out_dept_code, in_dept_code, hosp_code,( SELECT NAME FROM base_dept WHERE CODE = out_dept_code ) AS outDeptName, ( SELECT NAME FROM base_dept WHERE CODE = in_dept_code ) AS inDeptName, '3' AS typeCode FROM base_item WHERE is_valid = '1' UNION ALL SELECT id, name, CODE, price, unit_code, split_price, split_unit_code, spec, type_code AS itemTypeCode, '', '', hosp_code, '', '', '2' AS typeCode FROM base_material WHERE is_valid = '1' ) mAndi ) a ON b.item_code = a.CODE AND b.hosp_code = a.hosp_code AND a.typeCode = b.type_code
and b.hosp_code = #{hospCode}
and b.ac_code = #{acCode}
