mapper.select:
select
a.id, a.hosp_code, a.code, a.name, a.usage_code, a.type_code, a.biz_code, a.way_code, a.is_valid, a.crte_id,
a.crte_name, a.crte_time ,b.name as dept_code_name,a.dept_code, a.is_first
from base_assist a
LEFT JOIN base_dept b on a.dept_code=b.code

        and a.hosp_code = #{hospCode}

        and a.name like concat('%',#{name},'%')





        and a.way_code = #{wayCode}


        and a.is_valid = #{isValid}


        and dept_code in

            #{item}



order by a.crte_time desc

mapper.update:
update base_assist
name = #{name}, usage_code = #{usageCode}, dept_code = #{deptCode}, type_code = #{typeCode}, biz_code = #{bizCode}, way_code = #{wayCode}, is_valid = #{isValid}, is_first = #{isFirst}
where id = #{id} and hosp_code= #{hospCode}

update base_assist set

    is_valid = CASE
    WHEN #{item.id} THEN #{item.isValid}
    END

where id in

    #{item.id}

and hosp_code in

    #{item.hospCode}

mapper.select:
select a.id, a.hosp_code hospCode, a.code, a.name, a.usage_code usageCode, a.dept_code deptCode, a.type_code typeCode, a.biz_code bizCode, a.way_code wayCode, b.id deptId, case when a.type_code = '1' then c.id when a.type_code = '4' then d.id end zdxmid from base_assist a left join base_dept b on a.dept_code = b.code and a.hosp_code = b.hosp_code left join base_drug c on a.biz_code = c.code and a.hosp_code = c.hosp_code and a.type_code = '1' left join base_advice d on a.biz_code = d.code and a.hosp_code = d.hosp_code and a.type_code = '4' where a.hosp_code = #{hospCode} and a.way_code = #{wayCode} and a.is_valid = '1' and a.usage_code = #{usageCode}
and a.is_first = #{isFirst}
order by a.crte_time desc
