mapper.update:
UPDATE base_card
set status_code = #{statusCode}
where hosp_code = #{hospCode}
and id = #{id}

UPDATE base_card
set card_password = "000000"
where hosp_code = #{hospCode}
and id = #{id}

mapper.select:
select
    (select name from base_profile_file b where profile_id = b.id and b.hosp_code = hosp_code) as name,
    a.*
from base_card a
where a.hosp_code = #{hospCode}
and a.profile_id = #{profileId}

    and a.status_code = #{statusCode}


    and a.card_no like concat('%', #{keyword}, '%')

select
    id, hosp_code, profile_id, card_no, card_password, card_type_code, status_code,
    account_balance, settle_id, ele_health_id, city_health_id, def_card_type, def_card_no,
    health_code_text, crte_id, crte_name, crte_time
from base_card
where hosp_code = #{hospCode}
 and card_type_code = #{cardTypeCode}
 and card_no = #{cardNo}

select id,hosp_code,profile_id,card_id,status_code,pay_code,price,start_balance,start_balance_encryption,end_balance,
end_balance_encryption,settle_type,settle_id,daily_settle_id,crte_id,crte_name,crte_time
from base_card_recharge_change
where hosp_code=#{hospCode}
and profile_id =#{profileId}
and card_id=#{cardId}
and status_code in ('6','7')
order by crte_time desc

select a.name, a.gender_code, a.age, a.in_profile,a.out_profile,a.cert_no,b.card_type_code,b.card_no,b.status_code as cardStatusCode,b.crte_time as cardTime, c.status_code,c.pay_code,c.end_balance,c.start_balance,c.price,c.crte_time,c.crte_name from base_profile_file a JOIN base_card b ON a.hosp_code = b.hosp_code and a.id = b.profile_id JOIN base_card_recharge_change c on b.hosp_code = c.hosp_code and b.profile_id = c.profile_id and b.id = c.card_id where a.hosp_code = #{hospCode}
and (a.name like concat('%', #{keyword}, '%') or a.cert_no like concat('%', #{keyword}, '%') or a.in_profile like concat('%', #{keyword}, '%') or b.card_no like concat('%', #{keyword}, '%'))
and c.crte_time >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
and c.crte_time < DATE_ADD(STR_TO_DATE(#{endDate},'%Y-%m-%d'),INTERVAL 1 day)
and c.status_code = #{statusCode}
and c.pay_code = #{payCode}
and c.crte_id = #{crteId}
order by c.crte_time desc

mapper.update:
UPDATE base_card
set account_balance = #{accountBalance}
where hosp_code = #{hospCode}
and id = #{id}
