mapper.select:
SELECT bd.id, bd.hosp_code, bd.type_code, bd.nation_code, bd.ward_code, bd.code, bd.name, bd.phone, bd.person_name, bd.up_dept_code, pDept.name as upName, mDept.name as mgName, wDept.name as wardName, bd.mg_dept_code, bd.intro, bd.place, bd.remark, bd.pym, bd.wbm, bd.is_valid, bd.crte_id, bd.crte_name, bd.crte_time, bd.type_identity FROM base_dept bd left join base_dept pDept on bd.up_dept_code = pDept.code and bd.hosp_code = pDept.hosp_code left join base_dept mDept on bd.mg_dept_code = mDept.code and bd.hosp_code = mDept.hosp_code left join base_dept wDept on bd.ward_code = wDept.code and bd.hosp_code = wDept.hosp_code where bd.hosp_code=#{hospCode}
and bd.id = #{id}
and bd.type_code = #{typeCode}
and bd.nation_code = #{nationCode}
and bd.code = #{code}
and bd.name = #{name}
and bd.ward_code = #{wardCode}
and bd.phone = #{phone}
and bd.up_dept_code = #{upDeptCode}
and bd.mg_dept_code = #{mgDeptCode}
and bd.is_valid = #{isValid}
and bd.is_upload = #{isUpload}
and bd.crte_time = #{crteTime}
and ( bd.code like concat('%',#{keyword},'%') or bd.name like concat('%',#{keyword},'%') or bd.pym like concat('%',#{keyword},'%') or bd.wbm like concat('%',#{keyword},'%') )
and bd.code in
#{item}
order by bd.crte_time desc

SELECT bd.id, bd.hosp_code, bd.type_code, bd.nation_code, bd.ward_code, bd.code, bd.name, bd.phone, bd.person_name, bd.up_dept_code, pDept.name as upName, mDept.name as mgName, wDept.name as wardName, bd.mg_dept_code, bd.intro, bd.place, bd.remark, bd.pym, bd.wbm, bd.is_valid, bd.crte_id, bd.crte_name, bd.crte_time, bd.type_identity FROM base_dept bd left join base_dept pDept on bd.up_dept_code = pDept.code and bd.hosp_code = pDept.hosp_code left join base_dept mDept on bd.mg_dept_code = mDept.code and bd.hosp_code = mDept.hosp_code left join base_dept wDept on bd.ward_code = wDept.code and bd.hosp_code = wDept.hosp_code where bd.hosp_code = #{hospCode}
and bd.id = #{id}
and bd.code = #{code}
and bd.id in
#{item}

select * from base_dept where hosp_code=#{hospCode}

    and code = #{code}



    and id !=#{id}


    and is_valid = '1'


    and is_valid = #{isValid}

select * from base_dept where hosp_code=#{hospCode}

    and name = #{name}


    and id !=#{id}


    and is_valid = '1'


    and is_valid = #{isValid}

select * from base_dept where hosp_code=#{hospCode}

    and name = #{name}


    and id = #{id}


    and is_valid = #{isValid}

SELECT bd.id, bd.hosp_code, bd.type_code, bd.nation_code, bd.ward_code, bd.code, bd.name, bd.phone, bd.person_name, bd.up_dept_code, pDept.name as upName, mDept.name as mgName, wDept.name as wardName, bd.mg_dept_code, bd.intro, bd.place, bd.remark, bd.pym, bd.wbm, bd.is_valid, bd.crte_id, bd.crte_name, bd.crte_time, bd.type_identity FROM base_dept bd left join base_dept pDept on bd.up_dept_code = pDept.code and bd.hosp_code = pDept.hosp_code left join base_dept mDept on bd.mg_dept_code = mDept.code and bd.hosp_code = mDept.hosp_code left join base_dept wDept on bd.ward_code = wDept.code and bd.hosp_code = wDept.hosp_code where bd.hosp_code=#{hospCode} and bd.code not in (select DISTINCT up_dept_code from base_dept where up_dept_code is not null)
and bd.id = #{id}
and bd.type_code = #{typeCode}
and bd.nation_code = #{nationCode}
and bd.code = #{code}
and bd.name = #{name}
and bd.ward_code = #{wardCode}
and bd.phone = #{phone}
and bd.up_dept_code = #{upDeptCode}
and bd.mg_dept_code = #{mgDeptCode}
and bd.pym = #{pym}
and bd.wbm = #{wbm}
and bd.is_valid = #{isValid}
and bd.is_valid = '1'
and bd.id in
#{item}
and bd.type_code in
#{item}
and bd.id = #{notId}
order by bd.name desc

mapper.update:
