    hosp_code= #{hospCode}

        and id in

            #{id}

SELECT a.id, a.code, a.name, a.type_code, a.nation_code, a.intro, a.phone, a.person_name, a.crte_time, IFNULL(c.bed_num, 0) as bed_num, SUM(IF(b.work_type_code like '10%', 1, 0)) as doctor_num, SUM(IF(b.work_type_code like '20%', 1, 0)) as nurse_num, SUM(IF(b.work_type_code like '30%', 1, 0)) as drug_num, SUM(IF(b.work_type_code like '40%', 1, 0)) as medic_num FROM base_dept a left JOIN sys_user b ON a.hosp_code = b.hosp_code and a.code = b.dept_code and b.is_in_job = '1' left join ( SELECT hosp_code, dept_code, COUNT(dept_code) as bed_num from base_bed WHERE is_valid = '1' GROUP BY hosp_code, dept_code ) c on a.hosp_code = c.hosp_code and a.code = c.dept_code WHERE a.hosp_code = #{hospCode}
and a.is_valid = #{isValid}
and a.is_upload = #{isUpload}
and a.code = #{code}
and a.name = #{name}
GROUP BY a.id, b.dept_code ORDER BY a.code
