mapper.update:
update base_disease_rule
set is_valid = '0'
where hosp_code = #{hospCode}
and id = #{id}

mapper.select:
select
id, hosp_code, type_code, code, attach_code, name, usual_name, nation_code, is_add, pym, wbm, is_valid, crte_id,
crte_name, crte_time,is_infectious,infectious_name,infectious_interval_days,seq_no,platform_code
from base_disease

	hosp_code = #{hospCode}

		and id = #{id}

select
id, hosp_code, type_code, code, attach_code, name, usual_name, nation_code, nation_name, is_add, pym, wbm, is_valid, crte_id,
crte_name, crte_time,ifnull(seq_no,14836248) as seq_no,is_match
from base_disease

	hosp_code = #{hospCode}

		and id = #{id}


		and type_code = #{typeCode}


		and attach_code = #{attachCode}


		and nation_code = #{nationCode}


		and is_add = #{isAdd}


		and is_valid = #{isValid}


		and crte_id = #{crteId}


		and crte_name = #{crteName}


		and crte_time = #{crteTime}


		and (code like concat('%',#{keyword},'%')
		or name like concat('%',#{keyword},'%')
		or usual_name like concat('%',#{keyword},'%')
		or wbm like concat('%',#{keyword},'%')
		or pym like concat('%',#{keyword},'%'))


		and type_code in

			#{item}





			and type_code in ('1','2')


			and type_code = '3'


			and code like concat('%','M','%')


			and (code like concat('','V','%') or code like concat('','Y','%'))


			and type_code in ('1','2','4')







order by seq_no asc

		select
		id, hosp_code, type_code, code, attach_code, name, usual_name, nation_code, is_add, pym, wbm, is_valid, crte_id,
		crte_name, crte_time
		from base_disease

			hosp_code = #{hospCode}

				and is_valid = #{isValid}


				and is_valid = '1'


				and id = #{id}


				and type_code = #{typeCode}


				and attach_code = #{attachCode}


				and nation_code is not null and LENGTH(trim(nation_code))>0


				and nation_code = #{nationCode}


				and is_add = #{isAdd}


				and crte_id = #{crteId}


				and crte_name = #{crteName}


				and crte_time = #{crteTime}


				and (code like concat('%',#{keyword},'%')
				or name like concat('%',#{keyword},'%')
				or usual_name like concat('%',#{keyword},'%')
				or wbm like concat('%',#{keyword},'%')
				or pym like concat('%',#{keyword},'%'))


				and id in

					#{item}



		order by crte_time desc

            limit  #{limit}

mapper.update:
update base_disease
type_code = #{typeCode}, code = #{code}, attach_code = #{attachCode}, name = #{name}, usual_name = #{usualName}, nation_code = #{nationCode}, nation_name = #{nationName}, is_add = #{isAdd}, pym = #{pym}, wbm = #{wbm}, is_infectious = #{isCrb}, infectious_name = #{crbName}, infectious_interval_days = #{crbInterval}, seq_no = #{seqNo}, platform_code =#{platformCode}
where id = #{id} and hosp_code = #{hospCode}

update base_disease set is_valid = #{isValid} where hosp_code = #{hospCode} and
id in
#{id}

update.foreach:
update base_disease_rule


		type_code = #{item.typeCode},


		prompt_msg = #{item.promptMsg},


where hosp_code = #{item.hospCode}
and id = #{item.id}

mapper.select:
select count(id)
from base_disease
where hosp_code = #{hospCode}

	and code = #{code}


	and nation_code = #{nationCode}


	and attach_code = #{attachCode}


	and id != #{id}

select
id, hosp_code, type_code, code, attach_code, name, usual_name, nation_code, is_add, pym, wbm, is_valid, crte_id,
crte_name, crte_time
from base_disease

    hosp_code = #{hospCode}

        and type_code = #{typeCode}


        and attach_code = #{attachCode}


        and nation_code = #{nationCode}


        and is_add = #{isAdd}


        and is_valid = #{isValid}


        and crte_id = #{crteId}


        and crte_name = #{crteName}


        and crte_time = #{crteTime}


        and id in

            #{id}



        and code in

            #{code}



order by crte_time desc

SELECT a.drug_id, b.usual_name AS drug_name, a.disease_id, (SELECT NAME FROM base_disease c WHERE a.disease_id = c.id AND a.hosp_code = c.hosp_code AND c.is_valid = #{isValid} ) AS disease_name, a.type_code, a.prompt_msg, a.id FROM base_disease_rule a LEFT JOIN base_drug b ON a.drug_id = b.id AND a.hosp_code = b.hosp_code WHERE a.hosp_code = #{hospCode} AND a.is_valid = #{isValid}
AND a.disease_id IN
#{item}
ORDER BY a.disease_id, a.crte_time

select id, hosp_code, type_code, code, attach_code, name, usual_name, nation_code, is_add, pym, wbm, is_valid, crte_id, crte_name, crte_time,is_infectious,infectious_name,infectious_interval_days,platform_code from base_disease where hosp_code = #{hospCode} and is_infectious='1'
and id in
#{item}

mapper.update:
